        }
      : {
          type: "",
          transporterId: preSelectedTransporterId || undefined, // Usar o transportador prÃ©-selecionado
          mainVehiclePlate: "",
          tractorUnitId: undefined,
          firstTrailerId: undefined,
          dollyId: undefined,
          secondTrailerId: undefined,
          flatbedId: undefined,
          length: undefined, // Valor nÃ£o preenchido inicialmente
          width: undefined, // Sem valor padrÃ£o inicialmente
          height: undefined, // Sem valor padrÃ£o inicialmente
          additionalPlates: [],
          states: [],
          additionalPlatesDocuments: [],
          isDraft: true,
          comments: "",
          cargoType: undefined, // Adicionado para support ao tipo de carga
        },
  });

  // Efeito para mostrar notificaÃ§Ã£o quando tiver transportador prÃ©-selecionado
  useEffect(() => {
    if (preSelectedTransporterId && transporters && transporters.length > 0) {
      const selectedTransporter = transporters.find(
