          return {
            estado: estado,
            numero: data.numero,
            validade: data.validade,
            diasRestantes: data.diasRestantes
          };
        }
      } else {
        console.error(`[VALIDAÃ‡ÃƒO SIMPLES] Erro HTTP ${response.status} para ${estado}`);
