import { cn } from "@/lib/utils";
import { LicenseStatus } from "@shared/schema";
import { 
  Clock, 
  Loader2, 
  CheckCircle, 
  XCircle, 
  FileText, 
  File, 
  X
} from "lucide-react";
import { useWebSocketContext } from "@/hooks/use-websocket-context";
import { useState, useEffect } from "react";
import { Badge } from "@/components/ui/badge";

interface StatusBadgeProps {
  status: string;
  licenseId?: number;  // ID opcional da licenÃ§a para atualizaÃ§Ã£o em tempo real
  state?: string;      // Estado opcional para atualizaÃ§Ã£o em tempo real
  className?: string;
  showIcon?: boolean;
  size?: 'sm' | 'md' | 'lg'; // Tamanho do badge: pequeno, mÃ©dio (padrÃ£o) ou grande
}

export function StatusBadge({ 
  status: initialStatus, 
  licenseId, 
  state, 
  className, 
  showIcon = true,
  size = 'md'
}: StatusBadgeProps) {
  const [status, setStatus] = useState(initialStatus);
  const [recentUpdate, setRecentUpdate] = useState(false);
  const { lastMessage } = useWebSocketContext();

  // Efeito para resetar o indicador de atualizaÃ§Ã£o recente apÃ³s 3 segundos
  useEffect(() => {
    if (recentUpdate) {
      const timer = setTimeout(() => setRecentUpdate(false), 3000);
      return () => clearTimeout(timer);
    }
  }, [recentUpdate]);

  // Efeito para atualizar o status quando receber mensagem de atualizaÃ§Ã£o
  useEffect(() => {
    if (
      lastMessage?.type === 'STATUS_UPDATE' && 
      lastMessage.data && 
      licenseId && 
      lastMessage.data.licenseId === licenseId
    ) {
      // Se estamos exibindo o status para um estado especÃ­fico, verificar se a mensagem Ã© para este estado
      if (state && lastMessage.data.state === state) {
        setStatus(lastMessage.data.status);
        setRecentUpdate(true);
        console.log(`Status atualizado para licenÃ§a ${licenseId}, estado ${state}: ${lastMessage.data.status}`);
      }
      // Se estamos mostrando o status geral da licenÃ§a (sem estado especÃ­fico)
      else if (!state) {
        setStatus(lastMessage.data.license.status);
        setRecentUpdate(true);
        console.log(`Status geral atualizado para licenÃ§a ${licenseId}: ${lastMessage.data.license.status}`);
      }
    }
  }, [lastMessage, licenseId, state]);

  const getStatusStyles = () => {
    switch (status) {
      case "pending":
      case "pending_registration":
        return "bg-gray-100 text-gray-800";
      case "in_progress":
      case "registration_in_progress":
        return "bg-blue-100 text-blue-800";
      case "pending_documentation":
        return "bg-orange-100 text-orange-800";
      case "rejected":
        return "bg-red-100 text-red-800";
      case "analyzing":
      case "under_review":
        return "bg-yellow-100 text-yellow-800";
      case "pending_release":
      case "pending_approval":
        return "bg-purple-100 text-purple-800";
      case "released":
      case "approved":
        return "bg-green-100 text-green-800";
      case "canceled":
        return "bg-[#FFEDED] text-[#B22222]";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  const getStatusLabel = () => {
    switch (status) {
      case "pending":
      case "pending_registration":
        return "Pedido em Cadastramento";
      case "in_progress":
      case "registration_in_progress":
        return "Cadastro em Andamento";
      case "pending_documentation":
        return "Pendente DocumentaÃ§Ã£o";
      case "rejected":
        return "Reprovado";
      case "analyzing":
      case "under_review":
        return "AnÃ¡lise do Ã“rgÃ£o";
      case "pending_release":
      case "pending_approval":
        return "Pendente LiberaÃ§Ã£o";
      case "released":
      case "approved":
        return "Liberada";
      case "canceled":
        return "Cancelado";
      default:
        return status;
    }
  };

  const getStatusIcon = () => {
    switch (status) {
      case "pending":
      case "pending_registration":
        return <Clock className="h-3 w-3 mr-1" />;
      case "in_progress":
      case "registration_in_progress":
        return <Loader2 className="h-3 w-3 mr-1 animate-spin" />;
      case "pending_documentation":
        return <FileText className="h-3 w-3 mr-1" />;
      case "rejected":
        return <XCircle className="h-3 w-3 mr-1" />;
      case "analyzing":
      case "under_review":
        return <FileText className="h-3 w-3 mr-1" />;
      case "pending_release":
      case "pending_approval":
        return <File className="h-3 w-3 mr-1" />;
      case "released":
      case "approved":
        return <CheckCircle className="h-3 w-3 mr-1" />;
      case "canceled":
        return <X className="h-3 w-3 mr-1" />;
      default:
        return <Clock className="h-3 w-3 mr-1" />;
    }
  };

  // Definir classes com base no tamanho
  const getSizeClasses = () => {
    switch(size) {
      case 'sm':
        return "px-2 py-0.5 text-xs";
      case 'lg':
        return "px-3 py-1 text-sm";
      case 'md':
      default:
        return "px-2.5 py-0.5 text-xs";
    }
  };

  return (
    <div className="inline-flex items-center">
      <span
        className={cn(
          "inline-flex items-center rounded-full font-medium",
          getSizeClasses(),
          getStatusStyles(),
          recentUpdate ? "ring-2 ring-offset-1 ring-blue-400 transition-all duration-300" : "",
          className
        )}
      >
        {showIcon && getStatusIcon()}
        {getStatusLabel()}
      </span>
    </div>
  );
}

export function Status({ 
  status, 
  licenseId, 
  state 
}: { 
  status: string;
  licenseId?: number;
  state?: string;
}) {
  return (
    <div className="flex items-center">
      <StatusBadge 
        status={status} 
        licenseId={licenseId} 
        state={state} 
      />
    </div>
  );
}

client/src/components/licenses/status-hi.content:
import React from "react";
import { format } from "date-fns";
import { ptBR } from "date-fns/locale";
import { Loader2, Clock, User, MessageSquare, ArrowRightLeft, AlertCircle, RefreshCw } from "lucide-react";
import { useQuery, useQueryClient } from "@tanstack/react-query";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { StatusBadge } from "./status-badge";
import { Button } from "@/components/ui/button";

// Declare a tipagem para a conexÃ£o WebSocket global
declare global {
  interface Window {
    wsConnection?: WebSocket;
  }
}

interface StatusHistoryItem {
  id: number;
  licenseId: number;
  state: string;
  userId: number;
  oldStatus: string;
  newStatus: string;
  comments: string | null;
  createdAt: string;
  user?: {
    fullName: string;
    email: string;
  };
}

interface StatusHistoryProps {
  licenseId: number;
  states: string[];
  showHeader?: boolean;
  showTabs?: boolean;
  className?: string;
}

export function StatusHistoryNew({ licenseId, states, showHeader = true, showTabs = true, className }: StatusHistoryProps) {
  // Estados para controlar interface
  const [activeTab, setActiveTab] = React.useState("all");
  const [isRefreshing, setIsRefreshing] = React.useState(false);
  const { toast } = useToast();

  // Estado para controlar erros de autenticaÃ§Ã£o ou outros erros
  const [errorState, setErrorState] = React.useState({
    hasError: false,
    isAuth: false,
    message: ""
  });

  // Buscar o histÃ³rico de status da licenÃ§a
  const {
    data: historyData,
    isLoading,
    error,
    refetch
  } = useQuery({
    queryKey: [`/api/licenses/${licenseId}/status-history`],
    queryFn: async () => {
      try {
        const res = await fetch(`/api/licenses/${licenseId}/status-history`);
        if (!res.ok) {
          if (res.status === 401) {
            setErrorState({
              hasError: true,
              isAuth: true,
              message: "VocÃª precisa estar autenticado para visualizar o histÃ³rico"
            });
            throw new Error("NÃ£o autenticado");
          }
          throw new Error("Erro ao carregar histÃ³rico de status");
        }

        const data = await res.json() as StatusHistoryItem[];
        setErrorState({
          hasError: false,
          isAuth: false,
          message: ""
        });
        return data;
      } catch (err: any) {
        setErrorState({
          hasError: true,
          isAuth: err.message === "NÃ£o autenticado" || err.status === 401,
          message: err.message || "Erro ao carregar histÃ³rico de status"
        });
        throw err;
      }
    }
  });

  // Calcular dados de exibiÃ§Ã£o baseados na aba ativa
  const displayData = React.useMemo(() => {
    if (!historyData) return [] as StatusHistoryItem[];
    if (activeTab === "all") return historyData;
    return historyData.filter((item: StatusHistoryItem) => item.state === activeTab);
  }, [historyData, activeTab]);

  // Carregamento de dados quando a aba muda
  const isDataLoading = isLoading && activeTab === "all";

  const connectWebSocket = () => {
    try {
      // Se jÃ¡ existe uma conexÃ£o e estÃ¡ aberta, nÃ£o fazer nada
      if (window.wsConnection && window.wsConnection.readyState === WebSocket.OPEN) {
        return;
      }

      // Determinar o protocolo (ws ou wss) baseado no protocolo atual da pÃ¡gina
      const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
      const wsUrl = `${protocol}//${window.location.host}/ws`;

      // Criar nova conexÃ£o WebSocket
      const socket = new WebSocket(wsUrl);

      socket.onopen = () => {
        console.log("WebSocket conectado para histÃ³rico");
        window.wsConnection = socket;

        // Enviar mensagem para registrar interesse em atualizaÃ§Ãµes desta licenÃ§a
        if (socket.readyState === WebSocket.OPEN) {
          socket.send(JSON.stringify({
            type: "REGISTER_INTEREST",
            licenseId: licenseId
          }));
        }
      };

      socket.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === "STATUS_UPDATE" && data.licenseId === licenseId) {
          // Atualizar os dados quando receber uma notificaÃ§Ã£o de mudanÃ§a de status
          refetch();
        }
      };

      socket.onerror = (error) => {
        console.error("Erro WebSocket:", error);
      };

      socket.onclose = () => {
        console.log("WebSocket desconectado");
        // Remover a referÃªncia Ã  conexÃ£o quando fechada
        if (window.wsConnection === socket) {
          window.wsConnection = undefined;
        }
        // Tentar reconectar apÃ³s um pequeno atraso
        setTimeout(connectWebSocket, 3000);
      };
    } catch (err) {
      console.error("Erro ao conectar WebSocket:", err);
    }
  };

  // Conectar WebSocket quando o componente montar
  React.useEffect(() => {
    connectWebSocket();

    // Limpar conexÃ£o quando o componente desmontar
    return () => {
      if (window.wsConnection) {
        window.wsConnection.close();
      }
    };
  }, [licenseId]);

  // FunÃ§Ã£o para atualizar os dados manualmente
  const refreshData = async () => {
    setIsRefreshing(true);
    try {
      await refetch();
      toast({
        title: "HistÃ³rico atualizado",
        description: "Os dados de histÃ³rico foram atualizados com sucesso.",
      });
    } catch (err) {
      toast({
        title: "Erro ao atualizar",
        description: "NÃ£o foi possÃ­vel atualizar o histÃ³rico. Tente novamente.",
        variant: "destructive",
      });
    } finally {
      setIsRefreshing(false);
    }
  };

  // FunÃ§Ã£o para formatar data
  const formatDate = (dateString: string) => {
    try {
      const date = new Date(dateString);
      return format(date, "dd 'de' MMMM 'de' yyyy 'Ã s' HH:mm", { locale: ptBR });
    } catch (error) {
      return dateString;
    }
  };

  return (
    <Card className={className}>
      {showHeader && (
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
          <div>
            <CardTitle>HistÃ³rico de Status</CardTitle>
            <CardDescription>
              Acompanhe todas as mudanÃ§as de status desta licenÃ§a
            </CardDescription>
          </div>
          <Button 
            variant="outline" 
            size="sm" 
            className="ml-auto flex items-center gap-1.5" 
            onClick={refreshData}
            disabled={isRefreshing}
          >
            {isRefreshing ? (
              <Loader2 className="h-4 w-4 animate-spin" />
            ) : (
              <RefreshCw className="h-4 w-4" />
            )}
            Atualizar
          </Button>
        </CardHeader>
      )}
      <CardContent>
        {showTabs ? (
          <Tabs value={activeTab} onValueChange={setActiveTab}>
            <TabsList className="mb-4 flex-wrap bg-gray-50 p-2 rounded-lg inline-flex">
              <TabsTrigger value="all" className="px-4 py-1.5 rounded-md">Todos os Estados</TabsTrigger>
              {states.map((state) => (
                <TabsTrigger key={state} value={state} className="px-4 py-1.5 rounded-md">{state}</TabsTrigger>
              ))}
            </TabsList>

            {isDataLoading ? (
              <div className="flex justify-center p-8">
                <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
              </div>
            ) : errorState.hasError ? (
              <div className="text-center py-8">
                {errorState.isAuth ? (
                  <div>
                    <AlertCircle className="h-8 w-8 mx-auto mb-2 text-orange-500" />
                    <h3 className="font-medium text-lg">AutenticaÃ§Ã£o necessÃ¡ria</h3>
                    <p className="text-muted-foreground mt-1">
                      VocÃª precisa estar logado para visualizar o histÃ³rico de status.
                    </p>
                  </div>
                ) : (
                  <div>
                    <AlertCircle className="h-8 w-8 mx-auto mb-2 text-red-500" />
                    <h3 className="font-medium text-lg">Erro ao carregar histÃ³rico</h3>
                    <p className="text-muted-foreground mt-1">
                      {errorState.message}
                    </p>
                  </div>
                )}
              </div>
            ) : displayData && displayData.length > 0 ? (
              <ScrollArea className="h-[350px] pr-4">
                <div className="space-y-6">
                  {displayData.map((item: StatusHistoryItem) => (
                    <div key={item.id} className="pb-4">
                      <div className="flex items-center gap-2 mb-1">
                        <ArrowRightLeft className="h-4 w-4 text-muted-foreground" />
                        <span className="font-medium">
                          AlteraÃ§Ã£o de <StatusBadge status={item.oldStatus} showIcon={false} size="sm" /> para <StatusBadge status={item.newStatus} showIcon={false} size="sm" />
                        </span>
                        {(() => {
                          // Definir cores baseadas no status do estado
                          let badgeClass = "bg-gray-100 border-gray-200 text-gray-800";

                          // Usar o status atual do item para definir a cor
                          if (item.newStatus === "approved") {
                            badgeClass = "bg-green-50 border-green-200 text-green-800";
                          } else if (item.newStatus === "rejected") {
                            badgeClass = "bg-red-50 border-red-200 text-red-800";
                          } else if (item.newStatus === "pending_approval") {
                            badgeClass = "bg-yellow-50 border-yellow-200 text-yellow-800";
                          } else if (item.newStatus === "under_review") {
                            badgeClass = "bg-blue-50 border-blue-200 text-blue-800";
                          }

                          return (
                            <Badge variant="outline" className={`ml-auto text-xs px-2 py-0 ${badgeClass}`}>
                              {item.state}
                            </Badge>
                          );
                        })()}
                      </div>

                      <div className="flex items-center text-sm text-muted-foreground ml-6">
                        <Clock className="h-4 w-4 mr-1" />
                        <span>{formatDate(item.createdAt)}</span>
                      </div>

                      <div className="flex items-center text-sm text-muted-foreground ml-6">
                        <User className="h-4 w-4 mr-1" />
                        <span>
                          {item.user ? item.user.fullName : `UsuÃ¡rio ID: ${item.userId}`}
                        </span>
                      </div>

                      {item.comments && (
                        <div className="mt-2 ml-6">
                          <div className="flex items-start">
                            <MessageSquare className="h-4 w-4 mr-1 mt-0.5 text-muted-foreground" />
                            <div className="text-sm">{item.comments}</div>
                          </div>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </ScrollArea>
            ) : (
              <div className="text-center py-8 text-muted-foreground">
                Nenhum registro de histÃ³rico encontrado.
              </div>
            )}
          </Tabs>
        ) : (
          /* Quando showTabs for falso, mostrar apenas o histÃ³rico completo sem as abas */
          <>
            {isLoading ? (
              <div className="flex justify-center p-8">
                <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
              </div>
            ) : errorState.hasError ? (
              <div className="text-center py-8">
                {errorState.isAuth ? (
                  <div>
