import React from "react";
import { format } from "date-fns";
import { ptBR } from "date-fns/locale";
import { Loader2, Clock, User, MessageSquare, ArrowRightLeft, AlertCircle, RefreshCw } from "lucide-react";
import { useQuery, useQueryClient } from "@tanstack/react-query";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { StatusBadge } from "./status-badge";
import { Button } from "@/components/ui/button";

// Declare a tipagem para a conexÃ£o WebSocket global
declare global {
  interface Window {
    wsConnection?: WebSocket;
  }
}

interface StatusHistoryItem {
  id: number;
  licenseId: number;
  state: string;
  userId: number;
  oldStatus: string;
  newStatus: string;
  comments: string | null;
  createdAt: string;
  user?: {
    fullName: string;
    email: string;
  };
}

interface StatusHistoryProps {
  licenseId: number;
  states: string[];
  showHeader?: boolean;
  showTabs?: boolean;
}

export function StatusHistory({ licenseId, states, showHeader = true, showTabs = true }: StatusHistoryProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [activeTab, setActiveTab] = React.useState<string>("all");
  const [isRefreshing, setIsRefreshing] = React.useState(false);

  // Buscar o histÃ³rico completo
  const { 
    data: historyData, 
    isLoading, 
    isError,
    refetch: refetchHistory
  } = useQuery<StatusHistoryItem[]>({
    queryKey: [`/api/licenses/${licenseId}/status-history`],
    enabled: !!licenseId,
    staleTime: 30000, // 30 segundos
  });

  // Buscar histÃ³rico especÃ­fico de um estado quando a tab estÃ¡ ativa
  const { 
    data: stateHistoryData,
    isLoading: isStateLoading,
    isError: isStateError,
    refetch: refetchStateHistory
  } = useQuery<StatusHistoryItem[]>({
    queryKey: [`/api/licenses/${licenseId}/status-history/${activeTab}`],
    enabled: !!licenseId && activeTab !== "all",
    staleTime: 30000, // 30 segundos
  });

  // FunÃ§Ã£o para atualizar os dados
  const refreshData = async () => {
    setIsRefreshing(true);
    try {
      if (activeTab === "all") {
        await refetchHistory();
      } else {
        await refetchStateHistory();
      }
    } finally {
      setIsRefreshing(false);
    }
  };

  // Ouvir eventos de WebSocket para atualizaÃ§Ã£o em tempo real
  React.useEffect(() => {
    if (!licenseId) return;

    // ReferÃªncia para o objeto websocket
    let ws: WebSocket | null = null;

    const handleWebSocketMessage = (event: MessageEvent) => {
      try {
        const data = JSON.parse(event.data);

        // Se for uma atualizaÃ§Ã£o de status de licenÃ§a
        if (data.type === "STATUS_UPDATE" && data.data.licenseId === licenseId) {
          console.log("StatusUpdate em tempo real para histÃ³rico:", data.data);

          // Refetch dos dados
          refreshData();

          // Notificar o usuÃ¡rio
          toast({
            title: "HistÃ³rico atualizado",
            description: `Status da licenÃ§a alterado para ${data.data.status}`,
            variant: "default",
          });
        }
      } catch (error) {
        console.error("Erro ao processar mensagem WebSocket:", error);
      }
    };

    // Configurar o evento listener para WebSocket
    const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
    const wsUrl = `${protocol}//${window.location.host}/ws`;

    // Criar uma nova instÃ¢ncia do WebSocket
    try {
      ws = new WebSocket(wsUrl);

      ws.addEventListener("open", () => {
        console.log("WebSocket conectado para histÃ³rico");
      });

      ws.addEventListener("error", (error) => {
        console.error("Erro WebSocket:", error);
      });

      ws.addEventListener("message", handleWebSocketMessage);
    } catch (error) {
      console.error("Falha ao iniciar conexÃ£o WebSocket:", error);
    }

    // Limpar evento listener quando o componente for desmontado
    return () => {
      if (ws) {
        ws.removeEventListener("message", handleWebSocketMessage);
        if (ws.readyState === WebSocket.OPEN) {
          ws.close();
        }
      }
    };
  }, [licenseId, refreshData, toast]);

  // FunÃ§Ã£o para formatar data em formato legÃ­vel
  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return format(date, "dd 'de' MMMM 'de' yyyy 'Ã s' HH:mm", { locale: ptBR });
  };

  // Lidar com erros de maneira mais controlada, sem mostrar toast para erros de autenticaÃ§Ã£o
  const [errorState, setErrorState] = React.useState<{
    hasError: boolean;
    isAuth: boolean;
    message: string;
  }>({
    hasError: false,
    isAuth: false,
    message: "",
  });

  React.useEffect(() => {
    if (isError) {
      // Verificar se o erro Ã© de autenticaÃ§Ã£o (nÃ£o exibir toast nesse caso)
      const isAuthError = true; // Assumindo que todos os 401 sÃ£o por falta de autenticaÃ§Ã£o

      setErrorState({
        hasError: true,
        isAuth: isAuthError,
        message: "NÃ£o foi possÃ­vel carregar o histÃ³rico de status."
      });

      // Mostrar toast apenas para erros que nÃ£o sÃ£o de autenticaÃ§Ã£o
      if (!isAuthError) {
        toast({
          variant: "destructive",
          title: "Erro ao carregar histÃ³rico",
          description: "NÃ£o foi possÃ­vel carregar o histÃ³rico de status.",
        });
      }
    } else if (isStateError && activeTab !== "all") {
      setErrorState({
        hasError: true,
        isAuth: false,
        message: `NÃ£o foi possÃ­vel carregar o histÃ³rico de status para o estado ${activeTab}.`
      });

      toast({
        variant: "destructive",
        title: "Erro ao carregar histÃ³rico do estado",
        description: `NÃ£o foi possÃ­vel carregar o histÃ³rico de status para o estado ${activeTab}.`,
      });
    } else {
      setErrorState({
        hasError: false,
        isAuth: false,
        message: ""
      });
    }
  }, [isError, isStateError, activeTab, toast]);

  // Mostrar dados com base na tab ativa
  const displayData = activeTab === "all" ? historyData : stateHistoryData;
  const isDataLoading = activeTab === "all" ? isLoading : isStateLoading;

  return (
    <Card className="w-full">
      {showHeader && (
        <CardHeader className="flex flex-row items-center justify-between">
          <div>
            <CardTitle className="text-xl">HistÃ³rico de Status</CardTitle>
            <CardDescription>
              Acompanhe todas as mudanÃ§as de status desta licenÃ§a
            </CardDescription>
          </div>
          <Button 
            variant="outline" 
            size="sm" 
            className="ml-auto flex items-center gap-1.5" 
            onClick={refreshData}
            disabled={isRefreshing}
          >
            {isRefreshing ? (
              <Loader2 className="h-4 w-4 animate-spin" />
            ) : (
              <RefreshCw className="h-4 w-4" />
            )}
            Atualizar
          </Button>
        </CardHeader>
      )}
      <CardContent>
        {showTabs ? (
          <Tabs value={activeTab} onValueChange={setActiveTab}>
            <TabsList className="mb-4 flex-wrap bg-gray-50 p-2 rounded-lg inline-flex">
              <TabsTrigger value="all" className="px-4 py-1.5 rounded-md">Todos os Estados</TabsTrigger>
              {states.map((state) => (
                <TabsTrigger key={state} value={state} className="px-4 py-1.5 rounded-md">{state}</TabsTrigger>
              ))}
            </TabsList>

            {isDataLoading ? (
              <div className="flex justify-center p-8">
                <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
              </div>
            ) : errorState.hasError ? (
              <div className="text-center py-8">
                {errorState.isAuth ? (
                  <div>
                    <AlertCircle className="h-8 w-8 mx-auto mb-2 text-orange-500" />
                    <h3 className="font-medium text-lg">AutenticaÃ§Ã£o necessÃ¡ria</h3>
                    <p className="text-muted-foreground mt-1">
                      VocÃª precisa estar logado para visualizar o histÃ³rico de status.
                    </p>
                  </div>
                ) : (
                  <div>
                    <AlertCircle className="h-8 w-8 mx-auto mb-2 text-red-500" />
                    <h3 className="font-medium text-lg">Erro ao carregar histÃ³rico</h3>
                    <p className="text-muted-foreground mt-1">
                      {errorState.message}
                    </p>
                  </div>
                )}
              </div>
            ) : displayData && displayData.length > 0 ? (
              <ScrollArea className="h-[400px] pr-4">
                <div className="space-y-6">
                  {displayData.map((item) => (
                    <div key={item.id} className="pb-4">
                      <div className="flex items-center gap-2 mb-1">
                        <ArrowRightLeft className="h-4 w-4 text-muted-foreground" />
                        <span className="font-medium">
                          AlteraÃ§Ã£o de <StatusBadge status={item.oldStatus} /> para <StatusBadge status={item.newStatus} />
                        </span>
                        {(() => {
                          // Definir cores baseadas no status do estado
                          let badgeClass = "bg-gray-100 border-gray-200 text-gray-800";

                          // Usar o status atual do item para definir a cor
                          if (item.newStatus === "approved") {
