import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Building2, ChevronDown } from "lucide-react";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

interface TransporterCnpjSelectorProps {
  transporterId: number;
  selectedCnpj?: string;
  onCnpjSelect?: (cnpj: string, name: string) => void;
  licenseId?: number;
  state?: string;
}

interface Subsidiary {
  name: string;
  documentNumber: string;
  city: string;
  state: string;
  isActive?: boolean;
}

interface TransporterData {
  id: number;
  name: string;
  tradeName?: string;
  documentNumber: string;
  city: string;
  state: string;
  subsidiaries: Subsidiary[];
}

interface CnpjOption {
  value: string;
  label: string;
  type: "matriz" | "filial";
  location: string;
}

export function TransporterCnpjSelector({
  transporterId,
  selectedCnpj: externalSelectedCnpj,
  onCnpjSelect,
  licenseId,
  state,
}: TransporterCnpjSelectorProps) {
  const [selectedCnpj, setSelectedCnpj] = useState<string>(externalSelectedCnpj || "");

  // Atualizar o estado interno quando a prop externa mudar
  useEffect(() => {
    console.log('[CNPJ Selector] Prop externa mudou:', externalSelectedCnpj);
    setSelectedCnpj(externalSelectedCnpj || "");
  }, [externalSelectedCnpj]);

  const { data: transporter, isLoading } = useQuery<TransporterData>({
    queryKey: [`/api/public/transporters/${transporterId}`],
    enabled: !!transporterId,
  });

  if (isLoading) {
    return (
      <div className="mb-4">
        <Label className="text-sm font-medium text-gray-700 mb-2 block">
          CNPJ Cadastrado
        </Label>
        <div className="text-sm text-gray-500 p-3 border border-gray-200 rounded-lg">
          Carregando CNPJs...
        </div>
      </div>
    );
  }

  if (!transporter) {
    return (
      <div className="mb-4">
        <Label className="text-sm font-medium text-gray-700 mb-2 block">
          CNPJ Cadastrado
        </Label>
        <div className="text-sm text-gray-500 p-3 border border-gray-200 rounded-lg">
          Transportador nÃ£o encontrado
        </div>
      </div>
    );
  }

  // Construir lista de opÃ§Ãµes de CNPJ
  const cnpjOptions: CnpjOption[] = [];

  // Adicionar matriz
  cnpjOptions.push({
    value: transporter.documentNumber,
    label: `${transporter.tradeName || transporter.name} - ${transporter.documentNumber}`,
    type: "matriz",
    location: `${transporter.city}/${transporter.state}`,
  });

  // Adicionar filiais
  let subsidiaries = [];
  if (transporter.subsidiaries) {
    try {
      // Tentar analisar como JSON se for string
      subsidiaries = typeof transporter.subsidiaries === 'string' 
        ? JSON.parse(transporter.subsidiaries) 
        : transporter.subsidiaries;
    } catch (e) {
      console.log('Erro ao processar subsidiÃ¡rias:', e);
      subsidiaries = [];
    }
  }

  // Garantir que Ã© um array
  if (!Array.isArray(subsidiaries)) {
    subsidiaries = [];
  }

  console.log('[CNPJ Selector] SubsidiÃ¡rias processadas:', subsidiaries);

  subsidiaries.forEach((subsidiary) => {
    // Verificar se tem documentNumber ou cnpj
    const cnpj = subsidiary.documentNumber || subsidiary.cnpj;
    if (cnpj) {
      cnpjOptions.push({
        value: cnpj,
        label: `${subsidiary.name} - ${cnpj}`,
        type: "filial",
        location: `${subsidiary.city}/${subsidiary.state}`,
      });
    }
  });

  console.log('[CNPJ Selector] OpÃ§Ãµes de CNPJ geradas:', cnpjOptions);

  const handleCnpjChange = (value: string) => {
    console.log('[CNPJ Selector] CNPJ selecionado:', value);
    setSelectedCnpj(value);

    const selectedOption = cnpjOptions.find((option) => option.value === value);
    if (selectedOption && onCnpjSelect) {
      console.log('[CNPJ Selector] Chamando callback onCnpjSelect com:', value, selectedOption.label);
      onCnpjSelect(value, selectedOption.label);
    }
  };

  return (
    <div className="mb-4">
      <Select value={selectedCnpj} onValueChange={handleCnpjChange}>
        <SelectTrigger className="w-full">
          <SelectValue placeholder="Selecione um CNPJ cadastrado" />
        </SelectTrigger>
        <SelectContent 
          className="z-[9999] max-h-60 overflow-auto" 
          position="popper" 
          sideOffset={4}
        >
          {cnpjOptions.map((option, index) => (
            <SelectItem key={`${option.value}-${index}`} value={option.value}>
              <div className="flex items-center space-x-2 w-full">
                <Building2
                  className={`h-4 w-4 ${option.type === "matriz" ? "text-blue-600" : "text-green-600"}`}
                />
                <div className="flex-1">
                  <div className="font-medium text-sm">
                    {option.type === "matriz" ? "(Matriz)" : "(Filial)"}{" "}
                    {option.label.split(" - ")[0]}
                  </div>
                  <div className="text-xs text-gray-500 flex items-center space-x-2">
                    <span>CNPJ: {option.value}</span>
                    <span>â€¢</span>
                    <span>{option.location}</span>
                  </div>
                </div>
              </div>
            </SelectItem>
          ))}
        </SelectContent>
      </Select>

      {selectedCnpj && (
        <div className="mt-2 text-xs text-gray-600">
          <div className="bg-blue-50 p-2 rounded border border-blue-200">
            <span className="font-medium">CNPJ Selecionado:</span>{" "}
            {selectedCnpj}
          </div>
        </div>
      )}

      {cnpjOptions.length === 0 && (
        <div className="text-sm text-gray-500 p-3 border border-gray-200 rounded-lg">
          Nenhum CNPJ encontrado para este transportador
        </div>
      )}
    </div>
  );
}

import { useQuery } from "@tanstack/react-query";
import { Transporter } from "@shared/schema";
import { Loader2 } from "lucide-react";

interface TransporterInfoProps {
  transporterId: number | null;
  className?: string;
  compact?: boolean;
}

/**
 * Componente otimizado para exibir informaÃ§Ãµes do transportador
 * 
 * CaracterÃ­sticas:
 * - Utiliza endpoint pÃºblico que nÃ£o requer autenticaÃ§Ã£o
 * - Implementa cache de 10 minutos para reduzir chamadas Ã  API
 * - Suporta dois modos de exibiÃ§Ã£o: completo e compacto
 * - Exibe informaÃ§Ãµes formatadas de acordo com o tipo de pessoa (PJ/PF)
 * - Gerencia estados de carregamento e erro de forma intuitiva
 * 
 * Casos de uso:
 * - ExibiÃ§Ã£o de dados do transportador em detalhes de licenÃ§as
 * - ReferÃªncia em listagens e relatÃ³rios
 * - SeleÃ§Ã£o de transportador em formulÃ¡rios
 * 
 * Props:
 * - transporterId: ID do transportador a ser exibido
 * - className: Classe CSS adicional para estilizaÃ§Ã£o
 * - compact: Se true, exibe uma versÃ£o mais compacta do componente (apenas nome do transportador)
 */
export const TransporterInfo = ({ 
  transporterId, 
  className = "", 
  compact = false 
}: TransporterInfoProps) => {
  const { data: transporter, isLoading } = useQuery<Transporter>({
    queryKey: ['/api/public/transporters', transporterId],
    queryFn: async () => {
      if (!transporterId) return null;
      console.log(`[TransporterInfo] Buscando dados do transportador ID: ${transporterId}`);

      // Usar o endpoint pÃºblico que nÃ£o requer autenticaÃ§Ã£o
      const res = await fetch(`/api/public/transporters/${transporterId}`);

      if (!res.ok) {
        console.error(`[TransporterInfo] Erro ao buscar transportador ID ${transporterId}:`, res.status);
        return null;
      }
      const data = await res.json();
      console.log(`[TransporterInfo] Dados do transportador ID ${transporterId} carregados:`, data);
      return data;
    },
    enabled: !!transporterId,
    staleTime: 10 * 60 * 1000, // Cache por 10 minutos
    retry: 1
  });

  if (compact) {
    return (
      <span className={`inline-block ${className}`}>
        {isLoading ? (
          <span className="inline-flex items-center gap-1">
            <Loader2 className="h-3 w-3 animate-spin text-gray-400" />
            <span className="text-gray-500">Carregando...</span>
          </span>
        ) : transporter ? (
          <span className="font-medium">
            {transporter.name}
          </span>
        ) : (
          <span className="text-gray-500 italic">Transportador nÃ£o encontrado</span>
        )}
      </span>
    );
  }

  return (
    <div className={className}>
      {isLoading ? (
        <div className="flex items-center space-x-2">
          <Loader2 className="h-4 w-4 animate-spin text-gray-400" />
          <span className="text-sm text-gray-500">Carregando dados do transportador...</span>
        </div>
      ) : transporter ? (
        <>
          <p className="text-gray-900 font-medium">
            {transporter.name}
          </p>
          {transporter.documentNumber && (
            <p className="text-sm text-gray-700">
              <span className="font-medium">
                {transporter.personType === "pj" ? "CNPJ" : "CPF"}:
              </span> {transporter.documentNumber}
            </p>
          )}
        </>
      ) : (
        <p className="text-gray-500">Transportador nÃ£o encontrado</p>
      )}
    </div>
  );
};

import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { ChevronDown, Building2, MapPin } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
  DropdownMenuSeparator,
} from "@/components/ui/dropdown-menu";

interface TransporterWithSubsidiariesProps {
  transporterId: number;
  compact?: boolean;
}

interface Subsidiary {
  name: string;
  documentNumber: string;
  city: string;
  state: string;
  isActive?: boolean;
}

interface TransporterData {
  id: number;
  name: string;
  tradeName?: string;
  documentNumber: string;
  city: string;
  state: string;
  subsidiaries: Subsidiary[];
}

export function TransporterWithSubsidiaries({ transporterId, compact = false }: TransporterWithSubsidiariesProps) {
  const { data: transporter, isLoading } = useQuery<TransporterData>({
    queryKey: [`/api/public/transporters/${transporterId}`],
    enabled: !!transporterId,
  });

  if (isLoading) {
    return <div className="text-sm text-gray-500">Carregando...</div>;
  }

  if (!transporter) {
    return <div className="text-sm text-gray-500">Transportador nÃ£o encontrado</div>;
  }

  const subsidiaries = Array.isArray(transporter.subsidiaries) ? transporter.subsidiaries : [];
  const hasSubsidiaries = subsidiaries.length > 0;

  // Formato compacto (para tabelas)
  if (compact) {
    if (!hasSubsidiaries) {
      return (
        <div className="text-sm">
          <div className="font-medium text-gray-900 truncate max-w-[200px]" title={transporter.tradeName || transporter.name}>
            {transporter.tradeName || transporter.name}
          </div>
          <div className="text-xs text-gray-500 flex items-center">
            <MapPin className="h-3 w-3 mr-1" />
            {transporter.city}/{transporter.state}
          </div>
        </div>
      );
    }

    return (
      <DropdownMenu>
        <DropdownMenuTrigger asChild>
          <Button variant="ghost" className="h-auto p-1 justify-start text-left">
            <div className="text-sm">
              <div className="font-medium text-gray-900 truncate max-w-[180px] flex items-center" title={transporter.tradeName || transporter.name}>
                <Building2 className="h-3 w-3 mr-1 text-blue-600" />
                {transporter.tradeName || transporter.name}
                <ChevronDown className="h-3 w-3 ml-1" />
              </div>
              <div className="text-xs text-gray-500 flex items-center">
                <MapPin className="h-3 w-3 mr-1" />
                {transporter.city}/{transporter.state}
                {hasSubsidiaries && (
                  <span className="ml-1 text-blue-600">
                    +{subsidiaries.length} filial{subsidiaries.length > 1 ? 'is' : ''}
                  </span>
                )}
              </div>
            </div>
          </Button>
        </DropdownMenuTrigger>
        <DropdownMenuContent align="start" className="w-80">
          <DropdownMenuItem className="flex-col items-start p-3 cursor-default">
            <div className="font-medium text-gray-900 flex items-center">
              <Building2 className="h-4 w-4 mr-2 text-blue-600" />
              Matriz
            </div>
            <div className="text-sm text-gray-700 mt-1">
              {transporter.tradeName || transporter.name}
            </div>
            <div className="text-xs text-gray-500 mt-1 flex items-center">
              <MapPin className="h-3 w-3 mr-1" />
              {transporter.city}/{transporter.state}
            </div>
            <div className="text-xs text-gray-500 mt-1">
              CNPJ: {transporter.documentNumber}
            </div>
          </DropdownMenuItem>

          {hasSubsidiaries && (
            <>
              <DropdownMenuSeparator />
              <div className="px-3 py-2 text-xs font-medium text-gray-500 uppercase tracking-wide">
                Filiais ({subsidiaries.length})
              </div>
              {subsidiaries.map((subsidiary, index) => (
                <DropdownMenuItem key={index} className="flex-col items-start p-3 cursor-default">
                  <div className="font-medium text-gray-800 flex items-center">
                    <Building2 className="h-4 w-4 mr-2 text-green-600" />
                    {subsidiary.name}
                  </div>
                  <div className="text-xs text-gray-500 mt-1 flex items-center">
                    <MapPin className="h-3 w-3 mr-1" />
                    {subsidiary.city}/{subsidiary.state}
                  </div>
                  <div className="text-xs text-gray-500 mt-1">
                    CNPJ: {subsidiary.documentNumber}
                  </div>
                </DropdownMenuItem>
              ))}
            </>
          )}
        </DropdownMenuContent>
      </DropdownMenu>
    );
  }

  // Formato expandido (para formulÃ¡rios e visualizaÃ§Ãµes detalhadas)
  return (
    <div className="space-y-3">
      <div className="p-3 border border-gray-200 rounded-lg bg-blue-50">
        <div className="font-medium text-gray-900 flex items-center">
          <Building2 className="h-4 w-4 mr-2 text-blue-600" />
          Matriz
        </div>
        <div className="text-sm text-gray-700 mt-1">
          {transporter.tradeName || transporter.name}
        </div>
        <div className="text-xs text-gray-500 mt-1 flex items-center">
          <MapPin className="h-3 w-3 mr-1" />
          {transporter.city}/{transporter.state}
        </div>
        <div className="text-xs text-gray-500 mt-1">
          CNPJ: {transporter.documentNumber}
        </div>
      </div>


    </div>
  );
}

