  const [cmt, setCmt] = useState<number | undefined>(undefined);

  // Verificar se hÃ¡ uma placa prÃ©-preenchida (vinda de outro componente)
  useEffect(() => {
    const preFillPlate = localStorage.getItem('preFillPlate');
