                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            {/* CMT (apenas para unidade tratora) */}
            {vehicleType === "tractor_unit" && (
              <FormField
                control={form.control}
                name="cmt"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium">
                      CMT (kg) <span className="text-red-500">*</span>
                    </FormLabel>
                    <FormControl>
                      <Input 
                        type="number" 
                        placeholder="Ex: 80.00" 
                        step="0.01"
                        min="0"
                        max="200000"
                        {...field}
                        value={field.value || ''} 
                        onChange={(e) => {
                          const value = parseFloat(e.target.value) || undefined;
                          field.onChange(value);
                        }}
                        className="h-10 w-full" 
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            )}

            {/* Status do VeÃ­culo (sÃ³ quando editando) */}
            {vehicle && (
              <FormField
                control={form.control}
                name="status"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium">
                      Status do VeÃ­culo
                    </FormLabel>
                    <Select 
                      onValueChange={(value) => field.onChange(value)} 
                      value={field.value}
                      defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger className="h-10 w-full">
                          <SelectValue placeholder="Selecione o status" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="active">Ativo</SelectItem>
                        <SelectItem value="maintenance">Em ManutenÃ§Ã£o</SelectItem>
                        <SelectItem value="inactive">Inativo</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            )}

            {/* Tipo de Carroceria (campo largo quando necessÃ¡rio) */}
            {(vehicleType === "truck" || vehicleType === "semi_trailer" || vehicleType === "trailer") && (
              <FormField
                control={form.control}
                name="bodyType"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium">
                      Tipo de Carroceria <span className="text-red-500">*</span>
                    </FormLabel>
                    <Select 
                      onValueChange={(value) => field.onChange(value)} 
                      value={field.value}
                      defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger className="h-10 w-full">
                          <SelectValue placeholder="Selecione" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {bodyTypeOptions.map((option) => (
                          <SelectItem key={option.value} value={option.value}>
                            {option.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            )}

            {/* ObservaÃ§Ãµes (campo largo) */}
            <FormField
              control={form.control}
              name="remarks"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-sm font-medium">ObservaÃ§Ãµes</FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder="ObservaÃ§Ãµes sobre o veÃ­culo..." 
                      className="resize-none h-16 w-full" 
                      {...field} 
                      value={field.value || ''} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Upload do CRLV (campo largo) */}
            <div>
              <FormLabel htmlFor="crlvFile" className="text-sm font-medium">Upload do CRLV (PDF/imagem)</FormLabel>
              <div 
                className={`flex justify-center px-3 py-2 border-2 border-dashed rounded-md transition-colors ${
                  isDragOver 
                    ? 'border-blue-400 bg-blue-50' 
                    : 'border-gray-300'
                }`}
                onDragOver={handleDragOver}
                onDragLeave={handleDragLeave}
                onDrop={handleDrop}
              >
                <div className="space-y-1 text-center py-1">
                  <UploadCloud className={`mx-auto h-5 w-5 ${isDragOver ? 'text-blue-500' : 'text-gray-400'}`} />
                  <div className="flex text-xs text-gray-600">
                    <label
                      htmlFor="crlvFile"
                      className="relative cursor-pointer bg-white rounded-md font-medium text-blue-600 hover:text-blue-500 focus-within:outline-none"
                    >
                      <span>Carregar arquivo</span>
                      <input
                        id="crlvFile"
                        name="crlvFile"
                        type="file"
                        className="sr-only"
                        accept=".pdf,.jpg,.jpeg,.png"
                        onChange={handleFileChange}
                      />
                    </label>
                    <p className="pl-1">ou arraste e solte</p>
                  </div>
                  <p className="text-xs text-gray-500">
                    PDF, JPG, PNG atÃ© 10MB
                  </p>
                  {file && (
                    <p className="text-xs text-green-600">
                      Arquivo: {file.name}
                    </p>
                  )}
                  {vehicle?.crlvUrl && !file && (
                    <p className="text-xs text-blue-600">
                      <a href={vehicle.crlvUrl} target="_blank" rel="noopener noreferrer">
                        Visualizar CRLV atual
                      </a>
                    </p>
                  )}
                </div>
              </div>
            </div>
          </div>

          <div className="flex justify-end gap-2 py-4 px-6 border-t">
            <Button type="button" variant="outline" onClick={onCancel} className="h-10 px-4">
              Cancelar
            </Button>
            <Button type="submit" disabled={isSubmitting} className="h-10 px-4 bg-primary">
              {isSubmitting && <LoaderCircle className="mr-2 h-4 w-4 animate-spin" />}
              {vehicle ? "Atualizar" : "Cadastrar VeÃ­culo"}
            </Button>
          </div>
        </form>
      </div>
    </Form>
  );
}

