import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Button } from "@/components/ui/button";
import { 
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { Pencil, Trash, FileText, AlertCircle, ChevronLeft, ChevronRight } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { 
  Dialog, 
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";

interface VehicleListProps {
  vehicles: Vehicle[];
  isLoading: boolean;
  onEdit: (vehicle: Vehicle) => void;
  onRefresh: () => void;
}

export function VehicleList({ vehicles, isLoading, onEdit, onRefresh }: VehicleListProps) {
  const { toast } = useToast();
  const isMobile = useIsMobile();
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [selectedVehicle, setSelectedVehicle] = useState<Vehicle | null>(null);
  const [previewDialogOpen, setPreviewDialogOpen] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);

  // Constantes de paginaÃ§Ã£o
  const ITEMS_PER_PAGE = 10;

  // CÃ¡lculos de paginaÃ§Ã£o
  const totalPages = Math.ceil(vehicles.length / ITEMS_PER_PAGE);
  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const endIndex = startIndex + ITEMS_PER_PAGE;

  // VeÃ­culos paginados
  const paginatedVehicles = useMemo(() => {
    return vehicles.slice(startIndex, endIndex);
  }, [vehicles, startIndex, endIndex]);

  // Reset pÃ¡gina quando veÃ­culos mudarem (por exemplo, apÃ³s filtros)
  useMemo(() => {
    if (currentPage > totalPages && totalPages > 0) {
      setCurrentPage(1);
    }
  }, [vehicles.length, totalPages, currentPage]);

  const deleteMutation = useMutation({
    mutationFn: async (id: number) => {
      await apiRequest("DELETE", `/api/vehicles/${id}`);
    },
    onSuccess: () => {
      toast({
        title: "VeÃ­culo excluÃ­do",
        description: "O veÃ­culo foi excluÃ­do com sucesso",
      });
      onRefresh();
    },
    onError: (error: Error) => {
      toast({
        title: "Erro",
        description: error.message || "NÃ£o foi possÃ­vel excluir o veÃ­culo",
        variant: "destructive",
      });
    },
  });

  const handleDeleteClick = (vehicle: Vehicle) => {
    setSelectedVehicle(vehicle);
    setDeleteDialogOpen(true);
  };

  const handleConfirmDelete = () => {
    if (selectedVehicle) {
      deleteMutation.mutate(selectedVehicle.id);
      setDeleteDialogOpen(false);
    }
  };

  const handleDocumentPreview = (vehicle: Vehicle) => {
    // Verificar se o veÃ­culo realmente tem o CRLV disponÃ­vel
    if (!vehicle.crlvUrl) {
      toast({
        title: "Documento indisponÃ­vel",
        description: "O CRLV deste veÃ­culo nÃ£o estÃ¡ disponÃ­vel no momento.",
        variant: "destructive",
      });
      return;
    }

    if (isMobile) {
      // Em dispositivos mÃ³veis, abrir diretamente em nova aba
      window.open(vehicle.crlvUrl, '_blank', 'noopener,noreferrer');
    } else {
      // Em desktop, abrir o modal
      setSelectedVehicle(vehicle);
      setPreviewDialogOpen(true);
    }
  };

  const getVehicleTypeLabel = (type: string) => {
    switch (type) {
      case "tractor_unit": return "Unidade Tratora (Cavalo)";
      case "semi_trailer": return "Semirreboque";
      case "trailer": return "Reboque";
      case "dolly": return "Dolly";
      case "flatbed": return "Prancha";
      default: return type;
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "active":
        return <Badge className="bg-green-100 text-green-800 hover:bg-green-200">Ativo</Badge>;
      case "pending_documents":
        return <Badge className="bg-orange-100 text-orange-800 hover:bg-orange-200">Pendente DocumentaÃ§Ã£o</Badge>;
      case "maintenance":
        return <Badge className="bg-yellow-100 text-yellow-800 hover:bg-yellow-200">Em ManutenÃ§Ã£o</Badge>;
      case "inactive":
        return <Badge className="bg-gray-100 text-gray-800 hover:bg-gray-200">Inativo</Badge>;
      case "blocked":
        return <Badge className="bg-red-100 text-red-800 hover:bg-red-200">Bloqueado</Badge>;
      case "expired":
        return <Badge className="bg-red-100 text-red-800 hover:bg-red-200">Vencido</Badge>;
      default:
        return <Badge className="bg-gray-100 text-gray-800 hover:bg-gray-200">{status}</Badge>;
    }
  };

  // DiÃ¡logo de confirmaÃ§Ã£o de exclusÃ£o - aparece em qualquer visualizaÃ§Ã£o (mÃ³vel ou desktop)
  const DeleteConfirmDialog = () => (
    <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
      <AlertDialogContent>
        <AlertDialogHeader>
          <AlertDialogTitle>Excluir VeÃ­culo</AlertDialogTitle>
          <AlertDialogDescription>
            VocÃª tem certeza que deseja excluir o veÃ­culo {selectedVehicle?.plate}?
            Esta aÃ§Ã£o nÃ£o pode ser desfeita.
          </AlertDialogDescription>
        </AlertDialogHeader>
        <AlertDialogFooter>
          <AlertDialogCancel>Cancelar</AlertDialogCancel>
          <AlertDialogAction
            onClick={handleConfirmDelete}
            className="bg-red-600 hover:bg-red-700"
          >
            Excluir
          </AlertDialogAction>
        </AlertDialogFooter>
      </AlertDialogContent>
    </AlertDialog>
  );

  if (isMobile) {
    return (
      <>
        {isLoading ? (
          <div className="py-10 text-center">
            <div className="inline-block animate-spin rounded-full h-8 w-8 border-4 border-solid border-blue-500 border-r-transparent"></div>
            <p className="mt-2 text-gray-600">Carregando veÃ­culos...</p>
          </div>
        ) : paginatedVehicles.length > 0 ? (
          <div className="space-y-4">
            {paginatedVehicles.map((vehicle) => (
              <div key={vehicle.id} className="bg-white border rounded-lg p-4 shadow-sm">
                <div className="flex justify-between items-start mb-4">
                  <div className="flex flex-col">
                    <div className="font-semibold text-lg">{vehicle.plate}</div>
                    <div className="text-sm text-gray-600">{getVehicleTypeLabel(vehicle.type)}</div>
                  </div>
                  {getStatusBadge(vehicle.status)}
                </div>

                <div className="grid grid-cols-2 gap-2 mb-4 text-sm">
                  <div>
                    <span className="text-gray-500">Marca:</span> {vehicle.brand || "-"}
                  </div>
                  <div>
                    <span className="text-gray-500">Modelo:</span> {vehicle.model || "-"}
                  </div>
                  <div>
                    <span className="text-gray-500">Eixos:</span> {vehicle.axleCount || "-"}
                  </div>
                  <div>
                    <span className="text-gray-500">Ano:</span> {vehicle.year || "-"}
                  </div>
                  <div>
                    <span className="text-gray-500">Tara:</span> {vehicle.tare.toLocaleString()} kg
                  </div>
                  <div>
                    <span className="text-gray-500">Ano CRLV:</span> {vehicle.crlvYear}
                  </div>
                </div>

                <div className="mt-4 flex justify-between items-center">
                  <div>
                    {vehicle.crlvUrl ? (
                      <Button 
                        variant="outline" 
                        size="sm"
                        onClick={() => handleDocumentPreview(vehicle)}
                      >
                        <FileText className="mr-1 h-4 w-4" /> Ver CRLV
                      </Button>
                    ) : (
                      <span className="text-gray-500 text-sm">CRLV nÃ£o disponÃ­vel</span>
                    )}
                  </div>

                  <div className="flex space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => onEdit(vehicle)}
                      className="text-blue-600 border-blue-200"
                    >
                      <Pencil className="h-4 w-4 mr-1" /> Editar
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleDeleteClick(vehicle)}
                      className="text-red-600 border-red-200"
                    >
                      <Trash className="h-4 w-4 mr-1" /> Excluir
                    </Button>
                  </div>
                </div>
              </div>
            ))}

            {/* Controles de paginaÃ§Ã£o - Mobile */}
            {vehicles.length > ITEMS_PER_PAGE && (
              <div className="bg-white p-4 rounded-lg border">
                <div className="flex justify-between items-center mb-3">
                  <span className="text-sm text-gray-600">
                    PÃ¡gina {currentPage} de {totalPages}
                  </span>
                  <span className="text-sm text-gray-600">
                    {startIndex + 1}-{Math.min(endIndex, vehicles.length)} de {vehicles.length} veÃ­culos
                  </span>
                </div>

                <div className="flex justify-center items-center space-x-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
                    disabled={currentPage === 1}
                    className="flex items-center"
                  >
                    <ChevronLeft className="h-4 w-4 mr-1" />
                    Anterior
                  </Button>

                  <span className="text-sm font-medium px-2">
                    {currentPage}/{totalPages}
                  </span>

                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
                    disabled={currentPage === totalPages}
                    className="flex items-center"
                  >
                    PrÃ³xima
                    <ChevronRight className="h-4 w-4 ml-1" />
                  </Button>
                </div>
              </div>
            )}

            {/* Info de total - Mobile */}
            {vehicles.length <= ITEMS_PER_PAGE && (
              <div className="bg-white p-4 rounded-lg border text-center text-gray-600 text-sm">
                Mostrando <span className="font-medium">{vehicles.length}</span> veÃ­culos
              </div>
            )}
          </div>
        ) : (
          <div className="bg-white rounded-lg p-8 shadow text-center text-gray-500">
            <FileText className="h-12 w-12 mx-auto mb-2 text-gray-400" />
            <p>Nenhum veÃ­culo cadastrado. Clique em "Cadastrar VeÃ­culo" para adicionar.</p>
          </div>
        )}

        {/* Modal de confirmaÃ§Ã£o de exclusÃ£o - versÃ£o mÃ³vel */}
        <DeleteConfirmDialog />
      </>
    );
  }

  // VersÃ£o Desktop - Tabela
  return (
    <>
      <div className="bg-white rounded-lg shadow overflow-hidden">
        <div className="overflow-x-auto">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Placa</TableHead>
                <TableHead>Tipo de VeÃ­culo</TableHead>
                <TableHead>Marca/Modelo</TableHead>
                <TableHead>Eixos</TableHead>
                <TableHead>Tara (kg)</TableHead>
                <TableHead>Ano</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>DocumentaÃ§Ã£o</TableHead>
                <TableHead className="text-right">AÃ§Ãµes</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {isLoading ? (
                <TableRow>
                  <TableCell colSpan={9} className="text-center py-10">
                    Carregando veÃ­culos...
                  </TableCell>
                </TableRow>
              ) : paginatedVehicles.length > 0 ? (
                paginatedVehicles.map((vehicle) => (
                  <TableRow key={vehicle.id}>
