      <Dialog open={previewDialogOpen} onOpenChange={setPreviewDialogOpen}>
        <DialogContent className="max-w-3xl">
          <DialogHeader>
            <DialogTitle>CRLV do VeÃ­culo {selectedVehicle?.plate}</DialogTitle>
