// Schema para atualizaÃ§Ã£o de status
const updateStatusSchema = z.object({
  status: z.string({
    required_error: "O status Ã© obrigatÃ³rio",
  }),
  comments: z.string().optional(),
  licenseFile: z.any().optional(),
});

// Schema para atualizaÃ§Ã£o de status por estado
const updateStateStatusSchema = z.object({
  state: z.string({
    required_error: "O estado Ã© obrigatÃ³rio",
  }),
  status: z.string({
    required_error: "O status Ã© obrigatÃ³rio",
  }),
  comments: z.string().optional(),
  validUntil: z.string().optional(),
  issuedAt: z.string().optional(),
  aetNumber: z.string().optional(),
  selectedCnpj: z.string().optional(),
  licenseFile: z
    .any()
    .optional()
    .refine(
      (file) => {
        if (!file) return true;
        return file && typeof file === 'object' && 'type' in file && 
          file.type === "application/pdf";
      },
      {
        message: "Apenas arquivos PDF sÃ£o permitidos para a licenÃ§a",
      }
    ),
}).superRefine((data, ctx) => {
  // Se o status for "approved", validade e data de emissÃ£o sÃ£o obrigatÃ³rias
  if (data.status === "approved") {
    if (!data.validUntil) {
      ctx.addIssue({
        code: z.ZodIssueCode.custom,
        message: "A data de validade Ã© obrigatÃ³ria quando o status Ã© Liberada",
        path: ["validUntil"]
      });
    }
    if (!data.issuedAt) {
      ctx.addIssue({
        code: z.ZodIssueCode.custom,
        message: "A data de emissÃ£o Ã© obrigatÃ³ria quando o status Ã© Liberada",
        path: ["issuedAt"]
      });
    }
  }

  // Se o status for "under_review" ou "pending_approval", nÃºmero da AET Ã© obrigatÃ³rio
  if ((data.status === "under_review" || data.status === "pending_approval") && !data.aetNumber) {
    ctx.addIssue({
      code: z.ZodIssueCode.custom,
      message: `O nÃºmero da AET Ã© obrigatÃ³rio quando o status Ã© ${data.status === "under_review" ? "AnÃ¡lise do Ã“rgÃ£o" : "Pendente LiberaÃ§Ã£o"}`,
      path: ["aetNumber"]
    });
  }

  // Para o status "approved", o nÃºmero da AET deve ser informado apenas se nÃ£o houver um nÃºmero anterior
  if (data.status === "approved" && !data.aetNumber) {
    // NÃ£o vamos adicionar o erro aqui, pois o backend vai buscar o valor do status anterior
    // Mas podemos melhorar isso com validaÃ§Ã£o do lado do cliente se necessÃ¡rio
  }
});

// Constantes e funÃ§Ãµes auxiliares para status

export default function AdminLicensesPage() {
  const { toast } = useToast();
  const { user } = useAuth();
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [transporterFilter, setTransporterFilter] = useState("all");
  const [transporterSearchTerm, setTransporterSearchTerm] = useState("");
  const [dateFilter, setDateFilter] = useState("");
  const [stateFilter, setStateFilter] = useState("all_states");
  const [selectedLicense, setSelectedLicense] = useState<LicenseRequest | null>(null);
  const [licenseDetailsOpen, setLicenseDetailsOpen] = useState(false);
  const [stateStatusDialogOpen, setStateStatusDialogOpen] = useState(false);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [selectedState, setSelectedState] = useState("");
  const [location] = useLocation();
  const [visibleStateFlows, setVisibleStateFlows] = useState<string[]>([]);

  // Estado para ordenaÃ§Ã£o
  const [sortField, setSortField] = useState<string>("createdAt");
  const [sortDirection, setSortDirection] = useState<"asc" | "desc">("desc");
  const { lastMessage, isConnected } = useWebSocketContext();

  // Estados para controle do botÃ£o de atualizaÃ§Ã£o
  const [isRefreshing, setIsRefreshing] = useState(false);

  // Effect para invalidar cache quando houver atualizaÃ§Ãµes via WebSocket
  useEffect(() => {
    if (lastMessage && lastMessage.data) {
      try {
        const message = JSON.parse(lastMessage.data);

        // Invalidar cache para qualquer tipo de atualizaÃ§Ã£o
        if (message.type === 'STATUS_UPDATE' || message.type === 'LICENSE_UPDATE') {
          console.log('[REALTIME] Recebida atualizaÃ§Ã£o, invalidando cache:', message);

          // Invalidar todas as queries relacionadas
          queryClient.invalidateQueries({ queryKey: ['/api/admin/licenses'] });
          queryClient.invalidateQueries({ queryKey: ['/api/admin/transporters'] });
          queryClient.invalidateQueries({ queryKey: ['/api/public/transporters'] });
          queryClient.invalidateQueries({ queryKey: ['/api/vehicles'] });
          queryClient.invalidateQueries({ queryKey: ['/api/licenses'] });

          // ForÃ§ar refetch imediato
          queryClient.refetchQueries({ queryKey: ['/api/admin/licenses'] });

          toast({
            title: "Dados atualizados",
            description: "As informaÃ§Ãµes foram atualizadas automaticamente.",
          });
        }
      } catch (error) {
        console.log('[REALTIME] Erro ao processar mensagem WebSocket:', error);
      }
    }
  }, [lastMessage, toast]);

  // Verificar se o usuÃ¡rio Ã© do tipo operacional
  const isOperational = user?.role === 'operational';

  // Verificar se estamos na rota de gerenciar-licencas (staff) ou admin
  const isStaffRoute = location.includes('gerenciar-licencas');
  const apiEndpoint = isStaffRoute ? '/api/staff/licenses' : '/api/admin/licenses';

  // Efeito para atualizar o objeto selectedLicense em tempo real quando receber mensagem WebSocket
  useEffect(() => {
    if (
      lastMessage?.type === 'STATUS_UPDATE' && 
      lastMessage.data && 
      selectedLicense && 
      lastMessage.data.licenseId === selectedLicense.id
    ) {
      // Se o evento Ã© para um estado especÃ­fico
      if (lastMessage.data.state) {
        // AtualizaÃ§Ã£o de status de um estado especÃ­fico
        const updatedStateStatuses = [...(selectedLicense.stateStatuses || [])];
        const stateStatusIndex = updatedStateStatuses.findIndex(
          entry => entry.startsWith(`${lastMessage.data.state}:`)
        );

        // Se o estado jÃ¡ existe nos status, atualizar
        if (stateStatusIndex >= 0) {
          updatedStateStatuses[stateStatusIndex] = `${lastMessage.data.state}:${lastMessage.data.status}`;
        } else {
          // Se nÃ£o existe, adicionar
          updatedStateStatuses.push(`${lastMessage.data.state}:${lastMessage.data.status}`);
        }

        // Criar uma cÃ³pia atualizada da licenÃ§a selecionada
        setSelectedLicense(prevLicense => {
          if (!prevLicense) return null;
          const updatedLicense = {
            ...prevLicense,
            stateStatuses: updatedStateStatuses,
            // Se tambÃ©m recebemos uma atualizaÃ§Ã£o completa da licenÃ§a, usar todos os dados
            ...(lastMessage.data.license && lastMessage.data.license),
            // Se tambÃ©m recebemos uma atualizaÃ§Ã£o para o status geral da licenÃ§a
            ...(lastMessage.data.license?.status && { status: lastMessage.data.license.status })
          };

          // Se o modal de ediÃ§Ã£o de status estÃ¡ aberto para este estado, atualizar o formulÃ¡rio
          if (stateStatusDialogOpen && selectedState === lastMessage.data.state) {
            console.log('[WebSocket] Atualizando formulÃ¡rio em tempo real para estado:', lastMessage.data.state);
            // Usar setTimeout para garantir que o estado foi atualizado
            setTimeout(() => {
              // Determinar o CNPJ especÃ­fico para este estado
              let currentStateCnpj = "";
              if (updatedLicense.stateCnpjs && updatedLicense.stateCnpjs.length > 0) {
                const stateCnpjEntry = updatedLicense.stateCnpjs.find((entry: string) => entry.startsWith(`${lastMessage.data.state}:`));
                if (stateCnpjEntry) {
                  const [_, cnpj] = stateCnpjEntry.split(':');
                  if (cnpj) {
                    currentStateCnpj = cnpj;
                  }
                }
              }

              // Fallback para o CNPJ global se nÃ£o houver CNPJ especÃ­fico para o estado
              if (!currentStateCnpj && updatedLicense.selectedCnpj) {
                currentStateCnpj = updatedLicense.selectedCnpj;
              }

              // Atualizar apenas o campo selectedCnpj do formulÃ¡rio se necessÃ¡rio
              const currentFormCnpj = stateStatusForm.getValues("selectedCnpj");
              if (currentFormCnpj !== currentStateCnpj) {
                console.log('[WebSocket] Atualizando CNPJ no formulÃ¡rio de', currentFormCnpj, 'para', currentStateCnpj);
                stateStatusForm.setValue("selectedCnpj", currentStateCnpj);
              }
            }, 100);
          }

          return updatedLicense;
        });

        console.log(`StatusUpdate em tempo real: LicenÃ§a ${selectedLicense.id} estado ${lastMessage.data.state} => ${lastMessage.data.status}`);
      } 
      // Se o evento Ã© para a licenÃ§a inteira (sem estado especÃ­fico)
      else if (lastMessage.data.license) {
        setSelectedLicense(prevLicense => {
          if (!prevLicense) return null;
          return {
            ...prevLicense,
            status: lastMessage.data.license.status,
            ...(lastMessage.data.license.stateStatuses && { stateStatuses: lastMessage.data.license.stateStatuses })
          };
        });

        console.log(`StatusUpdate em tempo real: LicenÃ§a ${selectedLicense.id} => ${lastMessage.data.license.status}`);
      }
    }

    // ATUALIZAÃ‡ÃƒO EM TEMPO REAL PARA A LISTA PRINCIPAL
    // Atualizar tambÃ©m a lista principal de licenÃ§as quando houver mudanÃ§as de status
    if (
      lastMessage?.type === 'STATUS_UPDATE' && 
      lastMessage.data && 
      lastMessage.data.licenseId && 
      lastMessage.data.state && 
      lastMessage.data.status
    ) {
      // Invalidar a query da lista de licenÃ§as para recarregar com os dados atualizados
      queryClient.invalidateQueries({ queryKey: [apiEndpoint] });

      console.log(`[TEMPO REAL] Lista atualizada: LicenÃ§a ${lastMessage.data.licenseId}, Estado ${lastMessage.data.state} => ${lastMessage.data.status}`);
    }
  }, [lastMessage, selectedLicense]);

  // Form removido para atualizaÃ§Ã£o de status geral

  // Form para atualizaÃ§Ã£o de status por estado
  const stateStatusForm = useForm<z.infer<typeof updateStateStatusSchema>>({
    resolver: zodResolver(updateStateStatusSchema),
    defaultValues: {
      state: "",
      status: "",
      comments: "",
      aetNumber: "", // Adicionar campo para nÃºmero da AET
      licenseFile: undefined, // Adicionar valor padrÃ£o para licenseFile
      validUntil: "", // Corrigindo: iniciar como string vazia ao invÃ©s de undefined
    },
  });

  // Buscar todas as licenÃ§as (excluindo rascunhos de renovaÃ§Ã£o)
  const { data: licenses = [], isLoading, refetch } = useQuery<LicenseRequest[]>({
    queryKey: [apiEndpoint, "excludeRenewal"],
    queryFn: async () => {
      const res = await fetch(`${apiEndpoint}?includeRenewal=false`, {
        credentials: "include"
      });
      if (!res.ok) {
        if (res.status === 401) {
          throw new Error("NÃ£o autorizado");
        }
        throw new Error("Erro ao buscar licenÃ§as");
      }

      const data = await res.json();

      // Filtro adicional no cliente para garantir que nÃ£o vamos mostrar
      // rascunhos de renovaÃ§Ã£o na pÃ¡gina de gerenciamento
      return data.filter((license: LicenseRequest) => {
        return !(license.isDraft && license.comments?.includes('RenovaÃ§Ã£o'));
      });
    },
    // TEMPO REAL: ConfiguraÃ§Ãµes para atualizaÃ§Ãµes automÃ¡ticas
    staleTime: 30 * 1000, // 30 segundos
    refetchInterval: 60 * 1000, // Refetch a cada 60 segundos
    refetchOnWindowFocus: true,
    refetchOnMount: true,
  });

  // Buscar todos os transportadores para o filtro
  const { data: transporters = [] } = useQuery<Transporter[]>({
    queryKey: ['/api/admin/transporters'],
  });

  // FunÃ§Ã£o de atualizaÃ§Ã£o melhorada com feedback visual e integraÃ§Ã£o WebSocket
  const handleRefresh = async () => {
    setIsRefreshing(true);
    try {
      // Invalidar cache primeiro
      await queryClient.invalidateQueries({ queryKey: [apiEndpoint] });
      await queryClient.invalidateQueries({ queryKey: ["/api/admin/transporters"] });

      // Fazer refetch
      await refetch();

      toast({
        title: "Sucesso",
        description: "Lista de licenÃ§as atualizada com sucesso.",
      });
    } catch (error) {
      console.error("Erro ao atualizar:", error);
      toast({
        title: "Erro",
        description: "Erro ao atualizar a lista. Tente novamente.",
        variant: "destructive",
      });
    } finally {
      setIsRefreshing(false);
    }
  };

  // MutaÃ§Ã£o para atualizaÃ§Ã£o de status geral foi removida - agora sÃ³ usamos atualizaÃ§Ã£o por estado

  // Atualizar status por estado da licenÃ§a
  const updateStateStatusMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number, data: z.infer<typeof updateStateStatusSchema> }) => {
      const formData = new FormData();
      formData.append("state", data.state);
      formData.append("status", data.status);
      if (data.comments) {
        formData.append("comments", data.comments);
      }

      // Incluir data de validade se fornecida
      if (data.validUntil) {
        formData.append("validUntil", data.validUntil);
      }

      // Incluir data de emissÃ£o se fornecida  
      if (data.issuedAt) {
        formData.append("issuedAt", data.issuedAt);
        console.log('[Frontend] Data de emissÃ£o sendo enviada:', data.issuedAt);
      }

      // Incluir arquivo da licenÃ§a se o status for "approved" (Liberada)
      if (data.licenseFile && data.status === "approved") {
        formData.append("stateFile", data.licenseFile);
      }

      // Incluir nÃºmero da AET se o status for "under_review" (AnÃ¡lise do Ã“rgÃ£o), "pending_approval" (Pendente LiberaÃ§Ã£o) ou "approved" (Liberada)
      if (data.aetNumber && (data.status === "under_review" || data.status === "pending_approval" || data.status === "approved")) {
        formData.append("aetNumber", data.aetNumber);
      }

      // Incluir CNPJ selecionado sempre (pode ser string vazia)
      formData.append("selectedCnpj", data.selectedCnpj || "");

      // Incluir CNPJ especÃ­fico para este estado
      formData.append("stateCnpj", data.selectedCnpj || "");
      console.log('Enviando dados - selectedCnpj:', data.selectedCnpj);
      console.log('Enviando dados - stateCnpj:', data.selectedCnpj);
      console.log('Enviando dados - state:', data.state);

      const response = await apiRequest("PATCH", `/api/admin/licenses/${id}/state-status`, formData);
      return await response.json();
    },
    onSuccess: (updatedLicense) => {
      // Primeiro, mostrar a notificaÃ§Ã£o de sucesso
      toast({
        title: "Status do estado atualizado",
        description: "Status do estado atualizado com sucesso!",
      });

      // Fechar o modal apÃ³s sucesso
      setTimeout(() => {
        setStateStatusDialogOpen(false);
        setSelectedState("");
      }, 500);

      // Invalidar todas as queries relacionadas para garantir dados atualizados
      setTimeout(() => {
        // Invalidar as consultas especÃ­ficas
        queryClient.invalidateQueries({ queryKey: [apiEndpoint] });
        queryClient.invalidateQueries({ queryKey: [`${apiEndpoint}/${updatedLicense.id}`] });
        queryClient.invalidateQueries({ queryKey: ['/api/licenses/issued'] });
        queryClient.invalidateQueries({ queryKey: ['/api/licenses'] });

        // ForÃ§ar uma nova busca dos dados (opcional, mas pode ajudar)
        refetch();
      }, 300);
    },
    onError: (error: Error) => {
      toast({
        title: "Erro ao atualizar status do estado",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // MutaÃ§Ã£o para excluir licenÃ§a
  const deleteLicenseMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await apiRequest("DELETE", `/api/admin/licenses/${id}`);
      return response.ok;
    },
    onSuccess: () => {
      toast({
        title: "LicenÃ§a excluÃ­da",
        description: "A licenÃ§a foi excluÃ­da com sucesso!",
      });
      // Invalidar as queries para manter a consistÃªncia
      queryClient.invalidateQueries({ queryKey: [apiEndpoint] });
      setDeleteDialogOpen(false);
      setLicenseDetailsOpen(false);
      setSelectedLicense(null);
    },
    onError: (error: Error) => {
      toast({
        title: "Erro ao excluir licenÃ§a",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Filtrar licenÃ§as com critÃ©rios mÃºltiplos
  const filteredLicenses = licenses
    .filter(license => {
      // Filtro de busca (nÃºmero do pedido, placa)
      const matchesSearch = !searchTerm || 
        license.requestNumber?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        license.mainVehiclePlate?.toLowerCase().includes(searchTerm.toLowerCase());

      // Filtro de status - buscar nos estados individuais, nÃ£o no status geral
      let matchesStatus = !statusFilter || statusFilter === "all";

      if (statusFilter && statusFilter !== "all") {
        // Verificar se algum estado da licenÃ§a tem o status selecionado
        if (license.stateStatuses && license.stateStatuses.length > 0) {
          matchesStatus = license.stateStatuses.some(stateStatus => {
            const parts = stateStatus.split(':');
            // O status estÃ¡ na segunda posiÃ§Ã£o: "ESTADO:STATUS:..."
            return parts[1] === statusFilter;
          });
        } else {
          // Se nÃ£o hÃ¡ status por estado, verificar o status geral (fallback)
          matchesStatus = license.status === statusFilter;
        }
      }

      // Filtro de transportador por busca de texto (nome, CNPJ, CPF)
      let matchesTransporter = true;
      if (transporterSearchTerm.trim()) {
        const searchLower = transporterSearchTerm.toLowerCase().trim();
        const transporter = transporters.find(t => t.id === license.transporterId);

        if (transporter) {
          const nameMatch = Boolean(transporter.name?.toLowerCase().includes(searchLower));
          const documentMatch = Boolean(transporter.documentNumber?.toLowerCase().includes(searchLower));
          const tradeNameMatch = Boolean(transporter.tradeName?.toLowerCase().includes(searchLower));

          matchesTransporter = nameMatch || documentMatch || tradeNameMatch;
        } else {
          matchesTransporter = false;
        }
      }

      // Filtro de data
      let matchesDate = true;
      if (dateFilter) {
        const requestDate = license.createdAt ? new Date(license.createdAt) : null;
        const filterDate = new Date(dateFilter);

        if (requestDate) {
          // Comparar apenas ano, mÃªs e dia
          matchesDate = 
            requestDate.getFullYear() === filterDate.getFullYear() &&
            requestDate.getMonth() === filterDate.getMonth() &&
            requestDate.getDate() === filterDate.getDate();
        } else {
          matchesDate = false;
        }
      }

      // Filtro por estado
      let matchesState = true;
      if (stateFilter && stateFilter !== "all_states") {
        matchesState = license.states && license.states.includes(stateFilter);
      }

      return matchesSearch && matchesStatus && matchesTransporter && matchesDate && matchesState;
    })
    // Aplicar ordenaÃ§Ã£o
    .sort((a, b) => {
      const getValue = (license: LicenseRequest, field: string) => {
        switch (field) {
          case 'requestNumber':
            return license.requestNumber || '';
          case 'type':
            return license.type || '';
          case 'mainVehiclePlate':
            return license.mainVehiclePlate || '';
          case 'status':
            return license.status || '';
          case 'createdAt':
            return new Date(license.createdAt || 0).getTime();
          default:
            return '';
        }
      };

      const aValue = getValue(a, sortField);
      const bValue = getValue(b, sortField);

      // Se ambos os valores sÃ£o strings, ordenar ignorando case
      if (typeof aValue === 'string' && typeof bValue === 'string') {
        const comparison = aValue.localeCompare(bValue);
        return sortDirection === 'asc' ? comparison : -comparison;
      }

      // Se sÃ£o nÃºmeros (timestamp para datas)
      if (sortDirection === 'asc') {
        return (aValue as number) - (bValue as number);
      } else {
        return (bValue as number) - (aValue as number);
      }
    });

  // Hook de paginaÃ§Ã£o das licenÃ§as filtradas
  const { 
    paginatedItems: paginatedLicenses, 
    pagination, 
    currentPage, 
    setCurrentPage 
  } = usePaginatedList({ items: filteredLicenses });

  // FunÃ§Ã£o removida pois o status agora sÃ³ serÃ¡ editado por estado individual

  const handleViewDetails = (license: LicenseRequest) => {
    console.log("Detalhes da licenÃ§a:", license);
    setSelectedLicense(license);
    // Inicialmente, todos os estados tÃªm o fluxo oculto
    setVisibleStateFlows([]);
    setLicenseDetailsOpen(true);
  };

  // FunÃ§Ã£o removida pois o status agora sÃ³ Ã© editado por estado individual

  const handleStateStatusUpdate = (license: LicenseRequest, state: string) => {
    setSelectedLicense(license);
    setSelectedState(state);

    // Determinar o status atual deste estado
    let currentStateStatus = "pending";

    // Parse dos stateStatuses (que sÃ£o armazenados como "ESTADO:STATUS:VALIDUNTIL:ISSUEDDAT")
    let currentValidUntil = "";
    let currentIssuedAt = "";

    if (license.stateStatuses && license.stateStatuses.length > 0) {
      const stateStatusEntry = license.stateStatuses.find(entry => entry.startsWith(`${state}:`));
      if (stateStatusEntry) {
        const parts = stateStatusEntry.split(':');
        if (parts[1]) {
          currentStateStatus = parts[1];
        }
        // Extrair data de validade (3Âª parte)
        if (parts[2]) {
          currentValidUntil = parts[2];
        }
        // Extrair data de emissÃ£o especÃ­fica do estado (4Âª parte)
        if (parts[3]) {
          // Converter a data ISO para formato do input (YYYY-MM-DD)
          try {
            const issuedDate = new Date(parts[3]);
            currentIssuedAt = issuedDate.toISOString().split('T')[0];
          } catch (e) {
            console.warn('Erro ao converter data de emissÃ£o do estado:', parts[3]);
