              <div className="flex justify-center items-center gap-4 mt-6 mb-2">
                <div className="bg-gray-50 rounded-md px-8 py-3 shadow-sm mx-auto">
                  <Button 
                    onClick={handleCloseLicenseDetails}
                    className="bg-blue-600 hover:bg-blue-700 text-white px-8 py-2 rounded-md"
                  >
                    <X className="h-4 w-4 mr-2" />
                    Fechar detalhes
                  </Button>
                </div>

                {/* BotÃ£o de excluir licenÃ§a - nÃ£o visÃ­vel para usuÃ¡rios operacionais */}
                {!isOperational && (
                  <div className="bg-gray-50 rounded-md px-8 py-3 shadow-sm mx-auto">
                    <Button 
                      onClick={handleDeleteLicense}
                      variant="destructive"
                      className="px-8 py-2 rounded-md"
                    >
                      <Trash2 className="h-4 w-4 mr-2" />
                      Excluir LicenÃ§a
                    </Button>
                  </div>
                )}
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>

      {/* DiÃ¡logo de confirmaÃ§Ã£o de exclusÃ£o */}
      <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Excluir LicenÃ§a</AlertDialogTitle>
            <AlertDialogDescription>
              VocÃª tem certeza que deseja excluir esta licenÃ§a?
              Esta aÃ§Ã£o nÃ£o pode ser desfeita e todos os dados associados serÃ£o perdidos.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Cancelar</AlertDialogCancel>
            <AlertDialogAction
              onClick={handleConfirmDelete}
              className="bg-red-600 hover:bg-red-700"
              disabled={deleteLicenseMutation.isPending}
            >
              {deleteLicenseMutation.isPending ? (
                <>
                  <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                  Excluindo...
                </>
              ) : (
                "Excluir"
              )}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </AdminLayout>
  );
}

