        // Buscar por nome fantasia
        const tradeNameMatch = transporter.tradeName ? transporter.tradeName.toLowerCase().includes(searchTerm) : false;

        // Buscar por CPF/CNPJ (removendo pontuaÃ§Ã£o para busca mais flexÃ­vel)
