        "CPF/CNPJ",
        "Email",
        "Telefone",
        "Contato Principal",
        "Tipo de Pessoa",
        "Data de CriaÃ§Ã£o"
      ];

      const formattedData = filteredTransporters.map((transporter: Transporter) => ({
        ID: transporter.id,
        "Nome/RazÃ£o Social": transporter.name,
        "CPF/CNPJ": transporter.documentNumber,
        Email: transporter.email,
        Telefone: transporter.phone,
        "Contato Principal": transporter.contact1Name,
        "Tipo de Pessoa": transporter.personType === "pf" ? "Pessoa FÃ­sica" : "Pessoa JurÃ­dica",
        "Data de CriaÃ§Ã£o": formatDateForCSV(transporter.createdAt)
      }));

      exportToCSV({
        filename: "transportadores",
