                  <div className="flex justify-between">
                    <span className="text-sm text-gray-500">CPF/CNPJ:</span>
                    <span className="text-sm font-medium">{transporter.documentNumber}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-500">Email:</span>
                    <span className="text-sm">{transporter.email}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-500">Contato:</span>
                    <span className="text-sm">{transporter.contact1Name}</span>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      );
    }

    return (
      <Table>
        <TableHeader>
          <TableRow>
            <TableHead>Nome/RazÃ£o Social</TableHead>
            <TableHead>CPF/CNPJ</TableHead>
            <TableHead>Email</TableHead>
            <TableHead>Contato Principal</TableHead>
            <TableHead className="text-right">AÃ§Ãµes</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {paginatedTransporters.map((transporter: Transporter) => (
            <TableRow key={transporter.id}>
              <TableCell className="font-medium">{transporter.name}</TableCell>
              <TableCell>{transporter.documentNumber}</TableCell>
              <TableCell>{transporter.email}</TableCell>
              <TableCell>{transporter.contact1Name}</TableCell>
              <TableCell className="text-right">
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" size="icon">
                      <MoreVertical size={16} />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end">
                    <DropdownMenuItem onClick={() => handleEditTransporter(transporter)}>
                      <Edit size={16} className="mr-2" />
                      Editar
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={() => handleLinkUser(transporter)}>
                      <UserCircle2 size={16} className="mr-2" />
                      Vincular UsuÃ¡rio
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={() => handleDeleteTransporter(transporter.id)} className="text-red-600">
                      <Trash size={16} className="mr-2" />
                      Excluir
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    );
  };

  return (
    <AdminLayout>
      <div className="container mx-auto py-6 max-w-6xl">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold">Cadastro Transportador</h1>
          <div className="flex gap-2">
            <Button
              variant="outline"
              size="sm"
              onClick={handleExportCSV}
              disabled={isLoading}
              title="Exportar dados dos transportadores"
            >
              <Download className="h-4 w-4 mr-2" />
              Exportar
            </Button>
            <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
              <DialogTrigger asChild>
                <Button size="sm">
                  <Plus size={16} className="mr-2" />
                  Novo
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-6xl max-h-[95vh] overflow-y-auto">
                <DialogHeader>
                  <DialogTitle>Novo Transportador</DialogTitle>
                </DialogHeader>
                <div className="pb-4">
                  <TransporterForm 
                    onSuccess={() => {
                      setIsCreateDialogOpen(false);
                    }} 
                  />
                </div>
              </DialogContent>
            </Dialog>
          </div>
        </div>

        {/* Campo de busca inteligente */}
        <Card className="mb-6">
          <CardHeader>
            <CardTitle className="text-lg flex items-center gap-2">
              <Search className="h-5 w-5" />
              Buscar Transportadores
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
              <Input
                placeholder="Digite CNPJ/CPF, nome, razÃ£o social, email ou cidade..."
                value={searchFilter}
                onChange={(e) => setSearchFilter(e.target.value)}
                className="pl-10 pr-10"
              />
              {searchFilter && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setSearchFilter("")}
                  className="absolute right-1 top-1/2 transform -translate-y-1/2 h-8 w-8 p-0"
                >
                  <X className="h-4 w-4" />
                </Button>
              )}
            </div>
            {searchFilter && (
              <div className="mt-2 text-sm text-gray-600">
                {filteredTransporters.length === 0 ? (
                  <span>Nenhum resultado encontrado para "{searchFilter}"</span>
                ) : (
                  <span>
                    {filteredTransporters.length} transportador(es) encontrado(s) de {transporters.length} total
                  </span>
                )}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Lista de transportadores */}
        {renderTransportersList()}

        {/* Controles de paginaÃ§Ã£o - VersÃ£o desktop */}
        {filteredTransporters.length > 0 && !isLoading && (
          <div className="hidden md:block mt-6">
            <ListPagination 
              currentPage={currentPage}
              totalPages={pagination.totalPages}
              totalItems={filteredTransporters.length}
              itemsPerPage={10}
              hasPrev={currentPage > 1}
              hasNext={currentPage < pagination.totalPages}
              startItem={(currentPage - 1) * 10 + 1}
              endItem={Math.min(currentPage * 10, filteredTransporters.length)}
              onPageChange={setCurrentPage}
            />
          </div>
        )}

        {/* Controles de paginaÃ§Ã£o - VersÃ£o mobile */}
        {filteredTransporters.length > 0 && !isLoading && (
          <div className="block md:hidden mt-6">
            <MobileListPagination
              currentPage={currentPage}
              totalPages={pagination.totalPages}
              totalItems={filteredTransporters.length}
              itemsPerPage={10}
              hasPrev={currentPage > 1}
              hasNext={currentPage < pagination.totalPages}
              startItem={(currentPage - 1) * 10 + 1}
              endItem={Math.min(currentPage * 10, filteredTransporters.length)}
              onPageChange={setCurrentPage}
            />
          </div>
        )}

        {/* Modal de ediÃ§Ã£o de transportador */}
        {selectedTransporter && (
          <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
            <DialogContent className="max-w-6xl max-h-[95vh] overflow-y-auto">
              <DialogHeader>
                <DialogTitle>Editar Transportador</DialogTitle>
              </DialogHeader>
              <div className="pb-4">
                <TransporterForm 
                  transporter={selectedTransporter} 
                  onSuccess={() => {
                    setIsEditDialogOpen(false);
                    setSelectedTransporter(null);
                  }} 
                />
              </div>
            </DialogContent>
          </Dialog>
        )}

        {/* Modal de vinculaÃ§Ã£o de usuÃ¡rio */}
        {selectedTransporter && (
          <Dialog open={isLinkUserDialogOpen} onOpenChange={setIsLinkUserDialogOpen}>
            <DialogContent className="max-w-lg">
              <DialogHeader>
                <DialogTitle>Vincular UsuÃ¡rio ao Transportador</DialogTitle>
              </DialogHeader>
              <div className="py-4">
                <TransporterLinkUser 
                  transporter={selectedTransporter}
                  onSuccess={() => {
                    setIsLinkUserDialogOpen(false);
                    setSelectedTransporter(null);
                  }}
                />
              </div>
            </DialogContent>
          </Dialog>
        )}

        {/* Modal de confirmaÃ§Ã£o de exclusÃ£o */}
        <AlertDialog open={isDeleteDialogOpen} onOpenChange={setIsDeleteDialogOpen}>
          <AlertDialogContent>
            <AlertDialogHeader>
              <AlertDialogTitle>Confirmar ExclusÃ£o</AlertDialogTitle>
              <AlertDialogDescription>
                Tem certeza que deseja excluir este transportador? Esta aÃ§Ã£o nÃ£o pode ser desfeita e todos os dados relacionados serÃ£o removidos permanentemente.
              </AlertDialogDescription>
            </AlertDialogHeader>
            <AlertDialogFooter>
              <AlertDialogCancel>Cancelar</AlertDialogCancel>
              <AlertDialogAction
                onClick={handleConfirmDelete}
                className="bg-red-600 hover:bg-red-700"
                disabled={deleteTransporterMutation.isPending}
              >
                {deleteTransporterMutation.isPending ? "Excluindo..." : "Excluir"}
              </AlertDialogAction>
            </AlertDialogFooter>
          </AlertDialogContent>
        </AlertDialog>
      </div>
    </AdminLayout>
  );
}

