import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { AdminLayout } from "@/components/layout/admin-layout";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle } from "@/components/ui/alert-dialog";
import { Card, CardContent } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { useToast } from "@/hooks/use-toast";
import { exportToCSV, formatDateForCSV } from "@/lib/csv-export";
import { LoadingSpinner } from "@/components/ui/loading-spinner";
import { Plus, MoreVertical, Edit, Trash, User as UserIcon, Download, Search, X } from "lucide-react";
import { useIsMobile } from "@/hooks/use-mobile";
import { SkeletonTable } from "@/components/ui/skeleton-table";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Input } from "@/components/ui/input";
import { FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useForm } from "react-hook-form";
import { Form } from "@/components/ui/form";
import { z } from "zod";
import { Badge } from "@/components/ui/badge";
import { getRoleColor, getRoleLabel } from "@/lib/role-utils";
import { usePaginatedList } from "@/hooks/use-paginated-list";
import { ListPagination, MobileListPagination } from "@/components/ui/list-pagination";

// Schema para validaÃ§Ã£o do formulÃ¡rio base
const baseUserFormSchema = {
  fullName: z.string().min(3, "Nome completo deve ter pelo menos 3 caracteres"),
  email: z.string().email("Informe um email vÃ¡lido"),
  phone: z.string().min(8, "Telefone deve ter pelo menos 8 caracteres"),
  role: z.string()
};

// Schema para criaÃ§Ã£o (com senha obrigatÃ³ria)
const createUserFormSchema = z.object({
  ...baseUserFormSchema,
  password: z.string().min(6, "Senha deve ter pelo menos 6 caracteres"),
});

// Schema para ediÃ§Ã£o (com senha opcional)
const updateUserFormSchema = z.object({
  ...baseUserFormSchema,
  password: z.string().min(6, "Senha deve ter pelo menos 6 caracteres").optional().or(z.literal('')),
});

type CreateUserFormValues = z.infer<typeof createUserFormSchema>;
type UpdateUserFormValues = z.infer<typeof updateUserFormSchema>;
type UserFormValues = CreateUserFormValues | UpdateUserFormValues;

interface User {
  id: number;
  email: string;
  fullName: string;
  phone: string;
  role: string;
  isAdmin: boolean;
  createdAt: string;
}

export default function AdminUsers() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const isMobile = useIsMobile();
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [isDeleteDialogOpen, setIsDeleteDialogOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [userToDelete, setUserToDelete] = useState<number | null>(null);
  const [searchTerm, setSearchTerm] = useState("");

  // Fetch users
  const { data: users = [], isLoading } = useQuery({
    queryKey: ["/api/admin/users"],
    queryFn: async () => {
      const response = await apiRequest("GET", "/api/admin/users");
      return await response.json();
    }
  });

  // Filtrar usuÃ¡rios com base no termo de busca
  const filteredUsers = users.filter((user: User) => {
    if (!searchTerm.trim()) return true;

    const searchLower = searchTerm.toLowerCase().trim();
    const nameMatch = user.fullName?.toLowerCase().includes(searchLower);
    const emailMatch = user.email?.toLowerCase().includes(searchLower);
    const phoneMatch = user.phone?.toLowerCase().includes(searchLower);
    const roleMatch = getRoleLabel(user.role)?.toLowerCase().includes(searchLower);

    return nameMatch || emailMatch || phoneMatch || roleMatch;
  });

  // Hook de paginaÃ§Ã£o dos usuÃ¡rios filtrados
  const { 
    paginatedItems: paginatedUsers, 
    pagination, 
    currentPage, 
    setCurrentPage 
  } = usePaginatedList({ items: filteredUsers });

  // Delete user mutation
  const deleteUserMutation = useMutation({
    mutationFn: async (userId: number) => {
      await apiRequest("DELETE", `/api/admin/users/${userId}`);
    },
    onSuccess: () => {
      toast({
        title: "UsuÃ¡rio excluÃ­do",
        description: "O usuÃ¡rio foi excluÃ­do com sucesso",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Erro ao excluir usuÃ¡rio",
        description: error.message,
        variant: "destructive",
      });
    }
  });

  // User form for create/edit
  const UserForm = ({ user, onSuccess }: { user?: User, onSuccess?: () => void }) => {
    const form = useForm<UserFormValues>({
      resolver: zodResolver(user ? updateUserFormSchema : createUserFormSchema),
      defaultValues: user ? {
        fullName: user.fullName,
        email: user.email,
        password: "",
        phone: user.phone,
        role: user.role
      } : {
        fullName: "",
        email: "",
        password: "",
        phone: "",
        role: "user"
      }
    });

    // Create user mutation
    const createUserMutation = useMutation({
      mutationFn: async (data: UserFormValues) => {
        const response = await apiRequest("POST", "/api/admin/users", data);
        return await response.json();
      },
      onSuccess: () => {
        toast({
          title: "UsuÃ¡rio criado",
          description: "O usuÃ¡rio foi criado com sucesso",
        });
        queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
        onSuccess?.();
      },
      onError: (error: Error) => {
        toast({
          title: "Erro ao criar usuÃ¡rio",
          description: error.message,
          variant: "destructive",
        });
      }
    });

    // Update user mutation
    const updateUserMutation = useMutation({
      mutationFn: async (data: UserFormValues) => {
        if (!user) throw new Error("UsuÃ¡rio nÃ£o encontrado");
        const response = await apiRequest("PATCH", `/api/admin/users/${user.id}`, data);
        return await response.json();
      },
      onSuccess: () => {
        toast({
          title: "UsuÃ¡rio atualizado",
          description: "O usuÃ¡rio foi atualizado com sucesso",
        });
        queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
        onSuccess?.();
      },
      onError: (error: Error) => {
        toast({
          title: "Erro ao atualizar usuÃ¡rio",
          description: error.message,
          variant: "destructive",
        });
      }
    });

    const onSubmit = (data: UserFormValues) => {
      if (user) {
        // Se estiver editando e a senha estiver vazia, remova o campo password
        if (data.password === '') {
          const { password, ...restData } = data;
          updateUserMutation.mutate(restData as UpdateUserFormValues);
        } else {
          updateUserMutation.mutate(data);
        }
      } else {
        createUserMutation.mutate(data as CreateUserFormValues);
      }
    };

    const isPending = createUserMutation.isPending || updateUserMutation.isPending;

    return (
      <Form {...form}>
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <FormField
              control={form.control}
              name="fullName"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Nome Completo</FormLabel>
                  <FormControl>
                    <Input placeholder="Nome completo" {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="email"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Email</FormLabel>
                  <FormControl>
                    <Input type="email" placeholder="email@exemplo.com" {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <FormField
              control={form.control}
              name="password"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>{user ? "Nova senha (deixe em branco para manter)" : "Senha"}</FormLabel>
                  <FormControl>
                    <Input type="password" placeholder="********" {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="phone"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Telefone</FormLabel>
                  <FormControl>
                    <Input placeholder="(00) 00000-0000" {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>

          <FormField
            control={form.control}
            name="role"
            render={({ field }) => (
              <FormItem>
                <FormLabel>FunÃ§Ã£o</FormLabel>
                <FormControl>
                  <select 
                    className="w-full p-2 border rounded" 
                    {...field}
                  >
                    <option value="user">Transportador</option>
                    <option value="operational">Operacional</option>
                    <option value="supervisor">Supervisor</option>
                    <option value="financial">Financeiro</option>
                    <option value="manager">Gerente</option>
                  </select>
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <div className="flex justify-end mt-6">
            <Button type="submit" disabled={isPending}>
              {isPending ? (
                <>
                  <LoadingSpinner size="sm" />
                  <span className="ml-2">Processando...</span>
                </>
              ) : user ? (
                "Atualizar UsuÃ¡rio"
              ) : (
                "Cadastrar UsuÃ¡rio"
              )}
            </Button>
          </div>
        </form>
      </Form>
    );
  };

  const handleEditUser = (user: User) => {
    setSelectedUser(user);
    setIsEditDialogOpen(true);
  };

  const handleDeleteUser = (userId: number) => {
    setUserToDelete(userId);
    setIsDeleteDialogOpen(true);
  };

  const handleConfirmDelete = () => {
    if (userToDelete) {
      deleteUserMutation.mutate(userToDelete);
      setIsDeleteDialogOpen(false);
      setUserToDelete(null);
    }
  };

  const handleExportCSV = () => {
    if (!users || users.length === 0) {
      toast({
        title: "Nenhum dado para exportar",
        description: "NÃ£o hÃ¡ usuÃ¡rios para exportar",
        variant: "destructive",
      });
      return;
    }

    try {
      const headers = [
        "ID",
        "Nome Completo",
        "Email",
        "Telefone",
        "FunÃ§Ã£o",
        "Data de CriaÃ§Ã£o"
      ];

      const filteredUsers = users.filter((user: User) => !user.isAdmin);
      const formattedData = filteredUsers.map((user: User) => ({
        ID: user.id,
        "Nome Completo": user.fullName,
        Email: user.email,
        Telefone: user.phone,
        FunÃ§Ã£o: getRoleLabel(user.role),
        "Data de CriaÃ§Ã£o": formatDateForCSV(user.createdAt)
      }));

      exportToCSV({
        filename: "usuarios",
        headers,
        data: formattedData
      });

      toast({
        title: "ExportaÃ§Ã£o concluÃ­da",
        description: `${filteredUsers.length} usuÃ¡rios exportados com sucesso`,
      });
    } catch (error) {
      toast({
        title: "Erro na exportaÃ§Ã£o",
        description: "Ocorreu um erro ao exportar os dados",
        variant: "destructive",
      });
    }
  };

  const renderUsersList = () => {
    if (isLoading) {
      return <SkeletonTable columns={5} rows={5} />;
    }

    if (users.length === 0) {
      return (
        <Alert className="my-4">
          <AlertDescription>
            Nenhum usuÃ¡rio cadastrado. Clique no botÃ£o "Novo" para adicionar.
          </AlertDescription>
        </Alert>
      );
    }

    if (isMobile) {
      return (
        <div className="space-y-4">
          {(paginatedUsers as User[]).filter((user: User) => !user.isAdmin).map((user: User) => (
            <Card key={user.id} className="overflow-hidden">
              <CardContent className="p-0">
                <div className="p-4 bg-gray-50 flex justify-between items-center">
                  <div>
                    <h3 className="font-medium">{user.fullName}</h3>
                    <p className="text-xs text-gray-500">ID: {user.id}</p>
                  </div>
                  <DropdownMenu>
                    <DropdownMenuTrigger asChild>
                      <Button variant="ghost" size="icon">
                        <MoreVertical size={16} />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent align="end">
                      <DropdownMenuItem onClick={() => handleEditUser(user)}>
                        <Edit size={16} className="mr-2" />
                        Editar
                      </DropdownMenuItem>
                      <DropdownMenuItem onClick={() => handleDeleteUser(user.id)} className="text-red-600">
                        <Trash size={16} className="mr-2" />
                        Excluir
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                </div>
                <div className="p-4 space-y-2">
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-500">Email:</span>
                    <span className="text-sm">{user.email}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-500">Telefone:</span>
                    <span className="text-sm">{user.phone}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-500">FunÃ§Ã£o:</span>
                    <Badge variant="outline" className={`bg-${getRoleColor(user.role)}-50 text-${getRoleColor(user.role)}-700 border-${getRoleColor(user.role)}-200`}>
                      {getRoleLabel(user.role)}
                    </Badge>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      );
    }

    return (
      <Table>
        <TableHeader>
          <TableRow>
            <TableHead>ID</TableHead>
            <TableHead>Nome</TableHead>
            <TableHead>Email</TableHead>
            <TableHead>Telefone</TableHead>
            <TableHead>FunÃ§Ã£o</TableHead>
            <TableHead className="w-[100px]">AÃ§Ãµes</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {(paginatedUsers as User[]).filter((user: User) => !user.isAdmin).map((user: User) => (
            <TableRow key={user.id}>
              <TableCell>{user.id}</TableCell>
              <TableCell className="font-medium">{user.fullName}</TableCell>
              <TableCell>{user.email}</TableCell>
              <TableCell>{user.phone}</TableCell>
              <TableCell>
                <Badge variant="outline" className={`bg-${getRoleColor(user.role)}-50 text-${getRoleColor(user.role)}-700 border-${getRoleColor(user.role)}-200`}>
                  {getRoleLabel(user.role)}
                </Badge>
              </TableCell>
              <TableCell>
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" size="icon">
                      <MoreVertical size={16} />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end">
                    <DropdownMenuItem onClick={() => handleEditUser(user)}>
                      <Edit size={16} className="mr-2" />
                      Editar
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={() => handleDeleteUser(user.id)} className="text-red-600">
                      <Trash size={16} className="mr-2" />
                      Excluir
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    );
  };

  return (
    <AdminLayout>
      <div className="container mx-auto py-6 max-w-6xl">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold">Gerenciar UsuÃ¡rios</h1>
          <div className="flex gap-2">
            <Button
              variant="outline"
              size="sm"
              onClick={handleExportCSV}
              disabled={isLoading}
              title="Exportar dados dos usuÃ¡rios"
            >
              <Download className="h-4 w-4 mr-2" />
              Exportar
            </Button>
            <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
              <DialogTrigger asChild>
                <Button size="sm">
                  <Plus size={16} className="mr-2" />
                  Novo
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-3xl">
                <DialogHeader>
                  <DialogTitle>Novo UsuÃ¡rio</DialogTitle>
                </DialogHeader>
                <UserForm 
                  onSuccess={() => {
                    setIsCreateDialogOpen(false);
                  }} 
                />
              </DialogContent>
            </Dialog>
          </div>
        </div>

        {/* Campo de busca inteligente */}
        <div className="mb-6">
          <div className="relative max-w-md">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <Input
              type="text"
              placeholder="Digite nome, email, telefone ou funÃ§Ã£o"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10"
            />
            {searchTerm && (
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setSearchTerm("")}
                className="absolute right-1 top-1/2 transform -translate-y-1/2 h-7 w-7 p-0 hover:bg-gray-100"
              >
                <X className="h-3 w-3" />
              </Button>
            )}
          </div>
        </div>

        {/* Lista de usuÃ¡rios */}
        {renderUsersList()}

        {/* Controles de paginaÃ§Ã£o - VersÃ£o desktop */}
        {filteredUsers.length > 0 && !isLoading && (
          <div className="hidden md:block mt-6">
            <ListPagination 
              currentPage={currentPage}
              totalPages={pagination.totalPages}
              totalItems={filteredUsers.length}
              itemsPerPage={10}
              hasPrev={currentPage > 1}
              hasNext={currentPage < pagination.totalPages}
              startItem={(currentPage - 1) * 10 + 1}
              endItem={Math.min(currentPage * 10, filteredUsers.length)}
              onPageChange={setCurrentPage}
            />
          </div>
        )}

        {/* Controles de paginaÃ§Ã£o - VersÃ£o mobile */}
        {filteredUsers.length > 0 && !isLoading && (
          <div className="block md:hidden mt-6">
            <MobileListPagination
              currentPage={currentPage}
              totalPages={pagination.totalPages}
              totalItems={filteredUsers.length}
              itemsPerPage={10}
              hasPrev={currentPage > 1}
              hasNext={currentPage < pagination.totalPages}
              startItem={(currentPage - 1) * 10 + 1}
              endItem={Math.min(currentPage * 10, filteredUsers.length)}
              onPageChange={setCurrentPage}
            />
          </div>
        )}

        {/* Modal de ediÃ§Ã£o de usuÃ¡rio */}
        {selectedUser && (
          <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
            <DialogContent className="max-w-3xl">
              <DialogHeader>
                <DialogTitle>Editar UsuÃ¡rio</DialogTitle>
              </DialogHeader>
              <UserForm 
                user={selectedUser} 
                onSuccess={() => {
                  setIsEditDialogOpen(false);
                  setSelectedUser(null);
                }} 
              />
            </DialogContent>
          </Dialog>
        )}

        {/* Modal de confirmaÃ§Ã£o de exclusÃ£o */}
        <AlertDialog open={isDeleteDialogOpen} onOpenChange={setIsDeleteDialogOpen}>
          <AlertDialogContent>
            <AlertDialogHeader>
              <AlertDialogTitle>Confirmar ExclusÃ£o</AlertDialogTitle>
              <AlertDialogDescription>
                Tem certeza que deseja excluir este usuÃ¡rio? Esta aÃ§Ã£o nÃ£o pode ser desfeita e todos os dados relacionados serÃ£o removidos permanentemente.
              </AlertDialogDescription>
            </AlertDialogHeader>
            <AlertDialogFooter>
              <AlertDialogCancel>Cancelar</AlertDialogCancel>
              <AlertDialogAction
                onClick={handleConfirmDelete}
                className="bg-red-600 hover:bg-red-700"
                disabled={deleteUserMutation.isPending}
              >
                {deleteUserMutation.isPending ? "Excluindo..." : "Excluir"}
              </AlertDialogAction>
            </AlertDialogFooter>
          </AlertDialogContent>
        </AlertDialog>
      </div>
    </AdminLayout>
  );
}

