import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Trash2, Plus, Edit, Download, FileText, Receipt, Filter } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { formatCurrency, formatDate } from "@/lib/utils";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from "@/components/ui/alert-dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useCallback, useState as useReactState } from "react";
import { Upload, X, File, Eye } from "lucide-react";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { AdminLayout } from "@/components/layout/admin-layout";
import { exportToCSV, formatDateForCSV, formatCurrencyForCSV } from "@/lib/csv-export";
import { usePaginatedList } from "@/hooks/use-paginated-list";
import { ListPagination, MobileListPagination } from "@/components/ui/list-pagination";

// Schema de validaÃ§Ã£o para o formulÃ¡rio de boleto
const boletoFormSchema = z.object({
  transportadorId: z.number().min(1, "Selecione um transportador"),
  nomeTransportador: z.string().min(1, "Nome do transportador Ã© obrigatÃ³rio"),
  cpfCnpj: z.string().min(11, "CPF/CNPJ Ã© obrigatÃ³rio"),
  numeroBoleto: z.string().min(1, "NÃºmero do boleto Ã© obrigatÃ³rio"),
  valor: z.string().min(1, "Valor Ã© obrigatÃ³rio"),
  dataEmissao: z.string().min(1, "Data de emissÃ£o Ã© obrigatÃ³ria"),
  dataVencimento: z.string().min(1, "Data de vencimento Ã© obrigatÃ³ria"),
  status: z.string().min(1, "Status Ã© obrigatÃ³rio"),
  observacoes: z.string().optional(),
  uploadBoletoUrl: z.string().optional(),
  uploadNfUrl: z.string().optional(),
});

type BoletoFormData = z.infer<typeof boletoFormSchema>;

interface Boleto {
  id: number;
  transportadorId: number;
  nomeTransportador: string;
  cpfCnpj: string;
  numeroBoleto: string;
  valor: string;
  dataEmissao: string;
  dataVencimento: string;
  status: string;
  uploadBoletoUrl?: string;
  uploadNfUrl?: string;
  observacoes?: string;
  criadoEm: string;
  atualizadoEm: string;
}

export default function BoletosPage() {
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [editingBoleto, setEditingBoleto] = useState<Boleto | null>(null);
  const [filtroStatus, setFiltroStatus] = useState<string>("todos");
  const [filtroVencimento, setFiltroVencimento] = useState<string>("todos");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: boletos = [], isLoading } = useQuery({
    queryKey: ["/api/boletos", filtroStatus, filtroVencimento],
