      transportadorId: 0,
      nomeTransportador: "",
      cpfCnpj: "",
      numeroBoleto: "",
      valor: "",
      dataEmissao: new Date().toISOString().split('T')[0],
      dataVencimento: "",
      status: "pendente",
      observacoes: "",
      uploadBoletoUrl: "",
      uploadNfUrl: "",
    },
  });

  const createMutation = useMutation({
    mutationFn: (data: BoletoFormData) => apiRequest("POST", "/api/boletos", data),
    onSuccess: () => {
