import { formatCurrency, formatDate, getLicenseTypeLabel } from "@/lib/utils";

// FunÃ§Ã£o para traduzir status para portuguÃªs brasileiro
const translateStatus = (status: string): string => {
  const translations: Record<string, string> = {
    'pending_registration': 'Pendente',
    'registration_in_progress': 'Em AnÃ¡lise',
    'pending_documentation': 'Pendente DocumentaÃ§Ã£o',
    'under_review': 'Em AnÃ¡lise',
    'pending_approval': 'Aguardando AprovaÃ§Ã£o',
    'approved': 'Aprovada',
    'rejected': 'Rejeitada',
    'canceled': 'Cancelada',
    'expired': 'Expirada'
  };

  return translations[status] || status;
};
import { 
  FileText, 
  CheckCircle, 
  Clock, 
  XCircle, 
  Truck, 
  Receipt, 
  DollarSign,
  TrendingUp,
  Calendar,
  MapPin
} from "lucide-react";
