                <DialogContent className="sm:max-w-[425px] p-0 max-h-[90vh] h-auto overflow-y-auto mobile-form-dialog">
                  <div className="p-4 border-b">
                    <h2 className="font-semibold text-lg">InformaÃ§Ãµes do VeÃ­culo</h2>
                    <p className="text-sm text-muted-foreground">Visualize ou edite as informaÃ§Ãµes do veÃ­culo</p>
                  </div>
                  <VehicleForm
                    vehicle={vehicle}
                    onSuccess={() => closeVehicleDialog(vehicle.id)}
                    onCancel={() => closeVehicleDialog(vehicle.id)}
                  />
                </DialogContent>
              </Dialog>
            ))
          )}
        </div>
      </div>
    </MobileLayout>
  );
}

