import { useState, useRef, useEffect } from "react";
import { Check, ChevronDown, Search, Truck, X } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { 
  Popover, 
  PopoverContent, 
  PopoverTrigger 
} from "@/components/ui/popover";
import { 
  Command, 
  CommandEmpty, 
  CommandGroup, 
  CommandItem, 
  CommandList 
} from "@/components/ui/command";
import { Badge } from "@/components/ui/badge";
import { cn } from "@/lib/utils";
import { useOptimizedVehicleSelector, VehicleOption } from "@/hooks/use-optimized-vehicle-selector";
import { usePaginatedSelector } from "@/hooks/use-paginated-selector";
import { PaginationControls } from "@/components/ui/pagination-controls";

interface OptimizedVehicleSelectorProps {
  value?: number | null;
  onValueChange: (value: number | null) => void;
  placeholder?: string;
  vehicleType: 'tractor_unit' | 'semi_trailer';
  disabled?: boolean;
  error?: string;
  required?: boolean;
  label?: string;
  description?: string;
}

export function OptimizedVehicleSelector({
  value,
  onValueChange,
  placeholder = "Digite a placa ou selecione um veÃ­culo",
  vehicleType,
  disabled = false,
  error,
  required = false,
  label,
  description
}: OptimizedVehicleSelectorProps) {
  const [open, setOpen] = useState(false);
  const [inputValue, setInputValue] = useState('');
  const inputRef = useRef<HTMLInputElement>(null);

  const {
    searchTerm,
    setSearchTerm,
    vehicles,
    isLoading,
    error: searchError,
    hasResults,
    formatVehicleLabel,
    getVehicleById
  } = useOptimizedVehicleSelector({
    vehicleType,
    searchEnabled: true,
    limit: 50,
    autoFocus: false
  });

  // PaginaÃ§Ã£o dos veÃ­culos
  const {
    currentItems: paginatedVehicles,
    currentPage,
    totalPages,
    totalItems,
    hasNextPage,
    hasPreviousPage,
    goToNextPage,
    goToPreviousPage,
    resetPagination
  } = usePaginatedSelector({
    items: vehicles,
    itemsPerPage: 10
  });

  // Sincronizar busca com input e resetar paginaÃ§Ã£o
  useEffect(() => {
    setSearchTerm(inputValue);
    resetPagination();
  }, [inputValue, setSearchTerm, resetPagination]);

  // Obter veÃ­culo selecionado
  const selectedVehicle = value ? getVehicleById(value) : null;

  // Atualizar input quando veÃ­culo Ã© selecionado
  useEffect(() => {
    if (selectedVehicle && !open) {
      setInputValue(selectedVehicle.plate);
    }
  }, [selectedVehicle, open]);

  const handleSelect = (vehicleId: number) => {
    const vehicle = getVehicleById(vehicleId);
    if (vehicle) {
      onValueChange(vehicleId);
      setInputValue(vehicle.plate);
      setOpen(false);
    }
  };

  const handleClear = () => {
    onValueChange(null);
    setInputValue('');
    setSearchTerm('');
    if (inputRef.current) {
      inputRef.current.focus();
    }
  };

  const handleInputChange = (newValue: string) => {
    setInputValue(newValue);
    if (!open) {
      setOpen(true);
    }
  };

  const getTypeLabel = (type: string): string => {
    switch (type) {
      case 'tractor_unit':
        return 'Unidade Tratora';
      case 'semi_trailer':
        return 'Semirreboque';
      default:
        return type;
    }
  };

  const showError = error || searchError?.message;

  return (
    <div className="space-y-2">
      {label && (
        <div className="flex items-center gap-2">
          <label className="text-sm font-medium text-gray-700">
            {label}
            {required && <span className="text-red-500 ml-1">*</span>}
          </label>
          <Badge variant="outline" className="text-xs">
            {getTypeLabel(vehicleType)}
          </Badge>
        </div>
      )}

      {description && (
        <p className="text-sm text-gray-500">{description}</p>
      )}

      <Popover open={open} onOpenChange={setOpen}>
        <div className="relative">
          <Input
            ref={inputRef}
            value={inputValue}
            onChange={(e) => handleInputChange(e.target.value)}
            placeholder={placeholder}
            disabled={disabled}
            className={cn(
              "pr-20",
