        if (cnpj) {
          currentStateCnpj = cnpj;
        }
      }
    }

    // Fallback para o CNPJ global se n√£o houver CNPJ espec√≠fico para o estado
    if (!currentStateCnpj && license.selectedCnpj) {
      currentStateCnpj = license.selectedCnpj;
