import { useState, useMemo } from "react";
import { MainLayout } from "@/components/layout/main-layout";
import { Input } from "@/components/ui/input";
import { RefreshCw, Loader2 } from "lucide-react";
import { useWebSocketContext } from "@/hooks/use-websocket-context";
import { useToast } from "@/hooks/use-toast";
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue
} from "@/components/ui/select";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { LicenseRequest, LicenseStatus } from "@shared/schema";
import { format, isAfter, isBefore, addDays, differenceInDays } from "date-fns";
import { Button } from "@/components/ui/button";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { 
  Pagination, 
  PaginationContent, 
  PaginationItem, 
  PaginationLink, 
  PaginationNext, 
  PaginationPrevious 
} from "@/components/ui/pagination";
import { 
  Dialog, 
  DialogContent, 
  DialogTitle, 
  DialogHeader, 
  DialogDescription,
  DialogFooter,
  DialogClose
} from "@/components/ui/dialog";
import { FileDown, ExternalLink, AlertCircle, CheckCircle2, Clock, RefreshCcw, Download } from "lucide-react";
import { formatDateForCSV, exportToCSV } from "@/lib/csv-export";
import { Status, StatusBadge } from "@/components/licenses/status-badge";
import { TransporterInfo } from "@/components/transporters/transporter-info";
import { Badge } from "@/components/ui/badge";
import { SortableHeader } from "@/components/ui/sortable-header";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useLocation } from "wouter";
import { usePaginatedList } from "@/hooks/use-paginated-list";
import { ListPagination, MobileListPagination } from "@/components/ui/list-pagination";

// Extens√£o do tipo LicenseRequest para incluir dados do transportador
interface LicenseRequestWithTransporter extends LicenseRequest {
  transporter?: {
    id: number;
    name: string;
    tradeName: string;
    documentNumber: string;
  } | null;
}

// Tipo para as licen√ßas expandidas (uma linha por estado)
interface ExpandedLicenseRequest {
  id: number;
  licenseId: number;
  requestNumber: string;
  type: string;
  mainVehiclePlate: string;
  state: string;
  status: string;
  stateStatus: string;
  emissionDate: string | null;
  validUntil: string | null;
  licenseFileUrl: string | null;
  stateFileUrl: string | null;
  transporterId: number;
  transporter?: {
    id: number;
    name: string;
    tradeName: string;
    documentNumber: string;
  } | null;
  aetNumber: string | null;
}

export default function IssuedLicensesPage() {
  const [searchTerm, setSearchTerm] = useState("");
  const [dateFrom, setDateFrom] = useState("");
  const [dateTo, setDateTo] = useState("");
  const [stateFilter, setStateFilter] = useState("");
  const [statusFilter, setStatusFilter] = useState("");

  const [selectedLicense, setSelectedLicense] = useState<LicenseRequestWithTransporter | null>(null);
  const [sortColumn, setSortColumn] = useState<string | null>("emissionDate");
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc' | null>('desc');
  const [renewDialogOpen, setRenewDialogOpen] = useState(false);
  const [licenseToRenew, setLicenseToRenew] = useState<{licenseId: number, state: string} | null>(null);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const queryClient = useQueryClient();
  const { isConnected } = useWebSocketContext();
  const { toast } = useToast();

  const { data: issuedLicenses, isLoading, refetch } = useQuery<LicenseRequestWithTransporter[]>({
    queryKey: ["/api/licenses/issued"],
    queryFn: async () => {
      const res = await fetch("/api/licenses/issued", {
        credentials: "include"
      });
      if (!res.ok) {
        throw new Error("Erro ao buscar licen√ßas emitidas");
      }
      return res.json();
    },
    // Desabilita o cache para garantir que sempre temos os dados mais recentes
    staleTime: 0,
    // Recarrega os dados quando a p√°gina recebe foco
    refetchOnWindowFocus: true
  });

  // Interface para as licen√ßas expandidas por estado

  // Obter licen√ßas com status aprovado por estado
  const expandedLicenses = useMemo(() => {
    if (!issuedLicenses) return [];

    const result: ExpandedLicenseRequest[] = [];

    issuedLicenses.forEach(license => {
      // Para cada licen√ßa, expandir para uma linha por estado que tenha sido aprovado
      license.states.forEach((state, index) => {
        // Verifica se este estado espec√≠fico foi aprovado
        const stateStatusEntry = license.stateStatuses?.find(entry => entry.startsWith(`${state}:`));
        const stateStatus = stateStatusEntry?.split(':')?.[1] || 'pending_registration';
        const stateFileEntry = license.stateFiles?.find(entry => entry.startsWith(`${state}:`));
        const stateFileUrl = stateFileEntry?.split(':')?.[1] || null;

        // S√≥ incluir estados com status "approved"
        if (stateStatus === 'approved') {
          // Obter data de validade e emiss√£o espec√≠ficas para este estado, se dispon√≠vel
          let stateValidUntil = null;
          let stateEmissionDate = null;

          // Novo formato: "estado:status:data_validade" ou "estado:status:data_validade:data_emissao"
          if (stateStatusEntry && stateStatusEntry.split(':').length > 2) {
            const statusParts = stateStatusEntry.split(':');
            // Extrair data de validade do formato estado:status:data
            stateValidUntil = statusParts[2];
            console.log(`Data de validade extra√≠da para ${state}: ${stateValidUntil}`);

            // Se tiver 4 partes, pode incluir data de emiss√£o
            if (statusParts.length > 3) {
              // A √∫ltima parte poderia ser data de emiss√£o se estiver no formato de data
              const lastPart = statusParts[statusParts.length - 1];
              if (lastPart.match(/^\d{4}-\d{2}-\d{2}$/)) {
                stateEmissionDate = lastPart;
                console.log(`Data de emiss√£o extra√≠da para ${state}: ${stateEmissionDate}`);
                console.log(`Status completo para ${state}: ${stateStatusEntry}`);
              }
            }
          }

          // Se n√£o encontrou data de emiss√£o espec√≠fica, usar a data global da licen√ßa
          if (!stateEmissionDate && license.issuedAt) {
            const issuedDate = typeof license.issuedAt === 'string' ? license.issuedAt : license.issuedAt.toISOString();
            stateEmissionDate = issuedDate.split('T')[0];
            console.log(`[DEBUG] Usando data global para ${state}: ${stateEmissionDate}`);
          }

          // Obter n√∫mero AET espec√≠fico para este estado, se dispon√≠vel
          let stateAETNumber = null;

          // Verificar primeiro no array stateAETNumbers (formato "SP:123456")
          if (license.stateAETNumbers && Array.isArray(license.stateAETNumbers)) {
            const aetEntry = license.stateAETNumbers.find(entry => entry.startsWith(`${state}:`));
            if (aetEntry) {
              const parts = aetEntry.split(':');
              if (parts.length >= 2) {
                stateAETNumber = parts[1];
              }
            }
          }

          // Se n√£o encontrou no stateAETNumbers, tentar no campo aetNumber (legado)
          if (!stateAETNumber && license.aetNumber) {
            stateAETNumber = license.aetNumber;
          }

          // Se n√£o encontrou data de validade espec√≠fica, usar a data global da licen√ßa
          if (!stateValidUntil && license.validUntil) {
            const validDate = typeof license.validUntil === 'string' ? license.validUntil : license.validUntil.toISOString();
            stateValidUntil = validDate.split('T')[0];
            console.log(`[DEBUG] Usando data validade global para ${state}: ${stateValidUntil}`);
          }



          result.push({
            id: license.id * 100 + index, // Gerar ID √∫nico para a linha
            licenseId: license.id,
            requestNumber: license.requestNumber,
            type: license.type,
            mainVehiclePlate: license.mainVehiclePlate,
            state,
            status: stateStatus,
            stateStatus,
            emissionDate: stateEmissionDate,
            validUntil: stateValidUntil,
            licenseFileUrl: license.licenseFileUrl,
            stateFileUrl,
            transporterId: license.transporterId || 0,
            transporter: license.transporter, // Passar dados do transportador para a linha expandida
            aetNumber: stateAETNumber // Usar o n√∫mero AET espec√≠fico do estado
          });
        }
      });
    });

    return result;
  }, [issuedLicenses]);



  // Verificar validade das licen√ßas
  const getLicenseStatus = (validUntil: string | null): 'active' | 'expired' | 'expiring_soon' => {
    if (!validUntil) return 'active';

    const validDate = new Date(validUntil);
    const today = new Date();

    if (isBefore(validDate, today)) {
      return 'expired';
    }

    // Se a validade √© menos de 30 dias a partir de hoje
    if (differenceInDays(validDate, today) <= 30) {
      return 'expiring_soon';
    }

    return 'active';
  };

  // Filtrar as licen√ßas expandidas
  const filteredLicenses = expandedLicenses.filter(license => {
    const matchesSearch = !searchTerm || 
      license.requestNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
      license.mainVehiclePlate.toLowerCase().includes(searchTerm.toLowerCase());

    const licenseDate = license.emissionDate ? new Date(license.emissionDate) : null;

    const matchesDateFrom = !dateFrom || (
      licenseDate && 
      licenseDate >= new Date(dateFrom)
    );

    const matchesDateTo = !dateTo || (
      licenseDate && 
      licenseDate <= new Date(dateTo)
    );

    const matchesState = !stateFilter || stateFilter === "all_states" || (
      license.state === stateFilter
    );

    // Verificar o status da licen√ßa para filtro de situa√ß√£o
    const validityStatus = getLicenseStatus(license.validUntil);
    const matchesStatus = !statusFilter || statusFilter === "all_status" || statusFilter === validityStatus;

    return matchesSearch && matchesDateFrom && matchesDateTo && matchesState && matchesStatus;
  });

  // Ordenar licen√ßas filtradas
  const sortedLicenses = useMemo(() => {
    if (!sortColumn || !sortDirection) {
      return filteredLicenses;
    }

    // Criar uma c√≥pia para ordena√ß√£o
    const toSort = [...filteredLicenses];

    // Definir uma fun√ß√£o de ordena√ß√£o personalizada com base na coluna e dire√ß√£o
    const getSortValue = (license: ExpandedLicenseRequest, column: string): any => {
      if (column === 'state') {
        return license.state;
      } else if (column === 'mainVehiclePlate') {
        return license.mainVehiclePlate;
      } else if (column === 'type') {
        return license.type;
      } else if (column === 'requestNumber') {
        return license.requestNumber;
      } else if (column === 'validUntil') {
        return license.validUntil ? new Date(license.validUntil).getTime() : 0;
      } else if (column === 'emissionDate') {
        return license.emissionDate ? new Date(license.emissionDate).getTime() : 0;
      } else if (column === 'status') {
        return getLicenseStatus(license.validUntil);
      } else {
        return license[column as keyof typeof license];
      }
    };

    // Ordenar o array
    toSort.sort((a, b) => {
      const aValue = getSortValue(a, sortColumn);
      const bValue = getSortValue(b, sortColumn);

      // Valores iguais
      if (aValue === bValue) return 0;

      // Tratamento para nulos
      if (aValue === null || aValue === undefined) {
        return sortDirection === 'asc' ? 1 : -1;
      }
      if (bValue === null || bValue === undefined) {
        return sortDirection === 'asc' ? -1 : 1;
      }

      // Para strings
      if (typeof aValue === 'string' && typeof bValue === 'string') {
        return sortDirection === 'asc' 
          ? aValue.localeCompare(bValue) 
          : bValue.localeCompare(aValue);
      }

      // Para n√∫meros e outros tipos
      return sortDirection === 'asc' 
        ? (aValue < bValue ? -1 : 1) 
        : (bValue < aValue ? -1 : 1);
    });

    return toSort;
  }, [filteredLicenses, sortColumn, sortDirection]);

  // Fun√ß√£o para ordenar as licen√ßas
  const handleSort = (column: string) => {
    if (sortColumn === column) {
      // Se j√° est√° ordenando por esta coluna, alterna a dire√ß√£o
      if (sortDirection === 'asc') {
        setSortDirection('desc');
      } else if (sortDirection === 'desc') {
        setSortDirection('asc');
      } else {
        setSortDirection('asc');
      }
    } else {
      // Nova coluna selecionada, come√ßa com ascendente
      setSortColumn(column);
      setSortDirection('asc');
    }
  };

  // Hook de pagina√ß√£o das licen√ßas filtradas
  const { 
    paginatedItems: paginatedLicenses, 
    pagination, 
    currentPage, 
    setCurrentPage 
  } = usePaginatedList({ items: sortedLicenses });


  const viewLicenseDetails = (license: LicenseRequest) => {
    setSelectedLicense(license);
  };

  // Navega√ß√£o para redirecionar ap√≥s renova√ß√£o
  const [, setLocation] = useLocation();

  // Muta√ß√£o para renovar licen√ßa
  const renewLicenseMutation = useMutation({
    mutationFn: async ({ licenseId, state }: { licenseId: number, state: string }) => {
      try {
        // Usar o endpoint que aceita o corpo da requisi√ß√£o
        const response = await apiRequest("POST", "/api/licenses/renew", { licenseId, state });

        if (!response.ok) {
          const errorText = await response.text();
          throw new Error(`Erro ao renovar licen√ßa: ${errorText}`);
        }
        return await response.json();
      } catch (error) {
        console.error("Erro na renova√ß√£o:", error);
        throw error;
      }
    },
    onSuccess: (data) => {
      console.log("Renova√ß√£o bem-sucedida:", data);

      // Fechar o di√°logo de renova√ß√£o
      setRenewDialogOpen(false);

      // Invalidar a cache para garantir que os dados s√£o atualizados
      queryClient.invalidateQueries({ queryKey: ["/api/licenses/drafts"] });
      queryClient.invalidateQueries({ queryKey: ["/api/licenses/issued"] });

      // Notificar o usu√°rio e redirecionar para a p√°gina de edi√ß√£o do rascunho
      toast({
        title: "Licen√ßa renovada com sucesso",
        description: `Licen√ßa renovada para o estado ${data.draft.states[0]}. Voc√™ ser√° redirecionado para editar o rascunho.`,
        duration: 5000,
      });

      // Navegar para a p√°gina de edi√ß√£o do rascunho ap√≥s um pequeno atraso
      setTimeout(() => {
        setLocation(`/request-license?draft=${data.draft.id}`);
      }, 1000);
    },
    onError: (error: Error) => {
      console.error("Erro na renova√ß√£o:", error);

      toast({
        title: "Erro ao renovar licen√ßa",
        description: error.message || "N√£o foi poss√≠vel completar a renova√ß√£o da licen√ßa. Tente novamente.",
        variant: "destructive",
        duration: 5000,
      });
    }
  });

  // Fun√ß√£o para atualizar os dados com feedback visual melhorado
  const handleRefresh = async () => {
    setIsRefreshing(true);
    try {
      // Invalidar cache primeiro
      await queryClient.invalidateQueries({ queryKey: ["/api/licenses/issued"] });
      await queryClient.invalidateQueries({ queryKey: ["/api/licenses"] });

      // Fazer refetch
      await refetch();

      toast({
        title: "Sucesso",
        description: "Lista de licen√ßas emitidas atualizada com sucesso.",
      });
    } catch (error) {
      console.error("Erro ao atualizar:", error);
      toast({
        title: "Erro",
        description: "Erro ao atualizar a lista. Tente novamente.",
        variant: "destructive",
      });
    } finally {
      setIsRefreshing(false);
    }
  };

  // Fun√ß√£o para formatar data para CSV
  const formatDateToCSV = (dateString: string | null): string => {
    if (!dateString) return '';
    try {
      const date = new Date(dateString);
      if (isNaN(date.getTime())) return '';
      return date.toLocaleDateString('pt-BR');
    } catch {
      return '';
    }
  };

  // Fun√ß√£o para traduzir tipos de ve√≠culo para portugu√™s
  const translateVehicleType = (type: string): string => {
    const vehicleTypeMap: Record<string, string> = {
      'truck': 'Caminh√£o',
