            // Dimens√µes (em cent√≠metros)
            length: length, // J√° convertido para cm
            width: width,   // J√° convertido para cm
            height: height, // J√° convertido para cm
            totalWeight: totalWeight,
            cargoType: 'dry_cargo' as const,

            // Campos obrigat√≥rios
            additionalPlates: [],
