          };
          await storage.createVehicle(transporterUserId, cleanVehicleData);

          console.log('[BULK IMPORT] Ve√≠culo criado com sucesso para transportador:', vehicleData.plate);
          results.inserted++;

          // Enviar notifica√ß√£o WebSocket sobre o novo ve√≠culo
          broadcastMessage({
