      const result = await db.execute(query);
      const queryTime = Date.now() - startTime;

      console.log(`[TRACTOR UNITS] Consulta executada em ${queryTime}ms - ${result.rows.length} resultados`);

      const response = { vehicles: result.rows };

      // Cache por 1 minuto
      appCache.set(cacheKey, response, 1);

      res.json(response);
    } catch (error) {
      console.error('[TRACTOR UNITS] Erro:', error);
      res.status(500).json({ message: 'Erro na busca de unidades tratoras' });
    }
  });

  // Busca de semirreboques otimizada  
  app.get('/api/vehicles/semi-trailers', requireAuth, async (req, res) => {
    try {
      const user = req.user!;
      const { search = '', limit = '50' } = req.query;

      const maxResults = Math.min(100, parseInt(limit as string));

      let query = sql`
        SELECT v.id, v.plate, v.brand, v.model, v.year, v.tare,
               t.name as transporter_name
        FROM vehicles v
        LEFT JOIN transporters t ON v.transporter_id = t.id
        WHERE v.type = 'semi_trailer' AND v.status = 'active'
      `;

      if (!isAdministrativeRole(user.role as UserRole)) {
        query = sql`${query} AND v.user_id = ${user.id}`;
      }

      if (search) {
        const searchPattern = `%${search.toString().toUpperCase()}%`;
        query = sql`${query} AND UPPER(v.plate) LIKE ${searchPattern}`;
      }

      query = sql`${query} ORDER BY v.plate LIMIT ${maxResults}`;

      const result = await db.execute(query);
      res.json({ vehicles: result.rows });
    } catch (error) {
      console.error('[SEMI TRAILERS] Erro:', error);
      res.status(500).json({ message: 'Erro na busca de semirreboques' });
    }
  });

  // Servir arquivos de upload da pasta externa
  app.use('/uploads', express.static(uploadDir));

  // Log da configura√ß√£o final de uploads
  console.log(`[UPLOAD] Servindo arquivos de ${uploadDir} em /uploads`);

  // ==========================================
  // OBJECT STORAGE PARA IMAGENS
  // ==========================================

  // Rota para servir arquivos p√∫blicos
  app.get("/public-objects/:filePath(*)", async (req, res) => {
    const filePath = req.params.filePath;
    const { ObjectStorageService } = await import('./objectStorage');
    const objectStorageService = new ObjectStorageService();
    try {
      const file = await objectStorageService.searchPublicObject(filePath);
      if (!file) {
        return res.status(404).json({ error: "File not found" });
      }
      objectStorageService.downloadObject(file, res);
    } catch (error) {
      console.error("Error searching for public object:", error);
      return res.status(500).json({ error: "Internal server error" });
    }
  });

  // Rota para upload de objetos
  app.post("/api/objects/upload", requireAuth, async (req, res) => {
    try {
      const { ObjectStorageService } = await import('./objectStorage');
      const objectStorageService = new ObjectStorageService();
      const uploadURL = await objectStorageService.getObjectEntityUploadURL();
      res.json({ uploadURL });
    } catch (error) {
      console.error("Error getting upload URL:", error);
      res.status(500).json({ error: "Failed to get upload URL" });
    }
  });

  // Rota para servir objetos privados
  app.get("/objects/:objectPath(*)", async (req, res) => {
    try {
      const { ObjectStorageService } = await import('./objectStorage');
      const objectStorageService = new ObjectStorageService();
      const objectFile = await objectStorageService.getObjectEntityFile(req.path);
      objectStorageService.downloadObject(objectFile, res);
    } catch (error) {
      console.error("Error accessing object:", error);
      res.status(404).json({ error: "Object not found" });
    }
  });

  // ==========================================
  // GEST√ÉO DE TIPOS DE CONJUNTO (ADMIN)
  // ==========================================

  // Listar todos os tipos de conjunto (com cache)
  app.get('/api/admin/vehicle-set-types', requireAuth, async (req, res) => {
    try {
      // Cache simples em mem√≥ria por 5 minutos
      const cacheKey = 'vehicle_set_types_cache';
      const cacheTime = 5 * 60 * 1000; // 5 minutos

      // For√ßar atualiza√ß√£o se o cache existir mas for solicitado refresh
      const forceRefresh = req.query.refresh === 'true';

      if (!forceRefresh && (global as any)[cacheKey] && (global as any)[`${cacheKey}_time`] > Date.now() - cacheTime) {
        console.log('[VEHICLE SET TYPES] Retornando dados do cache');
        return res.json((global as any)[cacheKey]);
      }

      const { DEFAULT_VEHICLE_SET_TYPES } = await import('../shared/vehicle-set-types');
      const { vehicleSetTypes } = await import('../shared/schema');
      const { eq } = await import('drizzle-orm');

      // Buscar tipos personalizados do banco de dados
      const customTypes = await db.select().from(vehicleSetTypes).where(eq(vehicleSetTypes.isActive, true));

      // Combinar tipos padr√£o com tipos personalizados
      const allTypes = [
        ...DEFAULT_VEHICLE_SET_TYPES,
        ...customTypes.map(type => ({
          ...type,
          axleConfiguration: type.axleConfiguration as any,
          dimensionLimits: type.dimensionLimits as any,
          vehicleTypes: type.vehicleTypes as any,
          createdAt: new Date(type.createdAt),
          updatedAt: new Date(type.updatedAt),
        }))
      ];

      // Armazenar no cache
      (global as any)[cacheKey] = allTypes;
      (global as any)[`${cacheKey}_time`] = Date.now();

      console.log(`[VEHICLE SET TYPES] Retornando ${allTypes.length} tipos (${DEFAULT_VEHICLE_SET_TYPES.length} padr√£o + ${customTypes.length} personalizados)`);
      console.log(`[VEHICLE SET TYPES] Tipos personalizados no banco:`, customTypes.map(t => ({name: t.name, label: t.label, isActive: t.isActive})));
      res.json(allTypes);
    } catch (error) {
      console.error('[VEHICLE SET TYPES] Erro ao buscar tipos:', error);
      res.status(500).json({ message: 'Erro ao buscar tipos de conjunto' });
    }
  });

  // Criar novo tipo de conjunto
  app.post('/api/admin/vehicle-set-types', requireAuth, async (req, res) => {
    try {
      console.log('[VEHICLE SET TYPES] Recebendo dados para cria√ß√£o:', req.body);

      // Validar se √© um usu√°rio admin
      const user = req.user as any;
      if (user.role !== 'admin') {
        return res.status(403).json({ message: 'Acesso negado. Apenas administradores podem criar tipos de conjunto.' });
      }

      const { vehicleSetTypes } = await import('../shared/schema');
      const { randomUUID } = await import('crypto');
      const { eq } = await import('drizzle-orm');

      // Gerar ID √∫nico
      const newId = randomUUID();

      // Calcular total de eixos automaticamente
      const totalAxles = (req.body.axleConfiguration.tractorAxles || 0) + 
                        (req.body.axleConfiguration.firstTrailerAxles || 0) + 
                        (req.body.axleConfiguration.secondTrailerAxles || 0);

      const vehicleSetTypeData = {
        id: newId,
        name: req.body.name,
        label: req.body.label,
        description: req.body.description || null,
        axleConfiguration: {
          ...req.body.axleConfiguration,
          totalAxles
        },
        dimensionLimits: req.body.dimensionLimits,
        vehicleTypes: req.body.vehicleTypes,
        iconPath: req.body.iconPath || null,
        imageUrl: req.body.imageUrl || null,
        isActive: req.body.isActive !== false, // Default true
      };

      // Inserir no banco de dados
      const [newType] = await db.insert(vehicleSetTypes)
        .values(vehicleSetTypeData)
        .returning();

      // Limpar cache GLOBAL for√ßadamente
      (global as any)['vehicle_set_types_cache'] = null;
      (global as any)['vehicle_set_types_cache_time'] = null;
      delete (global as any)['vehicle_set_types_cache'];
      delete (global as any)['vehicle_set_types_cache_time'];

      console.log('[VEHICLE SET TYPES] Cache limpo e tipo criado com sucesso:', newType.id);

      res.json({ 
        success: true, 
        message: 'Tipo de conjunto criado com sucesso',
        data: newType 
      });
    } catch (error) {
      console.error('[VEHICLE SET TYPES] Erro ao criar tipo:', error);
      res.status(500).json({ message: 'Erro ao criar tipo de conjunto' });
    }
  });

  // Atualizar tipo de conjunto
  app.put('/api/admin/vehicle-set-types/:id', requireAuth, async (req, res) => {
    try {
      const user = req.user as any;
      if (user.role !== 'admin') {
        return res.status(403).json({ message: 'Acesso negado. Apenas administradores podem atualizar tipos de conjunto.' });
      }

      const { vehicleSetTypes } = await import('../shared/schema');
      const { eq } = await import('drizzle-orm');
      const typeId = req.params.id;

      // Calcular total de eixos automaticamente
      const totalAxles = (req.body.axleConfiguration.tractorAxles || 0) + 
                        (req.body.axleConfiguration.firstTrailerAxles || 0) + 
                        (req.body.axleConfiguration.secondTrailerAxles || 0);

      const updateData = {
        name: req.body.name,
        label: req.body.label,
        description: req.body.description || null,
        axleConfiguration: {
          ...req.body.axleConfiguration,
          totalAxles
        },
        dimensionLimits: req.body.dimensionLimits,
        vehicleTypes: req.body.vehicleTypes,
        iconPath: req.body.iconPath || null,
        imageUrl: req.body.imageUrl || null,
        isActive: req.body.isActive !== false,
        updatedAt: new Date(),
      };

      // Atualizar no banco de dados
      const [updatedType] = await db.update(vehicleSetTypes)
        .set(updateData)
        .where(eq(vehicleSetTypes.id, typeId))
        .returning();

      if (!updatedType) {
        return res.status(404).json({ message: 'Tipo de conjunto n√£o encontrado' });
      }

      // Limpar cache GLOBAL for√ßadamente  
      (global as any)['vehicle_set_types_cache'] = null;
      (global as any)['vehicle_set_types_cache_time'] = null;
      delete (global as any)['vehicle_set_types_cache'];
      delete (global as any)['vehicle_set_types_cache_time'];

      console.log('[VEHICLE SET TYPES] Cache limpo e tipo atualizado com sucesso:', typeId);

      res.json({ 
        success: true, 
        message: 'Tipo de conjunto atualizado com sucesso',
        data: updatedType 
      });
    } catch (error) {
      console.error('[VEHICLE SET TYPES] Erro ao atualizar tipo:', error);
      res.status(500).json({ message: 'Erro ao atualizar tipo de conjunto' });
    }
  });

  // Deletar tipo de conjunto
  app.delete('/api/admin/vehicle-set-types/:id', requireAuth, async (req, res) => {
    try {
      const user = req.user as any;
      if (user.role !== 'admin') {
        return res.status(403).json({ message: 'Acesso negado. Apenas administradores podem deletar tipos de conjunto.' });
      }

      const { vehicleSetTypes } = await import('../shared/schema');
      const { eq } = await import('drizzle-orm');
      const typeId = req.params.id;

      // Verificar se o tipo existe
      const existingType = await db.select().from(vehicleSetTypes).where(eq(vehicleSetTypes.id, typeId)).limit(1);
      if (existingType.length === 0) {
        return res.status(404).json({ message: 'Tipo de conjunto n√£o encontrado' });
      }

      // Deletar do banco de dados
      await db.delete(vehicleSetTypes).where(eq(vehicleSetTypes.id, typeId));

      // Limpar cache
      delete (global as any)['vehicle_set_types_cache'];
      delete (global as any)['vehicle_set_types_cache_time'];

      console.log('[VEHICLE SET TYPES] Tipo deletado com sucesso:', typeId);

      res.status(204).send();
    } catch (error) {
      console.error('[VEHICLE SET TYPES] Erro ao deletar tipo:', error);
      res.status(500).json({ message: 'Erro ao deletar tipo de conjunto' });
    }
  });

  return httpServer;
}

client/src/components/ui/paginated-vehic.content:
import { useState, useRef, useEffect } from "react";
import { Vehicle } from "@shared/schema";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Check, ChevronDown, Search, Plus, Loader2 } from "lucide-react";
import { cn } from "@/lib/utils";
import { useDebounce } from "@/hooks/use-debounce";
import { useQuery } from "@tanstack/react-query";
import { Skeleton } from "@/components/ui/skeleton";

interface PaginatedVehicleSelectorProps {
  value?: number;
  onSelect: (vehicleId: number | undefined, plate?: string) => void;
  placeholder?: string;
  disabled?: boolean;
  allowManualEntry?: boolean;
  className?: string;
  label?: string;
  onCreateNew?: () => void;
  vehicleType?: 'tractor_unit' | 'trailer' | 'semi_trailer' | 'dolly' | 'truck' | 'flatbed';
  axleFilter?: number; // NOVO: Filtrar por n√∫mero espec√≠fico de eixos
}

interface VehicleSearchResponse {
  vehicles: Vehicle[];
  pagination: {
    page: number;
    limit: number;
    total: number;
    totalPages: number;
    hasNext: boolean;
    hasPrev: boolean;
  };
}

const PAGE_SIZE = 10;

export function PaginatedVehicleSelector({
  value,
  onSelect,
  placeholder = "Digite a placa ou selecione...",
  disabled = false,
  allowManualEntry = false,
  className,
  label,
  onCreateNew,
  vehicleType,
  axleFilter,
}: PaginatedVehicleSelectorProps) {
  const [inputValue, setInputValue] = useState("");
  const [isOpen, setIsOpen] = useState(false);
  const [highlightedIndex, setHighlightedIndex] = useState(-1);
  const [currentPage, setCurrentPage] = useState(1);
  const [allVehicles, setAllVehicles] = useState<Vehicle[]>([]);

  const inputRef = useRef<HTMLInputElement>(null);
  const listRef = useRef<HTMLUListElement>(null);
  // Usar debounce hook como o transportador
  const debouncedSearchTerm = useDebounce(inputValue, 300);

  // Query para buscar ve√≠culos com pagina√ß√£o
  const { data: vehicleData, isLoading, error } = useQuery<VehicleSearchResponse>({
    queryKey: ['/api/vehicles/search-paginated', debouncedSearchTerm, currentPage, vehicleType, axleFilter],
    queryFn: async () => {
      const params = new URLSearchParams({
        search: debouncedSearchTerm,
        page: currentPage.toString(),
        limit: PAGE_SIZE.toString(),
        ...(vehicleType && { type: vehicleType }),
        ...(axleFilter && { axles: axleFilter.toString() }) // NOVO: Filtro de eixos
      });

      console.log(`[PAGINATED VEHICLE] Buscando ve√≠culos - tipo: ${vehicleType}, busca: "${debouncedSearchTerm}", p√°gina: ${currentPage}`);
      console.log(`[PAGINATED VEHICLE] URL completa: /api/vehicles/search?${params.toString()}`);

      const res = await fetch(`/api/vehicles/search-paginated?${params}`, {
        credentials: "include"
      });

      if (!res.ok) {
        console.error(`[PAGINATED VEHICLE] Erro na requisi√ß√£o: ${res.status} ${res.statusText}`);
        throw new Error("Erro ao buscar ve√≠culos");
      }

      const data = await res.json();
      console.log(`[PAGINATED VEHICLE] Recebidos ${data.vehicles?.length || 0} ve√≠culos, hasNext: ${data.pagination?.hasNext}`);
      console.log(`[PAGINATED VEHICLE] Total no servidor: ${data.pagination?.total}, p√°gina atual: ${data.pagination?.page}`);

      return data;
    },
    enabled: isOpen,
    staleTime: 5 * 60 * 1000, // 5 minutos (otimizado)
    gcTime: 10 * 60 * 1000, // 10 minutos (otimizado)
  });

  // Buscar ve√≠culo selecionado por ID
  const { data: selectedVehicle } = useQuery<Vehicle>({
    queryKey: ['/api/vehicles', value],
    queryFn: async () => {
      const res = await fetch(`/api/vehicles/${value}`, {
        credentials: "include"
      });

      if (!res.ok) {
        throw new Error("Erro ao buscar ve√≠culo");
      }

      return res.json();
    },
    enabled: !!value && !allVehicles.find(v => v.id === value),
    staleTime: 5 * 60 * 1000,
  });

  // Atualizar lista de ve√≠culos quando nova p√°gina carrega
  useEffect(() => {
    if (!vehicleData?.vehicles) return;

    console.log(`[PAGINATED VEHICLE] P√°gina ${currentPage} recebeu ${vehicleData.vehicles.length} ve√≠culos`);

    if (currentPage === 1) {
      setAllVehicles(vehicleData.vehicles);
    } else {
      setAllVehicles(prev => {
        // Otimiza√ß√£o: Limitar o n√∫mero total de ve√≠culos em mem√≥ria para evitar problemas de performance
        const newVehicles = [...prev, ...vehicleData.vehicles];
        return newVehicles.length > 1000 ? newVehicles.slice(-1000) : newVehicles;
      });
    }
  }, [vehicleData?.vehicles, currentPage]);

  // Sincronizar input apenas quando ve√≠culo √© selecionado (n√£o durante digita√ß√£o)
  useEffect(() => {
    if (selectedVehicle && !isOpen) {
      setInputValue(selectedVehicle.plate);
    }
  }, [selectedVehicle, isOpen]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = e.target.value.toUpperCase();
    setInputValue(newValue);
    if (!isOpen) {
      setIsOpen(true);
    }
    setHighlightedIndex(-1);

    // Limpar sele√ß√£o se o input foi limpo
    if (!newValue && value) {
      onSelect(undefined);
    }

    // Se permitir entrada manual e n√£o h√° ve√≠culo correspondente
    if (allowManualEntry && newValue.length >= 3) {
      const exactMatch = allVehicles.find(v => v.plate === newValue);
      if (!exactMatch) {
        onSelect(undefined, newValue);
      }
    }
  };

  const handleVehicleSelect = (vehicle: Vehicle) => {
    onSelect(vehicle.id);
    setInputValue(vehicle.plate);
    setIsOpen(false);
    setHighlightedIndex(-1);
  };

  const handleLoadMore = () => {
    if (vehicleData?.pagination?.hasNext && !isLoading) {
      setCurrentPage(prev => prev + 1);
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (!isOpen) {
      if (e.key === "ArrowDown" || e.key === "Enter") {
        setIsOpen(true);
        // Abrir dropdown automaticamente
        return;
      }
      return;
    }

    switch (e.key) {
      case "ArrowDown":
        e.preventDefault();
        setHighlightedIndex(prev => 
          prev < allVehicles.length - 1 ? prev + 1 : prev
        );
        break;
      case "ArrowUp":
        e.preventDefault();
        setHighlightedIndex(prev => prev > 0 ? prev - 1 : -1);
        break;
      case "Enter":
        e.preventDefault();
        if (highlightedIndex >= 0 && highlightedIndex < allVehicles.length) {
          handleVehicleSelect(allVehicles[highlightedIndex]);
        } else if (allVehicles.length === 1) {
          handleVehicleSelect(allVehicles[0]);
        }
        break;
      case "Escape":
        setIsOpen(false);
        setHighlightedIndex(-1);
        break;
    }
  };

  const handleBlur = (e: React.FocusEvent) => {
    // Se o foco est√° indo para um elemento dentro do dropdown, n√£o fechar
    const relatedTarget = e.relatedTarget as HTMLElement;
    if (relatedTarget && relatedTarget.closest('.paginated-dropdown-content')) {
      return;
    }

    setTimeout(() => {
      setIsOpen(false);
      setHighlightedIndex(-1);
    }, 300);
  };

  // Fun√ß√£o handleFocus n√£o √© mais necess√°ria

  // Scroll para item destacado
  useEffect(() => {
    if (highlightedIndex >= 0 && listRef.current) {
      const highlightedElement = listRef.current.children[highlightedIndex] as HTMLElement;
      if (highlightedElement) {
        highlightedElement.scrollIntoView({
          block: 'nearest',
        });
      }
    }
  }, [highlightedIndex]);

  return (
    <div className={cn("relative", className)}>
      {label && (
        <label className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70 mb-2 block">
          {label}
        </label>
      )}

      <div className="relative">
        <Input
          ref={inputRef}
          value={inputValue}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          onBlur={handleBlur}
          onFocus={() => setIsOpen(true)}
          placeholder={placeholder}
          disabled={disabled}
          className={cn(
            "pr-10",
            error && "border-red-500"
          )}
        />

        <div className="absolute inset-y-0 right-0 flex items-center pr-3 pointer-events-none">
          {isLoading ? (
            <Loader2 className="h-4 w-4 animate-spin text-gray-400" />
          ) : (
            <ChevronDown className="h-4 w-4 text-gray-400" />
          )}
        </div>
      </div>

      {isOpen && (
        <div 
          className="absolute z-[9999] w-full mt-1 bg-white border border-gray-200 rounded-md shadow-lg max-h-60 overflow-hidden"
          onMouseDown={(e) => e.preventDefault()} // Previne que o clique feche o dropdown
          style={{ zIndex: 9999 }}
        >
          {isLoading && currentPage === 1 ? (
            <div className="p-2 space-y-2">
              {Array.from({ length: 3 }).map((_, i) => (
                <Skeleton key={i} className="h-10 w-full" />
              ))}
            </div>
          ) : error ? (
            <div className="p-4 text-center text-red-600">
              <p>Erro ao carregar ve√≠culos</p>
              <Button 
                variant="ghost" 
                size="sm" 
                onClick={() => window.location.reload()}
                className="mt-2"
              >
                Tentar novamente
              </Button>
            </div>
          ) : !vehicleData && allVehicles.length === 0 ? (
            <div className="p-4 text-center text-gray-500">
              <Search className="h-8 w-8 mx-auto mb-2 opacity-50" />
              <p>Nenhuma placa encontrada</p>
              <div className="text-xs mt-2 text-gray-400">
                Debug: allVehicles={allVehicles.length}, busca="{debouncedSearchTerm}"
              </div>
              {onCreateNew && (
                <Button 
                  variant="ghost" 
                  size="sm" 
                  onClick={onCreateNew}
                  className="mt-2"
                >
                  <Plus className="h-4 w-4 mr-2" />
                  Cadastrar novo ve√≠culo
                </Button>
              )}
            </div>
          ) : (
            <div className="flex flex-col max-h-60">
              <ul ref={listRef} className="overflow-y-auto flex-1">
                {allVehicles.map((vehicle, index) => (
                  <li
                    key={vehicle.id}
                    onClick={() => handleVehicleSelect(vehicle)}
                    className={cn(
                      "px-4 py-2 cursor-pointer hover:bg-gray-50 border-b border-gray-100 last:border-b-0",
                      highlightedIndex === index && "bg-blue-50",
                      value === vehicle.id && "bg-blue-100 font-medium"
                    )}
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex-1">
                        <div className="font-medium text-gray-900">
                          {vehicle.plate}
                        </div>
                        {(vehicle.brand || vehicle.model) && (
                          <div className="text-sm text-gray-500">
                            {[vehicle.brand, vehicle.model].filter(Boolean).join(' ')}
                          </div>
                        )}
                      </div>
                      {value === vehicle.id && (
                        <Check className="h-4 w-4 text-blue-600" />
                      )}
                    </div>
                  </li>
                ))}
              </ul>

              {vehicleData?.pagination?.hasNext && (
                <div className="p-2 border-t bg-gray-50">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={handleLoadMore}
                    disabled={isLoading}
                    className="w-full text-xs"
                  >
                    {isLoading ? (
                      <>
                        <Loader2 className="h-3 w-3 mr-2 animate-spin" />
                        Carregando...
                      </>
                    ) : (
                      <>
                        Carregar mais ({vehicleData.pagination.total - allVehicles.length} restantes)
                      </>
                    )}
                  </Button>
                </div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

