import { LicenseType, Vehicle } from "@shared/schema";
import { VehicleSetType } from "@shared/vehicle-set-types";

export interface AxleConfiguration {
  tractorAxles: number;
  firstTrailerAxles: number;
  secondTrailerAxles: number;
  dollyAxles?: number;
  totalAxles: number;
  requiresDolly: boolean;
  isFlexible?: boolean;
}

// Configura√ß√µes de eixos por tipo de licen√ßa (compatibilidade com tipos padr√£o)
export const AXLE_CONFIGURATIONS: Record<LicenseType, AxleConfiguration> = {
  "bitrain_9_axles": {
    tractorAxles: 3,
    firstTrailerAxles: 3,
    secondTrailerAxles: 3,
    totalAxles: 9,
    requiresDolly: false
  },
  "roadtrain_9_axles": {
    tractorAxles: 3,
    firstTrailerAxles: 2,
    secondTrailerAxles: 2,
    dollyAxles: 2,
    totalAxles: 9,
    requiresDolly: true
  },
  "bitrain_7_axles": {
    tractorAxles: 3,
    firstTrailerAxles: 2,
    secondTrailerAxles: 2,
    totalAxles: 7,
    requiresDolly: false
  },
  "bitrain_6_axles": {
    tractorAxles: 2,
    firstTrailerAxles: 2,
    secondTrailerAxles: 2,
    totalAxles: 6,
    requiresDolly: false
  },
  "flatbed": {
    tractorAxles: 0, // Flex√≠vel - qualquer cavalo
    firstTrailerAxles: 0, // Flex√≠vel - qualquer prancha
    secondTrailerAxles: 0,
    totalAxles: 0, // Sem restri√ß√£o espec√≠fica
    requiresDolly: false
  },
  "romeo_and_juliet": {
    tractorAxles: 0, // Flex√≠vel - qualquer cavalo
    firstTrailerAxles: 0, // Flex√≠vel - qualquer semirreboque
    secondTrailerAxles: 0,
    totalAxles: 0, // Sem restri√ß√£o espec√≠fica
    requiresDolly: false
  }
};

export interface VehicleValidationResult {
  isValid: boolean;
  error?: string;
  warning?: string;
}

// Fun√ß√£o para buscar configura√ß√£o de eixos (din√¢mica ou est√°tica)
export function getAxleConfiguration(licenseType: string, vehicleSetTypes?: VehicleSetType[]): AxleConfiguration | null {
  // Primeiro, tentar buscar nos tipos din√¢micos
  if (vehicleSetTypes) {
    const dynamicType = vehicleSetTypes.find(type => type.name === licenseType);
    if (dynamicType) {
      return {
        tractorAxles: dynamicType.axleConfiguration.tractorAxles,
        firstTrailerAxles: dynamicType.axleConfiguration.firstTrailerAxles,
        secondTrailerAxles: dynamicType.axleConfiguration.secondTrailerAxles,
        dollyAxles: dynamicType.axleConfiguration.dollyAxles,
        totalAxles: dynamicType.axleConfiguration.totalAxles,
        requiresDolly: dynamicType.axleConfiguration.requiresDolly,
        isFlexible: dynamicType.axleConfiguration.isFlexible,
      };
    }
  }

  // Fallback para configura√ß√µes est√°ticas (tipos padr√£o)
  return AXLE_CONFIGURATIONS[licenseType as LicenseType] || null;
}

// Validar se um ve√≠culo √© compat√≠vel com uma posi√ß√£o espec√≠fica na composi√ß√£o
export function validateVehicleForPosition(
  vehicle: Vehicle,
  position: 'tractor' | 'firstTrailer' | 'secondTrailer' | 'dolly',
  licenseType: LicenseType | string,
  vehicleSetTypes?: VehicleSetType[]
): VehicleValidationResult {
  const config = getAxleConfiguration(licenseType, vehicleSetTypes);

  if (!config) {
    return {
      isValid: false,
      error: "Configura√ß√£o de tipo de licen√ßa n√£o encontrada"
    };
  }

  if (!vehicle.axleCount) {
    return {
      isValid: false,
      error: "Ve√≠culo n√£o possui informa√ß√£o de quantidade de eixos cadastrada"
    };
  }

  let expectedAxles: number;
  let expectedType: string;

  switch (position) {
    case 'tractor':
      expectedAxles = config.tractorAxles;
      expectedType = 'tractor_unit';
      break;
    case 'firstTrailer':
      expectedAxles = config.firstTrailerAxles;
      expectedType = 'semi_trailer';
      break;
    case 'secondTrailer':
      expectedAxles = config.secondTrailerAxles;
      expectedType = 'semi_trailer';
      break;
    case 'dolly':
      expectedAxles = config.dollyAxles || 2;
      expectedType = 'dolly';
      break;
    default:
      return { isValid: false, error: "Posi√ß√£o inv√°lida" };
  }

  // Verificar tipo de ve√≠culo
  if (vehicle.type !== expectedType) {
    return {
      isValid: false,
      error: `Este ve√≠culo √© do tipo "${getVehicleTypeLabel(vehicle.type)}", mas para esta posi√ß√£o √© necess√°rio "${getVehicleTypeLabel(expectedType)}"`
    };
  }

  // REGRAS ESPEC√çFICAS CR√çTICAS POR TIPO DE LICEN√áA

  // TIPOS FLEX√çVEIS: SEM restri√ß√µes de eixos 
  if (config?.isFlexible || licenseType === 'flatbed' || licenseType === 'romeo_and_juliet') {
    // Para tipos flex√≠veis, apenas verificar o tipo de ve√≠culo, n√£o os eixos
    return { isValid: true };
  }

  // BITREM 7 EIXOS: Apenas semirreboques de 2 eixos
  if (licenseType === 'bitrain_7_axles' && (position === 'firstTrailer' || position === 'secondTrailer')) {
    if (vehicle.axleCount !== 2) {
      return {
        isValid: false,
        error: `‚ö†Ô∏è BITREM 7 EIXOS: Este semirreboque possui ${vehicle.axleCount} eixos. Para Bitrem 7 eixos s√£o aceitos APENAS semirreboques de 2 eixos.`
      };
    }
  }

  // BITREM 6 EIXOS: Apenas semirreboques de 2 eixos
  if (licenseType === 'bitrain_6_axles' && (position === 'firstTrailer' || position === 'secondTrailer')) {
    if (vehicle.axleCount !== 2) {
      return {
        isValid: false,
        error: `‚ö†Ô∏è BITREM 6 EIXOS: Este semirreboque possui ${vehicle.axleCount} eixos. Para Bitrem 6 eixos s√£o aceitos APENAS semirreboques de 2 eixos.`
      };
    }
  }

  // BITREM 9 EIXOS: Apenas semirreboques de 3 eixos
  if (licenseType === 'bitrain_9_axles' && (position === 'firstTrailer' || position === 'secondTrailer')) {
    if (vehicle.axleCount !== 3) {
      return {
        isValid: false,
        error: `‚ö†Ô∏è BITREM 9 EIXOS: Este semirreboque possui ${vehicle.axleCount} eixos. Para Bitrem 9 eixos s√£o aceitos APENAS semirreboques de 3 eixos.`
      };
    }
  }

  // RODOTREM 9 EIXOS: Apenas semirreboques de 2 eixos
  if (licenseType === 'roadtrain_9_axles' && (position === 'firstTrailer' || position === 'secondTrailer')) {
    if (vehicle.axleCount !== 2) {
      return {
        isValid: false,
        error: `‚ö†Ô∏è RODOTREM 9 EIXOS: Este semirreboque possui ${vehicle.axleCount} eixos. Para Rodotrem 9 eixos s√£o aceitos APENAS semirreboques de 2 eixos.`
      };
    }
  }

  // Verificar quantidade de eixos (regra geral) - pular se for 0 (flex√≠vel)
  if (expectedAxles > 0 && vehicle.axleCount !== expectedAxles) {
    return {
      isValid: false,
      error: `Este ve√≠culo possui ${vehicle.axleCount} eixos, mas para ${getLicenseTypeLabel(licenseType)} √© necess√°rio ${expectedAxles} eixos nesta posi√ß√£o`
    };
  }

  return { isValid: true };
}

// Validar a composi√ß√£o completa
export function validateCompleteComposition(
  licenseType: LicenseType | string,
  tractor?: Vehicle,
  firstTrailer?: Vehicle,
  secondTrailer?: Vehicle,
  dolly?: Vehicle,
  vehicleSetTypes?: VehicleSetType[]
): VehicleValidationResult {
  const config = getAxleConfiguration(licenseType, vehicleSetTypes);

  if (!config) {
    return {
      isValid: false,
      error: "Configura√ß√£o de tipo de licen√ßa n√£o encontrada"
    };
  }

  // Verificar se o dolly √© obrigat√≥rio
  if (config.requiresDolly && !dolly) {
    return {
      isValid: false,
      error: `Para ${getLicenseTypeLabel(licenseType)} √© obrigat√≥rio selecionar um dolly`
    };
  }

  // Verificar se o dolly n√£o deve ser usado
  if (!config.requiresDolly && dolly) {
    return {
      isValid: false,
      error: `Para ${getLicenseTypeLabel(licenseType)} n√£o √© necess√°rio dolly`
    };
  }

  // Calcular total de eixos da composi√ß√£o
  let totalAxles = 0;

  if (tractor) totalAxles += tractor.axleCount || 0;
  if (firstTrailer) totalAxles += firstTrailer.axleCount || 0;
  if (secondTrailer) totalAxles += secondTrailer.axleCount || 0;
  if (dolly) totalAxles += dolly.axleCount || 0;

  if (totalAxles !== config.totalAxles) {
    return {
      isValid: false,
      error: `A composi√ß√£o atual possui ${totalAxles} eixos, mas ${getLicenseTypeLabel(licenseType)} requer exatamente ${config.totalAxles} eixos`
    };
  }

  return { isValid: true };
}

// Filtrar ve√≠culos compat√≠veis para uma posi√ß√£o espec√≠fica
export function filterVehiclesForPosition(
  vehicles: Vehicle[],
  position: 'tractor' | 'firstTrailer' | 'secondTrailer' | 'dolly',
  licenseType: LicenseType | string,
  vehicleSetTypes?: VehicleSetType[]
): Vehicle[] {
  const config = getAxleConfiguration(licenseType, vehicleSetTypes);

  if (!config) {
    return [];
  }

  let expectedAxles: number;
  let expectedType: string;

  switch (position) {
    case 'tractor':
      expectedAxles = config.tractorAxles;
      expectedType = 'tractor_unit';
      break;
    case 'firstTrailer':
      expectedAxles = config.firstTrailerAxles;
      expectedType = 'semi_trailer';
      break;
    case 'secondTrailer':
      expectedAxles = config.secondTrailerAxles;
      expectedType = 'semi_trailer';
      break;
    case 'dolly':
      expectedAxles = config.dollyAxles || 2;
      expectedType = 'dolly';
      break;
    default:
      return [];
  }

  return vehicles.filter(vehicle => 
    vehicle.type === expectedType && 
    vehicle.axleCount === expectedAxles &&
    vehicle.status === 'active'
  );
}

// Labels para exibi√ß√£o
function getVehicleTypeLabel(type: string): string {
  const labels: Record<string, string> = {
    'tractor_unit': 'Unidade Tratora',
    'semi_trailer': 'Semirreboque',
    'trailer': 'Reboque',
    'dolly': 'Dolly',
    'truck': 'Caminh√£o',
    'flatbed': 'Prancha'
  };
  return labels[type] || type;
}

export function getLicenseTypeLabel(type: LicenseType | string): string {
  const labels: Record<string, string> = {
    'bitrain_9_axles': 'Bitrem 9 eixos',
    'roadtrain_9_axles': 'Rodotrem 9 eixos',
    'bitrain_7_axles': 'Bitrem 7 eixos',
    'bitrain_6_axles': 'Bitrem 6 eixos',
    'flatbed': 'Prancha',
    'romeo_and_juliet': 'Romeu e Julieta'
  };
  return labels[type] || type;
}

// Obter resumo das especifica√ß√µes para um tipo de licen√ßa
export function getAxleSpecificationSummary(licenseType: LicenseType | string, vehicleSetTypes?: VehicleSetType[]): string {
  const config = getAxleConfiguration(licenseType, vehicleSetTypes);

  if (!config) {
    return `Tipo de licen√ßa n√£o encontrado: ${licenseType}`;
  }

  let summary = `${getLicenseTypeLabel(licenseType)}:\n`;
  summary += `‚Ä¢ Cavalo: ${config.tractorAxles} eixos\n`;
  summary += `‚Ä¢ 1¬™ Carreta: ${config.firstTrailerAxles} eixos\n`;

  if (config.secondTrailerAxles > 0) {
    summary += `‚Ä¢ 2¬™ Carreta: ${config.secondTrailerAxles} eixos\n`;
  }

  if (config.requiresDolly) {
    summary += `‚Ä¢ Dolly: ${config.dollyAxles} eixo(s)\n`;
  }

  summary += `‚Ä¢ Total: ${config.totalAxles} eixos`;

  return summary;
}

