import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient } from "@/lib/queryClient";
import { AdminLayout } from "@/components/layout/admin-layout";
import { Button } from "@/components/ui/button";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Plus, Edit, Trash2, Settings, Truck } from "lucide-react";
import { VehicleSetType } from "@shared/vehicle-set-types";
import { VehicleSetTypeForm } from "@/components/admin";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from "@/components/ui/alert-dialog";

export default function VehicleSetTypesPage() {
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [editingType, setEditingType] = useState<VehicleSetType | null>(null);

  // Buscar tipos de conjunto (com debug)
  const { data: vehicleSetTypes = [], isLoading, error } = useQuery<VehicleSetType[]>({
    queryKey: ['/api/admin/vehicle-set-types'],
    staleTime: 0, // Sempre buscar dados frescos
    refetchOnWindowFocus: true,
    refetchOnMount: true,
    retry: 3,
    onError: (error) => {
      // Performance: Log removido
    },
    onSuccess: (data) => {
      // Performance: Logs removidos
    }
  });

  // Muta√ß√£o para deletar tipo
  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      const res = await fetch(`/api/admin/vehicle-set-types/${id}`, {
        method: 'DELETE',
        credentials: 'include',
      });
      if (!res.ok) throw new Error('Erro ao deletar tipo de conjunto');
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/vehicle-set-types'] });
    },
  });

  const handleEdit = (type: VehicleSetType) => {
    setEditingType(type);
    setIsFormOpen(true);
  };

  const handleDelete = async (id: string) => {
    await deleteMutation.mutateAsync(id);
  };

  const handleCloseForm = () => {
    setIsFormOpen(false);
    setEditingType(null);
  };

  if (isLoading) {
    return (
      <AdminLayout>
        <div className="space-y-6">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-bold">Tipos de Conjunto de Ve√≠culos</h1>
              <p className="text-gray-600">Carregando tipos dispon√≠veis...</p>
            </div>
            <div className="animate-pulse bg-gray-200 h-10 w-32 rounded"></div>
          </div>
          <div className="bg-white rounded-lg border shadow-sm p-6">
            <div className="space-y-4">
              {[1,2,3].map(i => (
                <div key={i} className="animate-pulse">
                  <div className="flex items-center gap-3">
                    <div className="w-12 h-8 bg-gray-200 rounded"></div>
                    <div className="flex-1">
                      <div className="h-4 bg-gray-200 rounded w-1/4 mb-2"></div>
                      <div className="h-3 bg-gray-200 rounded w-1/2"></div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout>
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold">Tipos de Conjunto de Ve√≠culos</h1>
            <p className="text-gray-600">
              Gerencie os tipos de conjunto dispon√≠veis e suas regras de valida√ß√£o
            </p>
          </div>
          <Button onClick={() => setIsFormOpen(true)} className="flex items-center gap-2">
            <Plus className="h-4 w-4" />
            Novo Tipo
          </Button>
        </div>

        <div className="bg-white rounded-lg border shadow-sm">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Tipo</TableHead>
                <TableHead>Configura√ß√£o de Eixos</TableHead>
                <TableHead>Dimens√µes</TableHead>
                <TableHead>Status</TableHead>
                <TableHead className="text-right">A√ß√µes</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {vehicleSetTypes.map((type) => (
                <TableRow key={type.id}>
                  <TableCell>
                    <div className="flex items-center gap-3">
                      {type.imageUrl ? (
                        <img 
                          src={type.imageUrl} 
                          alt={type.label}
                          className="w-12 h-8 object-contain rounded border"
                          onError={(e) => {
                            // Se a imagem falhar, mostra o √≠cone padr√£o
                            const target = e.target as HTMLImageElement;
                            target.style.display = 'none';
                            target.nextElementSibling?.classList.remove('hidden');
                          }}
                        />
                      ) : null}
                      <Truck className={`h-8 w-8 text-gray-400 p-1 border rounded ${type.imageUrl ? 'hidden' : ''}`} />
                      <div>
                        <div className="font-medium">{type.label}</div>
                        {type.description && (
                          <div className="text-sm text-gray-500">{type.description}</div>
                        )}
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    {type.axleConfiguration.isFlexible ? (
                      <Badge variant="outline" className="text-green-700 bg-green-50">
                        Flex√≠vel
                      </Badge>
                    ) : (
                      <div className="text-sm space-y-1">
                        <div>Cavalo: {type.axleConfiguration.tractorAxles} eixos</div>
                        <div>1¬™ Carreta: {type.axleConfiguration.firstTrailerAxles} eixos</div>
                        {type.axleConfiguration.secondTrailerAxles > 0 && (
                          <div>2¬™ Carreta: {type.axleConfiguration.secondTrailerAxles} eixos</div>
                        )}
                        <div className="font-medium text-blue-600">
                          Total: {type.axleConfiguration.totalAxles} eixos
                        </div>
                        {type.axleConfiguration.requiresDolly && (
                          <Badge variant="secondary" className="text-xs">
                            Requer Dolly
                          </Badge>
                        )}
                      </div>
                    )}
                  </TableCell>
                  <TableCell>
                    <div className="text-sm space-y-1">
                      {type.dimensionLimits.minLength && (
                        <div>Compr. m√≠n: {type.dimensionLimits.minLength}m</div>
                      )}
                      {type.dimensionLimits.maxLength && (
                        <div>Compr. m√°x: {type.dimensionLimits.maxLength}m</div>
                      )}
                      {type.dimensionLimits.maxWidth && (
                        <div>Larg. m√°x: {type.dimensionLimits.maxWidth}m</div>
                      )}
                      {type.dimensionLimits.maxHeight && (
                        <div>Alt. m√°x: {type.dimensionLimits.maxHeight}m</div>
                      )}
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge variant={type.isActive ? "default" : "secondary"}>
                      {type.isActive ? "Ativo" : "Inativo"}
                    </Badge>
                  </TableCell>
                  <TableCell className="text-right">
                    <div className="flex gap-2 justify-end">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleEdit(type)}
                      >
                        <Edit className="h-3 w-3 mr-1" />
                        Editar
                      </Button>

                      <AlertDialog>
                        <AlertDialogTrigger asChild>
                          <Button variant="outline" size="sm" className="text-red-600 hover:text-red-700">
                            <Trash2 className="h-3 w-3" />
                          </Button>
                        </AlertDialogTrigger>
                        <AlertDialogContent>
                          <AlertDialogHeader>
                            <AlertDialogTitle>Confirmar Exclus√£o</AlertDialogTitle>
                            <AlertDialogDescription>
                              Tem certeza que deseja excluir o tipo "{type.label}"? 
                              Esta a√ß√£o n√£o pode ser desfeita.
                            </AlertDialogDescription>
                          </AlertDialogHeader>
                          <AlertDialogFooter>
                            <AlertDialogCancel>Cancelar</AlertDialogCancel>
                            <AlertDialogAction
                              onClick={() => handleDelete(type.id)}
                              className="bg-red-600 hover:bg-red-700"
                            >
                              Excluir
                            </AlertDialogAction>
                          </AlertDialogFooter>
                        </AlertDialogContent>
                      </AlertDialog>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>

        {/* Formul√°rio Modal */}
        {isFormOpen && (
          <VehicleSetTypeForm
            vehicleSetType={editingType}
            onClose={handleCloseForm}
            onSuccess={() => {
              handleCloseForm();
              queryClient.invalidateQueries({ queryKey: ['/api/admin/vehicle-set-types'] });
            }}
          />
        )}
      </div>
    </AdminLayout>
  );
}

