    },
    isActive: true,
    createdAt: new Date(),
    updatedAt: new Date(),
  },
  {
    id: "romeo_and_juliet",
    name: "romeo_and_juliet",
    label: "Romeu e Julieta",
    description: "Composi√ß√£o flex√≠vel para romeu e julieta",
    axleConfiguration: {
      tractorAxles: 0,
      firstTrailerAxles: 0,
      secondTrailerAxles: 0,
      totalAxles: 0,
      requiresDolly: false,
      isFlexible: true,
    },
    dimensionLimits: {
      minLength: 19.8,
      maxLength: 30.0,
      maxWidth: 2.6,
      maxHeight: 4.4,
    },
