import { Switch, Route } from "wouter";
import { queryClient, getQueryFn } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import NotFound from "@/pages/not-found";
import AuthPage from "@/pages/auth-page";
import RegularDashboardPage from "@/pages/regular-dashboard-page";
import VehiclesPage from "@/pages/vehicles-page";
import RequestLicensePage from "@/pages/request-license-page";
import TrackLicensePage from "@/pages/track-license-page";
import IssuedLicensesPage from "@/pages/issued-licenses-page";
import MyCompaniesPage from "@/pages/my-companies-page";
import AdminDashboardPage from "@/pages/admin/admin-dashboard";
import AdminLicensesPage from "@/pages/admin/admin-licenses";
import AdminTransportersPage from "@/pages/admin/admin-transporters";
import AdminUsersPage from "@/pages/admin/admin-users";
import AdminVehiclesPage from "@/pages/admin/admin-vehicles";
import AdminVehiclesOptimizedPage from "@/pages/admin/admin-vehicles-optimized";
import TestOptimizedSelectorsPage from "@/pages/test-optimized-selectors";
import VehicleModelsPage from "@/pages/admin/vehicle-models-page";
import BoletosPage from "@/pages/admin/boletos-page";
import DashboardAET from "@/pages/admin/dashboard-aet";
import VehicleTransferPage from "@/pages/admin/vehicle-transfer-page";
import VehicleSetTypesPage from "@/pages/admin/vehicle-set-types";
import BulkLicenseImport from "@/pages/admin/BulkLicenseImport";
import MeusBoletos from "@/pages/meus-boletos";
import BulkVehiclesPage from "@/pages/bulk-vehicles-page";
import RedirectPage from "@/pages/redirect-page";
import { ProtectedRoute, AdminRoute, StaffRoute } from "./lib/protected-route";
import { AuthProvider } from "./hooks/use-auth";
import { WebSocketProvider } from "./hooks/use-websocket-context";
import { MobileProvider, useMobileContext } from "./hooks/use-mobile-context";
import { useEffect, lazy } from "react";

// Importar p√°ginas mobile
import MobileDashboardPage from "@/pages/mobile/mobile-dashboard";
import MobileVehiclesPage from "@/pages/mobile/mobile-vehicles";
import MobileTrackLicensePage from "@/pages/mobile/mobile-track-license";
import MobileIssuedLicensesPage from "@/pages/mobile/mobile-issued-licenses";

function Router() {
  const { isMobile } = useMobileContext();

  return (
    <Switch>
      <Route path="/auth" component={AuthPage} />

      {/* P√°gina inicial - Dashboard direto sem redirecionamento */}
      <ProtectedRoute 
        path="/" 
        component={isMobile ? MobileDashboardPage : RegularDashboardPage} 
      />

      {/* Portal Admin - Rotas acess√≠veis via hierarquia de permiss√µes */}
      <AdminRoute path="/admin" component={AdminDashboardPage} />
      <StaffRoute path="/admin/licenses" component={AdminLicensesPage} requiredRole="operational" />
      <StaffRoute path="/admin/transporters" component={AdminTransportersPage} requiredRole="operational" />
      <AdminRoute path="/admin/users" component={AdminUsersPage} />
      <StaffRoute path="/admin/vehicles" component={AdminVehiclesPage} requiredRole="operational" />
      <StaffRoute path="/admin/vehicles-optimized" component={AdminVehiclesOptimizedPage} requiredRole="operational" />
      <AdminRoute path="/test-selectors" component={TestOptimizedSelectorsPage} />
      <AdminRoute path="/admin/vehicle-models" component={VehicleModelsPage} />
      <AdminRoute path="/admin/vehicle-set-types" component={VehicleSetTypesPage} />
      <AdminRoute path="/admin/vehicle-transfer" component={VehicleTransferPage} />
      <StaffRoute path="/admin/boletos" component={BoletosPage} requiredRole="financial" />
      <StaffRoute path="/admin/bulk-license-import" component={BulkLicenseImport} requiredRole="operational" />
      <AdminRoute path="/admin/dashboard-aet" component={DashboardAET} />

      {/* Sistema de Controle de Licen√ßas - Rotas do usu√°rio (vers√£o desktop ou mobile) */}
      <ProtectedRoute 
        path="/dashboard" 
        component={isMobile ? MobileDashboardPage : RegularDashboardPage} 
      />
      <ProtectedRoute
        path="/my-companies"
        component={MyCompaniesPage}
      />
      <ProtectedRoute 
        path="/vehicles" 
        component={isMobile ? MobileVehiclesPage : VehiclesPage} 
      />
      <ProtectedRoute 
        path="/nova-licenca" 
        component={RequestLicensePage} 
      />
      <ProtectedRoute 
        path="/request-license" 
        component={RequestLicensePage} 
      />
      <ProtectedRoute 
        path="/acompanhar-licenca" 
        component={isMobile ? MobileTrackLicensePage : TrackLicensePage} 
      />
      <ProtectedRoute 
        path="/track-license" 
        component={isMobile ? MobileTrackLicensePage : TrackLicensePage} 
      />
      <ProtectedRoute 
        path="/issued-licenses" 
        component={isMobile ? MobileIssuedLicensesPage : IssuedLicensesPage} 
      />
      <ProtectedRoute 
        path="/licencas-emitidas" 
        component={isMobile ? MobileIssuedLicensesPage : IssuedLicensesPage} 
      />

      {/* Acesso do cliente ao m√≥dulo financeiro - apenas para transportadores */}
      <ProtectedRoute 
        path="/meus-boletos" 
        component={MeusBoletos} 
      />

      {/* Cadastro em massa de ve√≠culos - dispon√≠vel para todos usu√°rios */}
      <ProtectedRoute 
        path="/cadastro-massa-veiculos" 
        component={BulkVehiclesPage} 
      />

      <Route component={NotFound} />
    </Switch>
  );
}

// Componente para pr√©-carregar dados importantes
function AppInitializer() {
  const { isMobile } = useMobileContext();

  // Efeito para carregar dados do usu√°rio e outros recursos importantes
  useEffect(() => {
    // Pr√©-carregar dados da sess√£o atual
    queryClient.prefetchQuery({
      queryKey: ["/api/user"],
      queryFn: getQueryFn({ on401: "returnNull" }),
    });

    // Adicionar classe para identificar dispositivos m√≥veis no body
    if (isMobile) {
      document.body.classList.add('mobile-device');
    } else {
      document.body.classList.remove('mobile-device');
    }

    // Configurar viewport para dispositivos m√≥veis
    const viewportMeta = document.querySelector('meta[name="viewport"]');
    if (viewportMeta && isMobile) {
      viewportMeta.setAttribute('content', 'width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=no');
    }
  }, [isMobile]);

  return null;
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <AuthProvider>
        <WebSocketProvider>
          <MobileProvider>
            <AppInitializer />
            <Router />
            <Toaster />
          </MobileProvider>
        </WebSocketProvider>
      </AuthProvider>
    </QueryClientProvider>
  );
}

export default App;

