import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Sheet, SheetContent, SheetTrigger } from "@/components/ui/sheet";
import { useAuth } from "@/hooks/use-auth";
import { usePermissions } from "@/hooks/use-permissions";
import { useIsMobile } from "@/hooks/use-mobile";
import { 
  Menu, 
  Home, 
  Truck, 
  FileText, 
  ClipboardList, 
  ListChecks, 
  LogOut, 
  ChevronRight, 
  ChevronDown,
  Building2, 
  ClipboardEdit,
  LayoutDashboard,
  Users,
  Settings,
  Car,
  RefreshCw,
  Receipt,
  BarChart3,
  UploadCloud,
  PanelLeftClose,
  PanelLeftOpen
} from "lucide-react";
import { cn } from "@/lib/utils";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Logo } from "@/components/ui/logo";
import { Separator } from "@/components/ui/separator";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";

interface SidebarProps {
  className?: string;
  isCollapsed?: boolean;
  onToggleCollapse?: () => void;
}

export function Sidebar({ className, isCollapsed = false, onToggleCollapse }: SidebarProps) {
  const [location, setLocation] = useLocation();
  const { user, logoutMutation } = useAuth();
  const permissions = usePermissions();

  const isMobile = useIsMobile();
  const [open, setOpen] = useState(false);
  const [vehicleMenuExpanded, setVehicleMenuExpanded] = useState(false);

  const userInitials = user?.fullName
    .split(' ')
    .map(name => name[0])
    .join('')
    .substring(0, 2)
    .toUpperCase();

  // Auto-expand vehicle menu when on vehicle-related pages
  useEffect(() => {
    const vehiclePages = ['/vehicles', '/admin/vehicle-models', '/admin/vehicle-transfer', '/cadastro-massa-veiculos'];
    // Otimiza√ß√£o: Usar early return e evitar expans√£o desnecess√°ria
    const shouldExpand = vehiclePages.includes(location);
    if (shouldExpand !== vehicleMenuExpanded) {
      setVehicleMenuExpanded(shouldExpand);
    }
  }, [location, vehicleMenuExpanded]);

  const handleLogout = async () => {
    // Previne m√∫ltiplos cliques durante logout
    if (logoutMutation.isPending) return;

    try {
      // Limpa o cache imediatamente para logout instant√¢neo
      const { queryClient } = await import("@/lib/queryClient");
      queryClient.setQueryData(["/api/user"], null);
      queryClient.clear();

      // Faz logout no servidor em background
      await fetch("/api/logout", { 
        method: "POST",
        credentials: "include"
      });

      // For√ßa redirecionamento para tela inicial
      window.location.href = "/auth";
    } catch (error) {
      console.error("Erro no logout:", error);
      // Mesmo com erro, redireciona para tela inicial
      window.location.href = "/auth";
    }
  };

  const handleNavigate = (path: string) => {
    // Navega√ß√£o otimizada - fecha modal primeiro para feedback visual instant√¢neo
    setOpen(false);

    // Navega√ß√£o apenas se for caminho diferente
    if (location !== path) {
      setLocation(path);
    }
  };

  // Componente SidebarItem com tooltip para modo colapsado
  const SidebarItem = ({ icon: Icon, label, path, isActive, children }: {
    icon: any;
    label: string;
    path?: string;
    isActive?: boolean;
    children?: React.ReactNode;
  }) => {
    const content = (
      <Button
        variant="ghost"
        className={cn(
          "w-full text-white hover:bg-gray-700 transition-colors",
          isCollapsed ? "justify-center px-2" : "justify-start",
          isActive ? "bg-gray-700" : "bg-transparent"
        )}
        onClick={() => path && handleNavigate(path)}
      >
        <Icon className={cn("h-5 w-5", !isCollapsed && "mr-3")} />
        {!isCollapsed && <span className="flex-1 text-left">{label}</span>}
        {!isCollapsed && children}
      </Button>
    );

    if (isCollapsed) {
      return (
        <TooltipProvider>
          <Tooltip>
            <TooltipTrigger asChild>
              {content}
            </TooltipTrigger>
            <TooltipContent side="right" className="ml-2">
              {label}
            </TooltipContent>
          </Tooltip>
        </TooltipProvider>
      );
    }

    return content;
  };

  const NavItems = () => (
    <>
      {/* Header da Sidebar */}
      <div className={cn(
        "flex items-center h-16 px-4 bg-gray-900 border-b border-gray-700",
        isCollapsed ? "justify-center" : "justify-between"
      )}>
        {!isCollapsed && <Logo width={120} className="py-2" />}
        {isCollapsed && <Logo width={32} className="py-2" />}

        {/* Bot√£o de colapsar - apenas quando n√£o est√° colapsado */}
        {!isMobile && !isCollapsed && onToggleCollapse && (
          <Button
            variant="ghost"
            size="sm"
            onClick={onToggleCollapse}
            className="text-white hover:bg-gray-700 p-1"
          >
            <PanelLeftClose className="h-4 w-4" />
          </Button>
        )}
      </div>

      {/* Conte√∫do Principal da Sidebar */}
      <div className={cn(
        "py-4 space-y-1 overflow-y-auto h-full max-h-screen scrollbar-thin scrollbar-thumb-gray-600 scrollbar-track-gray-800",
        isCollapsed ? "px-1" : "px-2"
      )}>
        {/* Dashboard - Apenas para transportadores (usu√°rios comuns) */}
        {user?.role === 'user' && (
          <SidebarItem
            icon={Home}
            label="Dashboard"
            path="/"
            isActive={location === "/" || location === "/dashboard"}
          />
        )}



        {/* Menu Hier√°rquico de Ve√≠culos */}
        {permissions.canViewVehicles() && (
          <div className="space-y-1">
            {/* Menu Principal de Ve√≠culos */}
            {!isCollapsed ? (
              <Button
                variant="ghost"
                className={cn(
                  "w-full justify-start text-white hover:bg-gray-700",
                  (location === "/vehicles" || location === "/admin/vehicle-models" || location === "/admin/vehicle-transfer" || location === "/cadastro-massa-veiculos") ? "bg-gray-700" : "bg-transparent"
                )}
                onClick={() => {
                  // Sempre navegar para ve√≠culos ao clicar no menu principal
                  handleNavigate("/vehicles");
                }}
              >
                <Truck className="mr-3 h-5 w-5" />
                <span className="flex-1 text-left">Ve√≠culos</span>
                {vehicleMenuExpanded ? (
                  <ChevronDown className="h-4 w-4" />
                ) : (
                  <ChevronRight className="h-4 w-4" />
                )}
              </Button>
            ) : (
              // No modo colapsado, mostrar apenas √≠cone dos ve√≠culos com acesso direto √† lista
              <SidebarItem
                icon={Truck}
                label="Ve√≠culos"
                path="/vehicles"
                isActive={location === "/vehicles" || location === "/admin/vehicle-models" || location === "/admin/vehicle-transfer" || location === "/cadastro-massa-veiculos"}
              />
            )}

            {/* Submenus de Ve√≠culos - apenas no modo expandido */}
            {!isCollapsed && vehicleMenuExpanded && (
              <div className="ml-6 space-y-1 border-l border-gray-600 pl-4">
                {/* Ve√≠culos Cadastrados */}
                <Button
                  variant="ghost"
                  className={cn(
                    "w-full justify-start text-white hover:bg-gray-600 text-sm",
                    location === "/vehicles" ? "bg-gray-600" : "bg-transparent"
                  )}
                  onClick={() => handleNavigate("/vehicles")}
                >
                  <Truck className="mr-3 h-4 w-4" />
                  Ve√≠culos Cadastrados
                </Button>

                {/* Cadastro em Massa - Apenas para usu√°rios administrativos */}
                {user?.role !== 'user' && (
                  <Button
                    variant="ghost"
                    className={cn(
                      "w-full justify-start text-white hover:bg-gray-600 text-sm",
                      location === "/cadastro-massa-veiculos" ? "bg-gray-600" : "bg-transparent"
                    )}
                    onClick={() => handleNavigate("/cadastro-massa-veiculos")}
                  >
                    <UploadCloud className="mr-3 h-4 w-4" />
                    Cadastro em Massa
                  </Button>
                )}

                {/* Modelos de Ve√≠culos - Apenas para usu√°rios administrativos (n√£o transportadores) */}
                {permissions.canViewVehicleModels() && user?.role !== 'user' && (
                  <Button
                    variant="ghost"
                    className={cn(
                      "w-full justify-start text-white hover:bg-gray-600 text-sm",
                      location === "/admin/vehicle-models" ? "bg-gray-600" : "bg-transparent"
                    )}
                    onClick={() => handleNavigate("/admin/vehicle-models")}
                  >
                    <Car className="mr-3 h-4 w-4" />
                    Modelos de Ve√≠culos
                  </Button>
                )}

                {/* Transferir Ve√≠culos - Apenas para usu√°rios administrativos (n√£o transportadores) */}
                {permissions.canViewUsers() && user?.role !== 'user' && (
                  <Button
                    variant="ghost"
                    className={cn(
                      "w-full justify-start text-white hover:bg-gray-600 text-sm",
                      location === "/admin/vehicle-transfer" ? "bg-gray-600" : "bg-transparent"
                    )}
                    onClick={() => handleNavigate("/admin/vehicle-transfer")}
                  >
                    <RefreshCw className="mr-3 h-4 w-4" />
                    Transferir Ve√≠culos
                  </Button>
                )}
              </div>
            )}
          </div>
        )}

        {/* Solicitar Licen√ßa - Todos podem solicitar */}
        {permissions.canCreateLicenses() && (
          <SidebarItem
            icon={FileText}
            label="Solicitar Licen√ßa"
            path="/request-license"
            isActive={location === "/request-license"}
          />
        )}

        {/* Acompanhar Licen√ßa - Todos podem acompanhar */}
        {permissions.canTrackLicenses() && (
          <SidebarItem
            icon={ClipboardList}
            label="Acompanhar Licen√ßa"
            path="/track-license"
            isActive={location === "/track-license"}
          />
        )}

        {/* Licen√ßas Emitidas - Todos podem ver suas licen√ßas emitidas */}
        {permissions.canTrackLicenses() && (
          <SidebarItem
            icon={ListChecks}
            label="Licen√ßas Emitidas"
            path="/issued-licenses"
            isActive={location === "/issued-licenses"}
          />
        )}

        {/* MEUS BOLETOS - Conforme permiss√µes */}
        {permissions.canViewMyBoletos() && (
          <SidebarItem
            icon={Receipt}
            label="Meus Boletos"
            path="/meus-boletos"
            isActive={location === "/meus-boletos"}
          />
        )}

        {/* Se√ß√£o de Funcionalidades Administrativas */}
        {user && ['admin', 'manager', 'supervisor', 'financial', 'operational'].includes(user.role) && (
          <>
            {!isCollapsed && (
              <div className="pt-2 pb-2">
                <Separator className="bg-gray-700" />
                <p className="text-xs text-gray-400 uppercase mt-2 ml-2 font-semibold">Administra√ß√£o</p>
              </div>
            )}

            {/* Dashboard AET - para perfis com acesso ao dashboard */}
            {permissions.canViewDashboard() && (
              <SidebarItem
                icon={BarChart3}
                label="Dashboard AET"
                path="/admin/dashboard-aet"
                isActive={location === "/admin/dashboard-aet"}
              />
            )}

            {/* Gerenciar Licen√ßas - conforme permiss√µes de gerenciamento */}
            {user && ['admin', 'manager', 'supervisor', 'financial', 'operational'].includes(user.role) && (
              <SidebarItem
                icon={ClipboardEdit}
                label="Gerenciar Licen√ßas"
                path="/admin/licenses"
                isActive={location === "/admin/licenses" || location === "/gerenciar-licencas"}
              />
            )}

            {/* Importa√ß√£o em Massa de Licen√ßas - para perfis operacionais */}
            {user && ['admin', 'manager', 'supervisor', 'operational'].includes(user.role) && (
              <SidebarItem
                icon={UploadCloud}
                label="Importar Licen√ßas"
                path="/admin/bulk-license-import"
                isActive={location === "/admin/bulk-license-import"}
              />
            )}

            {/* Gerenciar Transportadores - conforme permiss√µes */}
            {user && ['admin', 'manager', 'supervisor', 'financial', 'operational'].includes(user.role) && (
              <SidebarItem
                icon={Building2}
                label="Transportadores"
                path="/admin/transporters"
                isActive={location === "/admin/transporters"}
              />
            )}

            {/* Gerenciar Usu√°rios - conforme permiss√µes */}
            {permissions.canViewUsers() && (
              <SidebarItem
                icon={Users}
                label="Usu√°rios"
                path="/admin/users"
                isActive={location === "/admin/users"}
              />
            )}

            {/* Tipos de Conjunto - apenas admin */}
            {user?.role === 'admin' && (
              <SidebarItem
                icon={Settings}
                label="Tipos de Conjunto"
                path="/admin/vehicle-set-types"
                isActive={location === "/admin/vehicle-set-types"}
              />
            )}

            {/* M√≥dulo Financeiro - apenas para perfis financeiro, manager e admin */}
            {permissions.canViewFinancial() && (
              <SidebarItem
                icon={Receipt}
                label="M√≥dulo Financeiro"
                path="/admin/boletos"
                isActive={location === "/admin/boletos"}
              />
            )}

          </>
        )}
      </div>

      {/* Footer da Sidebar com informa√ß√µes do usu√°rio */}
      <div className="mt-auto border-t border-gray-700">
        {/* Bot√£o de expandir quando colapsado */}
        {!isMobile && isCollapsed && onToggleCollapse && (
          <div className="p-2 flex justify-center">
            <Button
              variant="ghost"
              size="sm"
              onClick={onToggleCollapse}
              className="text-white hover:bg-gray-700 p-2"
            >
              <PanelLeftOpen className="h-4 w-4" />
            </Button>
          </div>
        )}

        {/* Informa√ß√µes do usu√°rio */}
        <div className={cn(
          "p-3 bg-gray-800",
          isCollapsed ? "flex justify-center" : "flex items-center space-x-3"
        )}>
          <Avatar className="h-8 w-8">
            <AvatarFallback className="bg-blue-600 text-white text-sm">
              {userInitials}
            </AvatarFallback>
          </Avatar>

          {!isCollapsed && (
            <>
              <div className="flex-1 min-w-0">
                <p className="text-sm font-medium text-white truncate">
                  {user?.fullName}
                </p>
                <p className="text-xs text-gray-400 truncate">
                  {user?.email}
                </p>
              </div>

              <Button
                variant="ghost"
                size="sm"
                onClick={handleLogout}
                disabled={logoutMutation.isPending}
                className="text-gray-300 hover:text-white hover:bg-gray-700 p-1"
              >
                <LogOut className="h-4 w-4" />
              </Button>
            </>
          )}

          {isCollapsed && (
            <TooltipProvider>
              <Tooltip>
                <TooltipTrigger asChild>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={handleLogout}
                    disabled={logoutMutation.isPending}
                    className="text-gray-300 hover:text-white hover:bg-gray-700 p-2"
                  >
                    <LogOut className="h-4 w-4" />
                  </Button>
                </TooltipTrigger>
                <TooltipContent side="right" className="ml-2">
                  Sair ({user?.fullName})
                </TooltipContent>
              </Tooltip>
            </TooltipProvider>
          )}
        </div>
      </div>


    </>
  );

  return isMobile ? (
    <>
      {/* Mobile Header */}
      <div className="md:hidden fixed inset-x-0 top-0 z-20 bg-gray-900 text-white flex items-center justify-between h-16 px-4 shadow-md">
        <div className="flex items-center">
          <Sheet open={open} onOpenChange={setOpen}>
            <SheetTrigger asChild>
              <Button variant="ghost" size="icon" className="text-white p-2 mr-2">
                <Menu className="h-5 w-5" />
              </Button>
            </SheetTrigger>
            <SheetContent side="left" className="p-0 bg-gray-800 text-white w-[250px] sm:w-[280px] overflow-y-auto max-h-screen">
              <div className="flex flex-col h-full">
                <NavItems />
              </div>
            </SheetContent>
          </Sheet>
          <Logo width={100} className="py-2" />
        </div>

        <div className="flex items-center space-x-2">
          <Avatar className="h-8 w-8">
            <AvatarFallback className="bg-gray-600 text-white text-sm">
              {userInitials}
            </AvatarFallback>
          </Avatar>
          <Button 
            variant="ghost" 
            size="icon" 
            className="text-gray-300 hover:text-white"
            onClick={handleLogout}
          >
            <LogOut className="h-5 w-5" />
          </Button>
        </div>
      </div>
    </>
  ) : (
    <>
      {/* Desktop Sidebar - Responsiva com colapso */}
      <div className={cn(
        "hidden md:flex md:flex-col md:fixed md:inset-y-0 bg-gray-800 text-white z-10 transition-all duration-300",
        isCollapsed ? "md:w-16" : "md:w-56 lg:w-64 xl:w-72",
        className
      )}>
        <div className="flex flex-col h-full">
          <NavItems />
        </div>
      </div>
    </>
  );
}

shared/schema.ts.content:
import { pgTable, text, serial, integer, boolean, timestamp, json, index, uniqueIndex, numeric, date } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";
import { DEFAULT_VEHICLE_SET_TYPES } from './vehicle-set-types';

// Define enum para os tipos de role/perfil de usu√°rio
export const userRoleEnum = z.enum([
  "admin", // Administrador (acesso completo)
  "operational", // Operacional (gerenciamento de licen√ßas e ve√≠culos)
  "supervisor", // Supervisor (papel intermedi√°rio)
  "manager", // Gerente (papel com permiss√µes estendidas)
  "financial", // Financeiro (acesso a transportadores, usu√°rios e boletos)
  "user" // Usu√°rio transportador padr√£o
]);

export type UserRole = z.infer<typeof userRoleEnum>;

// Define enum para tipos de pessoa
export const personTypeEnum = z.enum([
  "pj", // Pessoa Jur√≠dica
  "pf"  // Pessoa F√≠sica
]);

export type PersonType = z.infer<typeof personTypeEnum>;

// Transportador model
export const transporters = pgTable("transporters", {
  id: serial("id").primaryKey(),
  personType: text("person_type").notNull(), // PJ ou PF

  // Campos comuns
  name: text("name").notNull(), // Raz√£o Social (PJ) ou Nome Completo (PF)
  documentNumber: text("document_number").notNull().unique(), // CNPJ ou CPF
  email: text("email").notNull(),
  phone: text("phone"),

  // Campos espec√≠ficos PJ
  tradeName: text("trade_name"), // Nome Fantasia
  legalResponsible: text("legal_responsible"), // Respons√°vel Legal

  // Campos espec√≠ficos PF
  birthDate: text("birth_date"), // Data de Nascimento para PF
  nationality: text("nationality"), // Nacionalidade para PF
  idNumber: text("id_number"), // RG para PF
  idIssuer: text("id_issuer"), // √ìrg√£o Emissor do RG
  idState: text("id_state"), // UF do RG

  // Endere√ßo
  street: text("street"), // Logradouro
  number: text("number"), // N√∫mero
  complement: text("complement"), // Complemento
  district: text("district"), // Bairro
  zipCode: text("zip_code"), // CEP
  city: text("city"), // Cidade
  state: text("state"), // UF

  // Filiais (apenas para PJ)
  subsidiaries: json("subsidiaries").default('[]'), // Array com filiais (CNPJ, nome, endere√ßo, etc)

  // Arquivos
  documents: json("documents").default('[]'), // URLs dos documentos anexados

  // Campo para retro-compatibilidade
  contact1Name: text("contact1_name"),
  contact1Phone: text("contact1_phone"),
  contact2Name: text("contact2_name"),
  contact2Phone: text("contact2_phone"),

  userId: integer("user_id").references(() => users.id), // Refer√™ncia para o usu√°rio vinculado
  createdAt: timestamp("created_at").defaultNow().notNull(),
}, (table) => {
  return {
    // √çndices b√°sicos mantidos
    documentNumberIdx: uniqueIndex("idx_transporter_document").on(table.documentNumber),
    userIdIdx: index("idx_transporter_user_id").on(table.userId),
    nameIdx: index("idx_transporter_name").on(table.name),

    // √çndices compostos para otimiza√ß√£o de buscas
    searchOptimizedIdx: index("idx_transporter_search").on(table.name, table.documentNumber, table.tradeName),
    personTypeNameIdx: index("idx_transporter_type_name").on(table.personType, table.name)
  };
});

// Esquema JSON para filiais (subsidi√°rias)
export const subsidiarySchema = z.object({
  cnpj: z.string().min(14, "CNPJ deve ter pelo menos 14 d√≠gitos"),
  name: z.string().min(3, "Raz√£o social deve ter pelo menos 3 caracteres"),
  tradeName: z.string().optional(),
  street: z.string().optional(),
  number: z.string().optional(),
  complement: z.string().optional(),
  zipCode: z.string().optional(),
  city: z.string().optional(),
  state: z.string().optional(),
  documents: z.array(z.string()).optional().default([]),
});

// Esquema JSON para documentos
export const documentSchema = z.object({
  type: z.string(), // "social_contract", "power_of_attorney", etc.
  url: z.string(),
  filename: z.string(),
});

// Schema para inser√ß√£o/atualiza√ß√£o de transportador
export const insertTransporterSchema = z.object({
  personType: personTypeEnum,

  // Campos comuns
  name: z.string().min(3, "Nome/Raz√£o Social deve ter pelo menos 3 caracteres"),
  documentNumber: z.string().min(11, "Documento deve ter pelo menos 11 d√≠gitos"),
  email: z.string().email("Email inv√°lido"),
  phone: z.string().optional(),

  // Campos espec√≠ficos PJ
  tradeName: z.string().optional(),
  legalResponsible: z.string().optional(),

  // Campos espec√≠ficos PF
  birthDate: z.string().optional(),
  nationality: z.string().optional(),
  idNumber: z.string().optional(),
  idIssuer: z.string().optional(),
  idState: z.string().optional(),

  // Endere√ßo
  street: z.string().optional(),
  number: z.string().optional(),
  complement: z.string().optional(),
  district: z.string().optional(),
  zipCode: z.string().optional(),
  city: z.string().optional(),
  state: z.string().optional(),

  // Filiais (apenas para PJ)
  subsidiaries: z.array(subsidiarySchema).optional().default([]),

  // Arquivos
  documents: z.array(documentSchema).optional().default([]),

  // Campos para retro-compatibilidade
  contact1Name: z.string().optional(),
  contact1Phone: z.string().optional(),
  contact2Name: z.string().optional(),
  contact2Phone: z.string().optional(),
});

// User model
export const users = pgTable("users", {
  id: serial("id").primaryKey(),
  email: text("email").notNull().unique(),
  password: text("password").notNull(),
  fullName: text("full_name").notNull(),
  phone: text("phone").notNull(),
  role: text("role").default("user").notNull(), // Novo campo: role como string (enum)
  isAdmin: boolean("is_admin").default(false).notNull(), // Mantido para compatibilidade
  createdAt: timestamp("created_at").defaultNow().notNull(),
}, (table) => {
  return {
    emailIdx: uniqueIndex("idx_user_email").on(table.email),
    roleIdx: index("idx_user_role").on(table.role)
  };
});

export const insertUserSchema = createInsertSchema(users).pick({
  email: true,
  password: true,
  fullName: true,
  phone: true,
}).extend({
  role: userRoleEnum.optional().default("user"),
});

// Vehicle model
export const vehicles = pgTable("vehicles", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").references(() => users.id),
  plate: text("plate").notNull(),
  type: text("type").notNull(), // Unidade Tratora, Semirreboque, Reboque, Dolly, Prancha
  bodyType: text("body_type"), // Tipo de Carroceria: ABERTA, BASCULANTE, PORTA-CONT√äINER, FECHADA, TANQUE
  brand: text("brand"),
  model: text("model"),
  year: integer("year"),
  renavam: text("renavam"),
  tare: numeric("tare").notNull(), // peso em kg
  axleCount: integer("axle_count"), // Quantidade de eixos
  remarks: text("remarks"),
  crlvYear: integer("crlv_year").notNull(),
  crlvUrl: text("crlv_url"),
  ownerName: text("owner_name"), // Nome do Propriet√°rio
  ownershipType: text("ownership_type").default("proprio").notNull(), // "proprio" ou "terceiro"
  cmt: numeric("cmt", { precision: 10, scale: 2 }), // Capacidade M√°xima de Tra√ß√£o (apenas para unidade tratora)
  status: text("status").default("active").notNull(),
}, (table) => {
  return {
    // √çndices b√°sicos mantidos
    plateIdx: index("idx_vehicle_plate").on(table.plate),
    userIdIdx: index("idx_vehicle_user_id").on(table.userId),
    statusIdx: index("idx_vehicle_status").on(table.status),
    typeIdx: index("idx_vehicle_type").on(table.type),

    // √çndices compostos para otimiza√ß√£o de consultas frequentes
    userStatusIdx: index("idx_vehicle_user_status").on(table.userId, table.status),
    plateUserIdx: index("idx_vehicle_plate_user").on(table.plate, table.userId),
    searchOptimizedIdx: index("idx_vehicle_search").on(table.plate, table.brand, table.model, table.type),
    typeStatusIdx: index("idx_vehicle_type_status").on(table.type, table.status)
  };
});

export const insertVehicleSchema = createInsertSchema(vehicles)
  .omit({ id: true, userId: true })
  .extend({
    // Campos obrigat√≥rios com valida√ß√µes
    plate: z.string().min(1, "A placa √© obrigat√≥ria"),
    type: z.string().min(1, "O tipo de ve√≠culo √© obrigat√≥rio"),
    bodyType: z.string().optional(), // Tipo de carroceria
    renavam: z.string().min(1, "O RENAVAM √© obrigat√≥rio"),
    brand: z.string().min(1, "A marca √© obrigat√≥ria"),
    model: z.string().min(1, "O modelo √© obrigat√≥rio"),
    tare: z.coerce.number().min(1, "A tara deve ser maior que zero"),
    axleCount: z.coerce.number().min(1, "A quantidade de eixos deve ser maior que zero"),
    year: z.coerce.number().min(1950, "O ano de fabrica√ß√£o √© obrigat√≥rio"),
    crlvYear: z.coerce.number().optional(),
    remarks: z.string().optional(),
    status: z.string().optional(),
    crlvFile: z.any().optional(),
    ownerName: z.string().optional(), // Nome do Propriet√°rio
    ownershipType: z.enum(["proprio", "terceiro"]).default("proprio"), // Tipo de propriedade
    cmt: z.union([z.string(), z.number()]).pipe(z.coerce.number().positive()).optional(), // Capacidade M√°xima de Tra√ß√£o (apenas para unidade tratora)
  });

// Enums for license status
export const licenseStatusEnum = z.enum([
  "pending_registration", // Pedido em Cadastramento
  "registration_in_progress", // Cadastro em Andamento
  "pending_documentation", // Pendente Documenta√ß√£o
  "rejected", // Reprovado - Pend√™ncia de Documenta√ß√£o
  "under_review", // An√°lise do √ìrg√£o
  "pending_approval", // Pendente Libera√ß√£o
  "approved", // Liberada
  "canceled", // Cancelado
]);

export type LicenseStatus = z.infer<typeof licenseStatusEnum>;

// License type enum
export const licenseTypeEnum = z.enum([
  "roadtrain_9_axles", // Rodotrem 9 eixos
  "bitrain_9_axles", // Bitrem 9 eixos
  "bitrain_7_axles", // Bitrem 7 eixos 
  "bitrain_6_axles", // Bitrem 6 eixos
  "flatbed", // Prancha
  "romeo_and_juliet", // Romeu e Julieta
]);

export type LicenseType = z.infer<typeof licenseTypeEnum>;

// Cargo type enums
export const nonFlatbedCargoTypeEnum = z.enum([
  "dry_cargo", // Carga Seca
  "liquid_cargo", // L√≠quida
  "live_cargo", // Viva
  "sugar_cane", // Cana de A√ß√∫car
]);

export const flatbedCargoTypeEnum = z.enum([
  "indivisible_cargo", // Carga Indivis√≠vel
  "agricultural_machinery", // M√°quinas Agr√≠colas
  "oversized", // SUPERDIMENSIONADA
]);

export const cargoTypeEnum = z.union([nonFlatbedCargoTypeEnum, flatbedCargoTypeEnum]);

export type NonFlatbedCargoType = z.infer<typeof nonFlatbedCargoTypeEnum>;
export type FlatbedCargoType = z.infer<typeof flatbedCargoTypeEnum>;
export type CargoType = z.infer<typeof cargoTypeEnum>;

// License requests model
export const licenseRequests = pgTable("license_requests", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").notNull().references(() => users.id),
  transporterId: integer("transporter_id").references(() => transporters.id),
  requestNumber: text("request_number").notNull().unique(),
  type: text("type").notNull(), // From licenseTypeEnum
  mainVehiclePlate: text("main_vehicle_plate").notNull(),
  tractorUnitId: integer("tractor_unit_id").references(() => vehicles.id),
  firstTrailerId: integer("first_trailer_id").references(() => vehicles.id),
  dollyId: integer("dolly_id").references(() => vehicles.id),
  secondTrailerId: integer("second_trailer_id").references(() => vehicles.id),
  firstTrailerManualPlate: text("first_trailer_manual_plate"), // Placa manual para 1¬™ carreta
  dollyManualPlate: text("dolly_manual_plate"), // Placa manual para dolly
  secondTrailerManualPlate: text("second_trailer_manual_plate"), // Placa manual para 2¬™ carreta
  flatbedId: integer("flatbed_id").references(() => vehicles.id),
  length: numeric("length", { precision: 10, scale: 2 }).notNull(), // total length in meters
  width: numeric("width", { precision: 5, scale: 2 }), // width in meters
  height: numeric("height", { precision: 5, scale: 2 }), // height in meters
  cargoType: text("cargo_type"), // tipo de carga (union de nonFlatbedCargoType e flatbedCargoType)
  additionalPlates: text("additional_plates").array(), // Lista de placas adicionais 
  additionalPlatesDocuments: text("additional_plates_documents").array(), // URLs dos documentos das placas adicionais
  states: text("states").array().notNull(),
  status: text("status").default("pending_registration").notNull(), // Status principal (legado)
  stateStatuses: text("state_statuses").array(), // Array com formato "ESTADO:STATUS" (ex: "SP:approved")
  stateFiles: text("state_files").array(), // Array com formato "ESTADO:URL" (ex: "SP:http://...pdf")
  stateAETNumbers: text("state_aet_numbers").array(), // Array com formato "ESTADO:NUMERO_AET" (ex: "SP:123456")
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
  isDraft: boolean("is_draft").default(true).notNull(),
  comments: text("comments"),
  licenseFileUrl: text("license_file_url").default(''),
  validUntil: timestamp("valid_until"),
  issuedAt: timestamp("issued_at"),
  aetNumber: text("aet_number"),
  selectedCnpj: text("selected_cnpj"), // CNPJ selecionado da empresa transportadora (global - legado)
  stateCnpjs: text("state_cnpjs").array(), // Array com formato "ESTADO:CNPJ" (ex: "SP:12345678000100")
}, (table) => {
  return {
    requestNumberIdx: uniqueIndex("idx_license_request_number").on(table.requestNumber),
    userIdIdx: index("idx_license_user_id").on(table.userId),
    transporterIdIdx: index("idx_license_transporter_id").on(table.transporterId),
    statusIdx: index("idx_license_status").on(table.status),
    isDraftIdx: index("idx_license_is_draft").on(table.isDraft),
    createdAtIdx: index("idx_license_created_at").on(table.createdAt),
    mainVehiclePlateIdx: index("idx_license_main_vehicle").on(table.mainVehiclePlate)
  };
});

export const insertLicenseRequestSchema = createInsertSchema(licenseRequests)
  .omit({ 
    id: true, 
    userId: true, 
    requestNumber: true, 
    createdAt: true, 
    updatedAt: true, 
    licenseFileUrl: true, 
    validUntil: true,
    aetNumber: true,
    stateAETNumbers: true
  })
  .extend({
    transporterId: z.number().positive("Um transportador deve ser selecionado"),
    states: z.array(z.string()).min(1, "Selecione pelo menos um estado"),
    cargoType: cargoTypeEnum.refine(val => !!val, {
      message: "O tipo de carga √© obrigat√≥rio",
      path: ["cargoType"]
    }),
    length: z.coerce.number().positive("O comprimento deve ser positivo"),
    width: z.coerce.number().positive("A largura deve ser um valor positivo"),
    height: z.coerce.number().positive("A altura deve ser um valor positivo"),
    additionalPlates: z.array(z.string()).optional().default([]),
    additionalPlatesDocuments: z.array(z.string()).optional().default([]),
    firstTrailerManualPlate: z.string().optional(),
    dollyManualPlate: z.string().optional(),
    secondTrailerManualPlate: z.string().optional(),
  })
  .superRefine((data, ctx) => {
    const licenseType = data.type;

    // Buscar o tipo de conjunto correspondente
    const vehicleSetType = DEFAULT_VEHICLE_SET_TYPES.find(
      (vst) => vst.id === licenseType || vst.name === licenseType
    );

    // Valida√ß√£o din√¢mica de dimens√µes baseada no tipo de conjunto
    if (vehicleSetType?.dimensionLimits) {
      const limits = vehicleSetType.dimensionLimits;

      // Validar comprimento
      if (limits.minLength && data.length < limits.minLength) {
        ctx.addIssue({
          code: z.ZodIssueCode.too_small,
          minimum: limits.minLength,
          type: "number",
          inclusive: true,
          exact: false,
          message: `O comprimento deve ser de no m√≠nimo ${limits.minLength.toFixed(2)} metros`,
          path: ["length"]
        });
      }
      if (limits.maxLength && data.length > limits.maxLength) {
        ctx.addIssue({
          code: z.ZodIssueCode.too_big,
          maximum: limits.maxLength,
          type: "number",
          inclusive: true,
          exact: false,
          message: `O comprimento deve ser de no m√°ximo ${limits.maxLength.toFixed(2)} metros`,
          path: ["length"]
        });
      }

      // Validar largura
      if (limits.maxWidth && data.width && data.width > limits.maxWidth) {
        ctx.addIssue({
          code: z.ZodIssueCode.too_big,
          maximum: limits.maxWidth,
          type: "number",
          inclusive: true,
          exact: false,
          message: `A largura m√°xima permitida √© ${limits.maxWidth.toFixed(2)} metros`,
          path: ["width"]
        });
      }

      // Validar altura
      if (limits.maxHeight && data.height && data.height > limits.maxHeight) {
        ctx.addIssue({
          code: z.ZodIssueCode.too_big,
          maximum: limits.maxHeight,
          type: "number",
          inclusive: true,
          exact: false,
          message: `A altura m√°xima permitida √© ${limits.maxHeight.toFixed(2)} metros`,
          path: ["height"]
        });
      }
    }

    // VALIDA√á√ÉO OBRIGAT√ìRIA: Unidade Tratora/Cavalo sempre obrigat√≥ria
    if (!data.tractorUnitId && !data.mainVehiclePlate) {
      ctx.addIssue({
        code: z.ZodIssueCode.custom,
        message: "Falta Placa da Linha de frente",
        path: ["tractorUnitId"]
      });
    }

    // VALIDA√á√ïES ESPEC√çFICAS POR TIPO DE LICEN√áA
    if (licenseType?.includes('flatbed') || licenseType?.includes('prancha')) {
      // TIPO PRANCHA: Cavalo + Prancha obrigat√≥rios
      if (!data.flatbedId) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "A Prancha √© obrigat√≥ria para licen√ßas do tipo Prancha",
          path: ["flatbedId"]
        });
      }
    } else if (licenseType?.includes('rodotrem') || licenseType?.includes('road_train')) {
      // TIPO RODOTREM: Cavalo + 1¬™ Carreta + Dolly + 2¬™ Carreta obrigat√≥rios
      if (!data.firstTrailerId && !data.firstTrailerManualPlate) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "A 1¬™ Carreta √© obrigat√≥ria para licen√ßas do tipo Rodotrem",
          path: ["firstTrailerId"]
        });
      }
      if (!data.dollyId && !data.dollyManualPlate) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "O Dolly √© obrigat√≥rio para licen√ßas do tipo Rodotrem",
          path: ["dollyId"]
        });
      }
      if (!data.secondTrailerId && !data.secondTrailerManualPlate) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "A 2¬™ Carreta √© obrigat√≥ria para licen√ßas do tipo Rodotrem",
          path: ["secondTrailerId"]
        });
      }
    } else if (licenseType?.includes('bitrem') || licenseType?.includes('bitrain')) {
      // TIPO BITREM: Cavalo + 1¬™ Carreta + 2¬™ Carreta obrigat√≥rios (sem dolly)
      if (!data.firstTrailerId && !data.firstTrailerManualPlate) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "A 1¬™ Carreta √© obrigat√≥ria para licen√ßas do tipo Bitrem",
          path: ["firstTrailerId"]
        });
      }
      if (!data.secondTrailerId && !data.secondTrailerManualPlate) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "A 2¬™ Carreta √© obrigat√≥ria para licen√ßas do tipo Bitrem",
          path: ["secondTrailerId"]
        });
      }
    } else {
      // TIPOS SIMPLES: Cavalo + 1¬™ Carreta obrigat√≥rios
      if (!data.firstTrailerId && !data.firstTrailerManualPlate) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "A 1¬™ Carreta √© obrigat√≥ria",
          path: ["firstTrailerId"]
        });
      }
    }
  });

// Schema para rascunhos - n√£o aplicar valida√ß√µes rigorosas de linha de frente
export const insertDraftLicenseSchema = createInsertSchema(licenseRequests)
  .omit({ 
    id: true, 
    userId: true, 
    requestNumber: true, 
    createdAt: true, 
    updatedAt: true, 
    licenseFileUrl: true, 
    validUntil: true,
    aetNumber: true,
    stateAETNumbers: true
  })
  .extend({
    type: licenseTypeEnum,
    isDraft: z.literal(true),
    transporterId: z.number().optional(),
    states: z.array(z.string()).optional().default([]),
    cargoType: cargoTypeEnum.optional(),
    length: z.coerce.number().optional(),
    width: z.coerce.number().optional(),
    height: z.coerce.number().optional(),
    additionalPlates: z.array(z.string()).optional().default([]),
    additionalPlatesDocuments: z.array(z.string()).optional().default([]),
    firstTrailerManualPlate: z.string().optional(),
    dollyManualPlate: z.string().optional(),
    secondTrailerManualPlate: z.string().optional(),
  });

export const updateLicenseStatusSchema = createInsertSchema(licenseRequests)
  .pick({
    status: true,
    comments: true,
  })
  .extend({
    licenseFile: z.any().optional(),
    validUntil: z.string().optional(),
    issuedAt: z.string().optional(),
    state: z.string().optional(), // Estado espec√≠fico sendo atualizado
    stateStatus: z.enum(licenseStatusEnum.options).optional(), // Status para o estado espec√≠fico
    stateFile: z.any().optional(), // Arquivo para o estado espec√≠fico
    selectedCnpj: z.string().optional(), // CNPJ selecionado da empresa transportadora (global - legado)
    stateCnpj: z.string().optional(), // CNPJ espec√≠fico para este estado
    aetNumber: z.string().optional(), // N√∫mero AET
  });

// Schema para quando todos os estados forem setados, atualizar o status geral
export const updateLicenseStateSchema = z.object({
  licenseId: z.number(),
  state: z.string(),
  status: licenseStatusEnum,
  file: z.any().optional(),
  comments: z.string().optional(),
  validUntil: z.string().optional(),
