    dataEmissao: z.string().refine((date) => !isNaN(Date.parse(date)), {
      message: "Data de emiss√£o inv√°lida",
    }),
    dataVencimento: z.string().refine((date) => !isNaN(Date.parse(date)), {
      message: "Data de vencimento inv√°lida",
    }),
    numeroBoleto: z.string().min(1, "N√∫mero do boleto √© obrigat√≥rio"),
    cpfCnpj: z.string().regex(/^(\d{11}|\d{14})$/, "CPF deve ter 11 d√≠gitos ou CNPJ deve ter 14 d√≠gitos"),
  })
