              <tbody className="bg-white divide-y divide-gray-200">
                {license.states.map((state, idx) => {
                  // Encontrar status para este estado
                  const stateStatusEntry = stateStatuses.find(entry => entry.startsWith(`${state}:`));
                  let status = "pending_registration";
                  let validUntil = null;
                  let issuedAt = null;

                  if (stateStatusEntry) {
                    const parts = stateStatusEntry.split(':');
                    status = parts[1];
                    validUntil = parts.length > 2 ? parts[2] : null;

                    // Se tiver 4 partes, a quarta √© a data de emiss√£o
                    if (parts.length > 3) {
                      issuedAt = parts[3];
                    }
                  }

                  // Usar a data de emiss√£o do campo dedicado da licen√ßa se n√£o encontrou no status
                  if (!issuedAt && license.issuedAt) {
                    // Converter para formato de data (YYYY-MM-DD)
                    const issuedDate = new Date(license.issuedAt);
                    issuedAt = issuedDate.toISOString().split('T')[0];
                  }

                  // Encontrar arquivo para este estado (usando estado atualizado em tempo real)
                  const stateFileEntry = stateFiles.find(entry => entry.startsWith(`${state}:`));
                  const stateFileUrl = stateFileEntry ? stateFileEntry.split(':')[1] : null;

                  // Encontrar n√∫mero AET para este estado (usando estado atualizado em tempo real)
                  const stateAETEntry = stateAETNumbers.find(entry => entry.startsWith(`${state}:`));
                  const stateAETNumber = stateAETEntry ? stateAETEntry.split(':')[1] : null;

                  return (
                    <tr key={idx}>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <Badge variant="outline" className="px-2.5 py-0.5">
                          {state}
                        </Badge>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <StatusBadge status={status} />
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {stateAETNumber ? (
                          <span className="font-semibold text-blue-700">{stateAETNumber}</span>
                        ) : (
                          <span className="text-gray-400">-</span>
                        )}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {issuedAt ? (
                          <span className="font-medium">
                            {new Date(issuedAt + 'T00:00:00').toLocaleDateString('pt-BR')}
                          </span>
                        ) : (
                          <span className="text-gray-400">-</span>
                        )}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {validUntil ? (
                          <span className="font-medium">
                            {new Date(validUntil + 'T00:00:00').toLocaleDateString('pt-BR')}
                          </span>
                        ) : (
                          <span className="text-gray-400">-</span>
                        )}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {stateFileUrl ? (
                          <a 
                            href={stateFileUrl} 
                            target="_blank" 
                            rel="noopener noreferrer" 
                            className="text-blue-600 hover:text-blue-800 inline-flex items-center"
